/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    // 공통 코드 조회

    gfn_SearchAllJibu(cbxJibu, "Y", "선택"); // 지부 코드 조회
        
    gfn_SearchCustomerCourseFullName(ds_oCourse, "0");				// 대표고객직능 조회
    
    //gfn_SearchCustomerDetailCourse("",ds_oDetailCourse, "0", "전체");
    gfn_SearchCustomerDetailCourseFullName("", ds_oDetailCourse, "0", "전체");			// 세부고객직능 조회

    // 교육접수가능 직능만 남김
    for (var i = 0; i <= ds_oCourse.GetRowCount(); i++) {
    
		if ((ds_oCourse.GetColumn(i, "CD") == "60") 
		
		|| (ds_oCourse.GetColumn(i, "CD") == "70")
		
		|| (ds_oCourse.GetColumn(i, "CD") == "90")) {
		
			ds_oCourse.DeleteRow(i);
			
			i = i - 1;
		}
    }
    
    cbxCourse_OnChanged();
    
    lfn_FilterDetailCourse(cbxCourse.Value);	// 세부직능필터

	radSearchGubun.Value = "0";
	
	edEduOrderStart.Enable = true;
	
	edEduOrderEnd.Enable = true;
	
	calEduStart.Enable = false;
	
	calEduEnd.Enable = false;

	// 실무교육일정관리에서 더블클릭하여 조회된 자료이면
	if (!gfn_isNull(sJibu)) {

		cbxJibu.Value = sJibu;
		
		edEduYear.Text = sYear;
		
		cbxCourse.Value = sCourse;
		
		edEduOrderStart.Text = sOrder;
		
		edEduOrderEnd.Text = sOrder;
		
		radSunapYn.Value = sSunapGubun;
		
		lfn_Search(obj);
	
	} else {
	
		cbxCourse.Value = "10";	
		edEduYear.Text = left(GetDate(), 4);	
		edEduOrderStart.Text = "001";	
		edEduOrderEnd.Text = "001";	
		edEduOrderStart.SetFocus();

	}
	
	
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do
    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState) {
    if (gs_IsResizingForm == "Y") {
        gdEduJubsu.Height = nCy - gdEduJubsu.Top - 4;
        gdEduJubsu.Width = nCx - gdEduJubsu.Left - 4;
    }
}

