var ls_PosJibu = "";  // POS 지부 코드
var ls_JibuMgId = ""; // 지부관리ID 

function Form_OnLoadCompleted(obj) {

	gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    if (IsExistVar("sEJMgno")) {
        lfn_SetSize();
        lfn_Search();
    } else {
        gfn_Alert("결제 정보가 없습니다.");
        lfn_Cancel();
    }

	if (ds_oJubsu.GetColumn(0, "EJSTATUS") == "6") {
		gfn_SearchPosJibu(ds_oPosJibu, iif(ds_oSunap.GetColumn(0, "PMPGMID") == "11", "0", "1")); // POS 지부 조회
		ls_PosJibu = substr(gds_oUserInfo.GetColumn(0, "PDEPTCD"), 1, 2); // POS 지부 코드
		ls_JibuMgId = ds_oPosJibu.GetColumn(ds_oPosJibu.FindRow("PICHPT", ls_PosJibu), "PIID"); // 지부관리ID
	}

	if (ds_oJubsu.GetColumn(0, "EJSTATUS") < "5") {
		btnReport.Visible = true;	// 교육참가원 출력
		btnRepay.Visible = false;	// 환불신청서 출력
	} else {
		btnReport.Visible = false;	// 교육참가원 출력
		btnRepay.Visible = true;	// 환불신청서 출력	
	}

}

