/**
 * 화면 설정
 */
function lfn_SetSize() {

    ds_oJubsu.ClearData();
    tit_clearActionInfo();
    tit_addCsvSearchActionInfo("cust:searchCust1031InetEduJubsu", false);
    tit_callService(
        ""
        ,""
        ,""	// inDs
        ,"ds_oJubsu=ds_oInetEduJubsu" // outDs
        ,"EJMGNO=" + quote(sEJMgno)
        ,""
        ,false
        ,false
        ,true
    );

    switch(ds_oJubsu.GetColumn(0, "EJSTATUS")) {

        case "0": //미신청
            divSunap.lbSunapDate.Text = "입금일자";
            window.Height = 172;
            break;

        case "1": //미입금
            divSunap.lbSunapDate.Text = "할당일자";
            window.Height = 172;
            break;

        case "2": //접수완료
            divSunap.lbSunapDate.Text = "입금일자";
            divSunap.Visible = true;
            window.Height = 248;
            break;

        case "3": //신청취소
            divSunap.lbSunapDate.Text = "취소일자";
            divSunap.Visible = true;
            window.Height = 248;
            break;

        case "4": //회차변경
            divSunap.lbSunapDate.Text = "입금일자";
            divSunap.Visible = true;
            window.Height = 248;
            break;

        case "5": //환불신청
            btnProc.Text = "환불접수";
            btnProc.Visible = true;
            btnCancel.Visible = true;
            divSunap.Visible = true;
            divRepay.Visible = true;
            window.Height = 348;
            break;

        case "6": //환불접수
            btnProc.Text = "환불완료";
            btnProc.Visible = true;
            btnCancel.Visible = true;
            divSunap.Visible = true;
            divRepay.Visible = true;
            window.Height = 348;
            break;

        case "7": //환불완료
        case "8": //환불취소
            btnProc.Visible = false;
            divSunap.Visible = true;
            divRepay.Visible = true;
            window.Height = 348;
            break;
    }

    if (ds_oJubsu.GetColumn(0, "EJCHANGECNT") >= "1") {
        divChange.Visible = true;
        window.Height = window.Height + 76;
    } else {
        divSunap.Top = divSunap.Top - 76;
        divRepay.Top = divRepay.Top - 76;
        divCustomer.Top = divCustomer.Top - 76;
    }

    if (!gfn_IsNull(ds_oJubsu.GetColumn(0, "EJCMGNO"))) {
        divCustomer.Visible = true;
        divCustomer.Top = window.Height - 36;
        window.Height = window.Height + 172;
    }

}

/**
 * 조회
 */
function lfn_Search() {

    var sArg = "";

    ds_ioEduJubsuH.ClearData();
    ds_oSunap.ClearData();
    ds_oChange.ClearData();
    ds_oRepay.ClearData();
    ds_oCustomer.ClearData();

    tit_clearActionInfo();

    tit_addSearchActionInfo("cust:searchCust1031EduJubsuH", false);
    sArg += "ds_ioEduJubsuH=ds_oEduJubsuH";

    if (ds_oJubsu.GetColumn(0, "EJCHANGECNT") >= "1") {
        tit_addSearchActionInfo("cust:searchCust1031InetEduChange", false);
        sArg += " ds_oChange=ds_oInetEduChange";
    }

    if (ds_oJubsu.GetColumn(0, "EJSTATUS") >= "1") {
        tit_addSearchActionInfo("cust:searchCust1031InetEduSunap", false);
        sArg += " ds_oSunap=ds_oInetEduSunap";
    }

    if (ds_oJubsu.GetColumn(0, "EJSTATUS") >= "5") {
        tit_addSearchActionInfo("cust:searchCust1031InetEduRepay", false);
        sArg += " ds_oRepay=ds_oInetEduRepay";
    }

    if (!gfn_IsNull(ds_oJubsu.GetColumn(0, "EJCMGNO"))) {
        tit_addSearchActionInfo("cust:searchCust1031InetEduCustomer", false);
        sArg += " ds_oCustomer=ds_oInetEduCustomer";
    }

    tit_callService(
        ""
        ,""
        ,""	// inDs
        ,sArg
        ,"EJMGNO=" + quote(sEJMgno)
        ,""
        ,false
        ,false
        ,true
    );

    if (right(ds_oSunap.GetColumn(0, "PMPGMGUBUN"), 1) == "3"
        || right(ds_oSunap.GetColumn(0, "PMPGMGUBUN"), 1) == "5") {
        divSunap.btnShowVirtualAccount.Enable = false;
    }

}

