/**
 * 환불처리 버튼
 */
function btnProc_OnClick(obj) {

    sPos = "FAIL";

    switch(ds_oJubsu.GetColumn(0, "EJSTATUS")) {
        case "5": //환불신청

            if (!gfn_Confirm("환불접수를 하시겠습니까?")) {
                return;
            }
            sPos = lfn_RepayJubsu();
            break;

        case "6": //환불접수
            if (!gfn_Confirm("환불완료를 하시겠습니까?")) {
                return;
            }
            sPos = lfn_RepayComplete();
            break;

    }

    Close(sPos);
}

/**
 * 환불접수처리
 */
function lfn_RepayJubsu() {

    ds_ioEduJubsuH.SetColumn(0, "EJHSTATUS", "6");

    tit_clearActionInfo();
    tit_addSingleActionInfo("cust:insertCust1031InetRepay", "", 0, "");
    tit_callService(
        ""
        ,""
        ,"ds_iInetRepay=ds_ioEduJubsuH"	// inDs
        ,""	// outDs
        ,""	// args 
        ,"lfn_AfterCall"
        ,false
    );

    gfn_Alert("환불이 접수되었습니다.");

    return "OK";
}

/**
 * 환불완료처리
 */
function lfn_RepayComplete() {

	if (ds_oSunap.GetColumn(ds_oSunap.currow, "PVINFO") == "발행") {

		gfn_Alert("                   < (세금)계산서 발행 자료 >\n\n"

			 + "해당 자료는 (세금)계산서가 발행되어 있습니다.\n\n"

			 + "    - (세금)계산서 관리번호 : " + ds_oSunap.GetColumn(ds_oSunap.currow, "PVVMYEAR") +"-"
												+ iif(ds_oSunap.GetColumn(ds_oSunap, "PVVMBILLTYPE")=="1","F","T") +"-"
												+ ds_oSunap.GetColumn(ds_oSunap.currow, "PVVMVATNO") + "\n\n"

			 + "회계 담당자에게 (세금)계산서 삭제 후 환불해 주시기 바랍니다.");

	}




    var sProcDate = left(getDate(), 8);

    ds_oPmMgno.ClearData();
    ds_ioCustomerFee.ClearData();
    ds_ioPosPrintM.ClearData();
    ds_ioPosPayment.ClearData();

/* 기존 수납정보 SELECT TRANSACTION */
    tit_clearActionInfo();
    tit_addSearchActionInfo("cust:searchCust2012CustomerFee", false);
    tit_addSearchActionInfo("cust:searchCust2020Pos", false);
    tit_addSearchActionInfo("cust:searchCust2020PosPayment", false);

    tit_callService(
        ""
        ,""
        ,""
        ,"ds_ioCustomerFee=ds_oCustomerFee"
            + " ds_ioPosPrintM=ds_oPos"
            + " ds_ioPosPayment=ds_oPosPayment"
        ,"SPMYEAR=" + quote(ds_oRepay.GetColumn(0, "EJPMYEAR"))
            + " SPMMGNO=" + quote(ds_oRepay.GetColumn(0, "EJPMMGNO"))
            + " LAST=" + quote("Y") // args 
        ,"lfn_AfterCall"
        ,false
        ,false
        ,true
    );

/* 환불 데이터 변경 */
    for (var i=0; i<ds_ioCustomerFee.RowCount(); i++) { //CUSTOMERFEE <= ds_ioCustomerFee
        ds_ioCustomerFee.SetColumn(i, "CFGUBUN", "02");
        ds_ioCustomerFee.SetColumn(i, "CFPROCAMOUNT", (-1) * ds_ioCustomerFee.GetColumn(i, "CFPROCAMOUNT"));
        ds_ioCustomerFee.SetColumn(i, "CFPROCDATE", sProcDate);
        ds_ioCustomerFee.SetColumn(i, "CFPONYGUBUN", "0");
        ds_ioCustomerFee.SetColumn(i, "CFPONYDATE", "");
        ds_ioCustomerFee.SetColumn(i, "CFREMARK", "[환불 : "+ divRepay.medAmount.Text +"원]");
    }

    for (var i=0; i<ds_ioPosPrintM.RowCount(); i++) { //POSPRINTM <= ds_ioPosPrintM
        ds_ioPosPrintM.SetColumn(i, "PMPCGUBUN", "1");
        ds_ioPosPrintM.SetColumn(i, "PMPROCDATE", sProcDate);
        if (ds_ioPosPrintM.GetColumn(i, "PMPAYMENTFLAG") <> "1") {
			ds_ioPosPrintM.SetColumn(i, "PMPIID", ls_JibuMgId);
        }
        ds_ioPosPrintM.SetColumn(i, "PMITEM4", "환불일자 : " + gfn_ToDate(sProcDate, "-"));
        ds_ioPosPrintM.SetColumn(i, "PMAMT", (-1) * ds_ioPosPrintM.GetColumn(i, "PMAMT"));
    }

    for (var i=0; i<ds_ioPosPayment.RowCount(); i++) { //CUSTOMERFEE <= ds_ioCustomerFee
        ds_ioPosPayment.SetColumn(i, "PPTOTAMT", (-1) * ds_ioPosPayment.GetColumn(i, "PPTOTAMT"));
    }

	var sArg = "";

    tit_clearActionInfo();
	//타지부 환불
    if (ds_ioCustomerFee.GetColumn(0, "CFMGGTMGNO") <> gds_oUserInfo.GetColumn(0, "PDEPTCD")) {

        tit_addMultiActionInfo("cust:insertCust2020TaCustomerFee", "", 0, "", "N");
        sArg = "ds_iTaCustomerFee=ds_ioCustomerFee ";
    }



/* 환불 처리 */

    tit_addSearchActionInfo("cust:searchCust2020CustomerFeePmMgno", false);
    tit_addMultiActionInfo("cust:insertCust2020CustomerFee", "", 0, "", "N");
    tit_addSingleActionInfo("cust:insertCust2020PosPrintM", "", 0, "");
    tit_addMultiActionInfo("cust:insertCust2020PosPayment", "", 0, "", "N");

    tit_callService(
        ""
        ,""
        ,sArg + "ds_iCustomerFee=ds_ioCustomerFee"
            + " ds_iPosPrintM=ds_ioPosPrintM"
            + " ds_iPosPayment=ds_ioPosPayment"
        ,"ds_oPmMgno=ds_oCustomerFeePmMgno"
        ,"SPMYEAR=" + quote(left(sProcDate, 4))
            + " SPROCDATE=" + quote(sProcDate)
            + " SGTMGNO=" + quote(gds_oUserInfo.GetColumn(0, "PDEPTCD")) // args 
        ,"lfn_AfterCall"
        ,false
        ,false
        ,true
    );

    if (ds_oPmMgno.GetRowCount() < 1) { // 쿼리 오류시 종료
        return "FAIL";
    }

/* POS실행 */
    var sPos = lfn_Pos();

/* POS실행 후 TRANSACTION */
     var sArg = "";
    tit_clearActionInfo();

    if (sPos == "OK") {

        ds_ioEduJubsuH.SetColumn(0, "EJHSTATUS", "7");

        tit_addSingleActionInfo("cust:updateCust2020CustomerPosAfter", "", 0, "");
        tit_addSingleActionInfo("cust:insertCust1031InetRepay", "", 0, "");

        sArg = "ds_iCustomerFee=ds_ioCustomerFee";
        sArg += " ds_iInetRepay=ds_ioEduJubsuH";

    } else if (sPos == "FAIL") {

        tit_addSingleActionInfo("cust:deleteCust2020CustomerPosAfter", "", 0, "");

        sArg = "ds_iCustomerFee=ds_ioCustomerFee";

    } else {
        return sPos;
    }

    tit_callService(
        ""
        ,""
        ,sArg
        ,""
        ,"SPMYEAR=" + quote(ds_oPmMgno.GetColumn(0, "PMYEAR"))
            + " SPMMGNO=" + quote(ds_oPmMgno.GetColumn(0, "PMMGNO"))
            + " SPCGUBUN="  + quote("1") // args 
        ,"lfn_AfterCall"
        ,false
        ,false
        ,true
    );

    return sPos;

}

