/**
 * 가상계좌보기
 */
function btnShowVirtualAccount_OnClick(obj) {

    if (gfn_isNull(ds_oCustomer.GetColumn(0, "CMGNO"))) {
        gfn_Alert("고객정보가 없습니다.");
        return false;
    }

    var sArg = "sGUBUN=" + quote(ds_oCustomer.GetColumn(0, "CSTATUSGUBUN"));
		sArg += " sTotalAmount=" + quote(ds_oCustomer.GetColumn(0, "MFEE"));
		sArg += " sJibu=" + quote(ds_oCustomer.GetColumn(0, "CGTMGNO"));
		sArg += " sCMgno=" + quote(ds_oCustomer.GetColumn(0, "CMGNO"));
		sArg += " sFMNM=" + quote(ds_oCustomer.GetColumn(0, "FMNM"));
		sArg += " sHPTEL=" + quote(ds_oCustomer.GetColumn(0, "FMHHPTEL"));
		sArg += " sBNM=" + quote(ds_oCustomer.GetColumn(0, "EJBUILDING"));
		sArg += " sCOURSENM=" + quote(ds_oCustomer.GetColumn(0, "CCOURSENM"));

	Dialog("cust::frmcust2018PFixVirtualAccount.xml", sArg, 400, 220, "", -1, -1);
}

/**
 * 영수증재발행
 */
function divSunap_btnPrintReRct_OnClick(obj) {

    if (gfn_isNull(ds_oSunap.GetColumn(0, "EJPMYEAR"))) {
        gfn_Alert("영수증 정보가 없습니다.");
        return false;
    }

    if (!gfn_Confirm("영수증을 재발행 하시겠습니까?")) {
        return false;
	}

    gfn_PrintReceipt(ds_oSunap.GetColumn(0, "EJPMYEAR"), ds_oSunap.GetColumn(0, "EJPMMGNO"));
}

