#include "LIB::cust.js"; // 공통업무 관련

function Form_OnLoadCompleted(obj)
{
	gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
	
    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

	gfn_SearchAllJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회
	
	gfn_SearchCustomerEduCourseFullName(ds_ioCourse, "0", "선택");	// 대표고객직능 조회
	
	radCourse.Value = "";
	//gfn_SearchCustomerDetailCourse("대표직능", ds_oDetailCourse, "Y", ""); // 세부고객직능 조회
	
	edYear.Text = substr(getDate(),0,4);
	
	radReport_OnChanged();
	radCourse_OnChanged();
	radEduYmd_OnChanged();
	
	radSuccessionGubun.Index = 0;
}

function lfn_SetFormAuth(bStatus) {

}


function lfn_Search(obj) {
	
	ds_EduList.ClearData(); // 데이타셋 초기화
	ds_DayEduStatistics.ClearData(); // 데이타셋 초기화
	ds_EduAllJibuList.ClearData(); // 데이타셋 초기화
	
	tit_clearActionInfo(this);
	
	//TAB_Training.TabIndex = 1; //조회버튼 선택시  나오게 한다.
	if(radReport.Value == "1" || radReport.Value == "2"){ 		tit_addCsvSearchActionInfo("cust:searchCust1142ProcEduList", false);
	} else if(radReport.Value == "3" || radReport.Value == "4"){ 
		tit_addCsvSearchActionInfo("cust:searchCust1142ProcEduStatistics", false);
	} else if(radReport.Value == "5" || radReport.Value == "6"){
		tit_addCsvSearchActionInfo("cust:searchCust1142AllJibuProcEduStatistics", false);
	}
	ds_SearchJogeon.DeleteAll();		ds_SearchJogeon.AddRow();
	ds_SearchJogeon.setColumn(0, "REPORT_GUBUN", radReport.Value);	
	if(radReport.Value == "2" || radReport.Value == "4")
		ds_SearchJogeon.setColumn(0, "REPORT_GUBUN_GROUP", "2");	
	else
		ds_SearchJogeon.setColumn(0, "REPORT_GUBUN_GROUP", "1");	
	
	ds_SearchJogeon.setColumn(0, "YEAR", edYear.Value);	
	ds_SearchJogeon.setColumn(0, "JIBU", cbxJibu.Value);	
	ds_SearchJogeon.setColumn(0, "MEMBER_GUBUN", radMemberGubun.Value);	
	ds_SearchJogeon.setColumn(0, "COURSE_GUBUN", radCourse.Value);	
	ds_SearchJogeon.setColumn(0, "COURSE", cbxCourse.Value);	
	
	var Course = "";
	
	if(cbxCourse1.Value != "")
		Course += "'" + cbxCourse1.Value + "'" + ",";
	if(cbxCourse2.Value != "")
		Course += "'" + cbxCourse2.Value + "'" + ",";	
    if(cbxCourse3.Value != "")
		Course += "'" + cbxCourse3.Value + "'" + ",";	
	if(cbxCourse4.Value != "")
		Course += "'" + cbxCourse4.Value + "'" + ",";	
	if(cbxCourse5.Value != "")
		Course += "'" + cbxCourse5.Value + "'" + ",";	
	if(cbxCourse6.Value != "")
		Course += "'" + cbxCourse6.Value + "'" + ",";	
	 	
	ds_SearchJogeon.setColumn(0, "COURSE1_6IN", left(Course, length(Course)-1));	
	ds_SearchJogeon.setColumn(0, "EDUYMD_GUBUN", radEduYmdGubun.Value);	
	ds_SearchJogeon.setColumn(0, "EDU_YMD_FR", calEduYmdFr.Value);	
	ds_SearchJogeon.setColumn(0, "EDU_YMD_TO", calEduYmdTo.Value);	
	ds_SearchJogeon.setColumn(0, "EDU_GUBUN", radEduGubun.Value);		
	
	if (edYear.Text >= 2010) {
		ds_SearchJogeon.setColumn(0, "EDU_ORDERGUBUN", radEduOrderGubun.Value); // 교육횟차 구분
		ds_SearchJogeon.setColumn(0, "EDU_ORDER", edEduOrder.Text);  			// 교육횟차
	} 
	
	ds_SearchJogeon.setColumn(0, "PRINT_GUBUN1", radPrintGubun1.Value);	
	ds_SearchJogeon.setColumn(0, "PRINT_GUBUN2", radPrintGubun2.Value);	
	ds_SearchJogeon.setColumn(0, "MINAPGUBUN", radMinap.Value);
	ds_SearchJogeon.setColumn(0, "SUCCESSIONGUBUN", radSuccessionGubun.Value);		


    if(radReport.Value == "1" || radReport.Value == "2"){              
		tit_callService(  
						 ""
					   , ""   
					   , "ds_iEduList=ds_SearchJogeon"//inDs 
					   , "ds_EduList=ds_oEduList" //outDs
					   , ""
					   , "afterSearch"  //callBack
					   , false
					   ,false
					   ,true
					   ); 
	} else if(radReport.Value == "3" || radReport.Value == "4"){ 
		tit_callService(  
						 ""
					   , ""   
					   , "ds_iEduStatistics=ds_SearchJogeon"//inDs 
					   , "ds_DayEduStatistics=ds_oEduStatistics" //outDs
					   , ""
					   , "afterSearch"  //callBack
					   , false
					   ,false
					   ,true
					   ); 
	} else if(radReport.Value == "5" || radReport.Value == "6"){ 
		tit_callService(  
						 ""
					   , ""   
					   , "ds_iEduStatistics=ds_SearchJogeon"//inDs 
					   , "ds_EduAllJibuList=ds_oEduStatistics" //outDs
					   , ""
					   , "afterSearch"  //callBack
					   , false
					   ,false
					   ,true
					   ); 
	} 
}

