		}
	}
	//trace(ds_DayEduStatistics.SaveXML());}


function lfn_Print(obj)
{

    if (!gfn_Confirm("출력 하시겠습니까?\n\n"
                     + "시간이 오래걸릴 수 있습니다.")) { // 입력 Validation Check
        return false;
    }

	if(radReport.Value == "1" || radReport.Value == "2"){   	
		
		lfn_Search();
				
		if(ds_EduList.rowcount == 0){
			alert("데이터가 없습니다.");
			return;
		}
		
		gfn_CallReportPopup("frmCust1142R01ProcEduReport.rex"
						  , "ds_EduList"
						  , "EDU_YMD_FR=" + calEduYmdFr.Value + "^EDU_YMD_TO=" + calEduYmdTo.Value + "^TITLE=" + radReport.Text
						  , true);
							  
	} else if(radReport.Value == "3" || radReport.Value == "4"){ 

		lfn_Search();
		
		if(ds_DayEduStatistics.rowcount == 0){
			alert("데이터가 없습니다.");
			return;
		}
		vReportTitle = "일별";
	
		if(cbxJibu.Value == "1011" || cbxJibu.Value == "1151") {
			gfn_CallReportPopup("frmCust1142R01ProcEduStatisticsCourse_Seoul.rex"
							  , "ds_DayEduStatistics"
							  , "PRINT_GUBUN=" + radReport.Value + "^TITLE=" + radReport.Text
							  , true);		
		
		} else {		
			gfn_CallReportPopup("frmCust1142R01ProcEduStatisticsCourse.rex"
							  , "ds_DayEduStatistics"
							  , "PRINT_GUBUN=" + radReport.Value + "^TITLE=" + radReport.Text
							  , true);
		}
	} else if(radReport.Value == "5" || radReport.Value == "6") {
	
		lfn_Search();
				
		if(ds_EduAllJibuList.rowcount == 0){
			alert("데이터가 없습니다.");
			return;
		}
		
		gfn_CallReportPopup("frmCust1142R01ProcAllJibuEduReport.rex"
						  , "ds_EduAllJibuList"
						  , "EDU_YMD_FR=" + calEduYmdFr.Value + "^EDU_YMD_TO=" + calEduYmdTo.Value + "^TITLE=" + radReport.Text
						  , true);	
	
	
	}
                      
}
