//엑샐인쇄 시작----------------------------------------------------------------------------------------
function lfn_Excel2(obj)
{
	if(radReport.Value == "1" || radReport.Value == "2"){
		lfn_Search();
		
		if(ds_EduList.rowcount == 0){
			alert("데이터가 없습니다.");
			return;
		}
		
		// 개인정보 다운로드 사유 등록
		var parValue = "sCode=1423";	// COMMON.PERSONALDOWNLOADH
			parValue += " sRowCnt=" + ds_EduList.GetRowCount();
			parValue += " sTitle=" + quote(gdList.UserData);
		var ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	
	
		if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
			return false;
		}
		
		gdList.ExportExcelEx(radReport.Text);

	} else if(radReport.Value == "3" || radReport.Value == "4"){
		lfn_Search();
		
		if (ds_DayEduStatistics.rowcount == 0) {
			alert("데이타가 없습니다.");
			return;
		}
		
		// 불러온 데이타 정보를 그리드에 셋팅 
		var NM = "";
		var CNT = "";
		var SUM = 0;
		for(var i = 1; i < 37; i++) {
			NM = "NM"+i;			
			NM = ds_DayEduStatistics.GetColumn(0,NM);
			gdList2.SetCellProp("head", i+3, "text", NM);
			gdList2.SetCellProp("head", i+3, "text", "지역"+i);
			//if (NM == "" || NM == null) {
			//	gdList2.SetColProp(i+2,"Width",0);
			//} else {
				gdList2.SetColProp(i+2,"Width",80);
			//}
		}
		
		// 합계구하는 식
		for(i = 0; i < ds_DayEduStatistics.count(); i++) {
			SUM = 0;
			for (var j = 1; j < 37; j++) {				
				CNT = "CNT"+j;
				SUM = parseInt(SUM) + parseInt(ds_DayEduStatistics.GetColumn(i,CNT));
			}
			ds_DayEduStatistics.SetColumn(i,"CNTSUM",SUM);
		}		
		
		gdList2.ExportExcelEx(radReport.Text);		
	} else {
		lfn_Search();
		
		if(ds_EduAllJibuList.rowcount == 0){
			alert("데이터가 없습니다.");
			return;
		}
		
		gdList3.ExportExcelEx(radReport.Text);
	}
}

//엑셀 함수 입니다. (위에 제목을 넣기 위함)
function setExcelGridHeader(grdObj,strText){
	create("grid","grdExcel",'BindDataSet="' + grdObj.BindDataset +'"');
	grdExcel.contents = grdObj.Contents;	
	var headCnt = getHeadCount(grdExcel);
	var totCol  = grdExcel.GetColCount();
	var irow;
	for(var i = 0 ; i < headCnt ; i++ )	{
		irow = toInteger(grdExcel.GetCellProp("head",i,"row"));
		grdExcel.SetCellProp("head",i,"row",irow+1);
	}
	var str_temp = grdExcel.GetCurContents();
	var str_Head = '<cell align="left" col="0" colspan="' + totCol + '" display="text" text="' + strText + '"/>';	
	str_temp = replace(str_temp, '</head>',str_Head +'</head>');
	grdExcel.contents = str_temp;
	grdExcel.ExportExcelEx("sheet1","A1",false,true,true);
	Destroy(grdExcel);
}
//엑셀 함수 입니다. (위에 제목을 넣기 위함)
function getHeadCount(grdObj){
	var strConts = grdObj.Contents;	
	var iPos = pos(strConts,"<head>");
	var strHead = Mid(strConts,iPos);
	iPos = pos(strHead,"</head>");
	strHead = Mid(strHead,0,iPos+7);
	var spObj = split(strHead,chr(10),"webstyle");
	return spObj.length()-2;
}
