//직능
function radCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 1) {          //선택
        cbxCourse.SetFocus();
        cbxCourse.Enable = true;
        cbxCourse1.Enable = false;
        cbxCourse2.Enable = false;
        cbxCourse3.Enable = false;
        cbxCourse4.Enable = false;
        cbxCourse5.Enable = false;
        cbxCourse6.Enable = false;        
        cbxCourse.index=0;
        cbxCourse1.index=0;
        cbxCourse2.index=0;
        cbxCourse3.index=0;
        cbxCourse4.index=0;
        cbxCourse5.index=0;
        cbxCourse6.index=0;
	} else if(strCode == 2){     //다중선택
        cbxCourse1.SetFocus();
        cbxCourse.Enable = false;
        cbxCourse1.Enable = true;
        cbxCourse2.Enable = true;
        cbxCourse3.Enable = true;
        cbxCourse4.Enable = true;
        cbxCourse5.Enable = true;
        cbxCourse6.Enable = true;        
        cbxCourse.index=0;
        cbxCourse1.index=0;
        cbxCourse2.index=0;
        cbxCourse3.index=0;
        cbxCourse4.index=0;
        cbxCourse5.index=0;
        cbxCourse6.index=0;
	} else {                    //전체
        cbxCourse.Enable = false;
        cbxCourse1.Enable = false;
        cbxCourse2.Enable = false;
        cbxCourse3.Enable = false;
        cbxCourse4.Enable = false;
        cbxCourse5.Enable = false;
        cbxCourse6.Enable = false;
        cbxCourse.index=0;
        cbxCourse1.index=0;
        cbxCourse2.index=0;
        cbxCourse3.index=0;
        cbxCourse4.index=0;
        cbxCourse5.index=0;
        cbxCourse6.index=0;
	}
} 

function lfn_Cancel(obj)
{
	radReport.Value = "1";
	radEduGubun.Value = "0";
	radPrintGubun1.Value = "1";
	radPrintGubun2.Value = "1";
	radEduYmdGubun.Value = "1";
	
	edYear.Text = substr(getDate(),0,4);
	
	radReport_OnChanged();
	radCourse_OnChanged();
	radEduYmd_OnChanged();
}


function radEduYmd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(radEduYmdGubun.Value == ""){
		calEduYmdFr.Enable = false;
		calEduYmdTo.Enable = false;
		calEduYmdFr.Value = "";
		calEduYmdTo.Value = "";
	}
	else{
		calEduYmdFr.Enable = true;
		calEduYmdTo.Enable = true;
		calEduYmdFr.Value = substr(getDate(),0,4)+"0101";
		calEduYmdTo.Value = substr(getDate(),0,8);
	}
}

function radReport_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(radReport.Value == "1" || radReport.Value == "2"){
		radPrintGubun1.Enable = false;
		radPrintGubun2.Enable = false;
	}
	else{
		radPrintGubun1.Enable = true;
		radPrintGubun2.Enable = true;
	}
	
	if (radReport.Value == "5" || radReport.Value == "6") {
		cbxJibu.Index = 0;
		radCourse.Index = 0;
	} else {
		if (gfn_IsNull(gds_oUserInfo.GetColumn(0, "ADMINGUBUN")) || gds_oUserInfo.GetColumn(0, "ADMINGUBUN") == "DM") {
			cbxJibu.Value = gds_oUserInfo.GetColumn(0, "PDEPTCD");
		} else {
			cbxJibu.Index = 1;
		}
	}
}

// 회차 변경시 체크 (단. 2010년 이전년도는 입력 불가 
function radEduOrderGubun_OnClick(obj)
{
	if (edYear.Text >= "2010") {
		if(radEduOrderGubun.Value == "1"){
			edEduOrder.Enable = true;
		} else {
			edEduOrder.Enable = false;
		}
	} else {
		alert("회차조회는 2010년 이후만 가능합니다.");
		edEduOrder.Enable = false;
	}
}
function lfn_End(obj)
{
	ds_DayEduStatistics.Reset();
}

