function lfn_Search(obj) {
var ls_PersonKey = "";

    if (gfn_IsNull(edManagerNm.Text) && gfn_IsNull(medResidentNo.Value)) {
        gfn_Alert("이름이나 주민번호 중 하나는 입력해 주십시오.");
        edManagerNm.SetFocus();
        return;
    }
	
	if (!gfn_FormValidate(this)) { // 입력 Validation Check
		return false;
	}

	// personkey 조회
	if (!gfn_isNull(edManagerNm.Text) && (length(medResidentNo.Value) == 13)) {

		ls_PersonKey = gfn_SearchPERSONKEY(edManagerNm.Text, medResidentNo.Value, "");
		
	}
	
    // 조회이력을 저장한다.  
    ls_HistoryArg = "성명=" + edManagerNm.Text
                  + ",주민번호=" + left(medResidentNo.Value,7);
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );
    
	// 콜센터 직원용 이면
	if (gs_IsCallCenter == "Y") {
		// 콜센터 직원 조회이력 저장
		gfn_InsertCallCenterSearchPrintHistory(
				"S"
				, gs_CallCenterUID				, gds_OpenedForm.GetColumn(gds_OpenedForm.FindRow("MURL",this.ID),"MMENUID")
				, this.ID				, global.mnuLeft.cbxOpenForm.Text				, ls_HistoryArg
			);
	}

  
    ds_EduPassList.ClearData();
	tit_ClearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust1150EduPassList", false);
       		  
	tit_callService(  
	                 ""
	               , ""  
	               , ""//inDs 
	               , "ds_EduPassList=ds_oEduPassList" //outDs
	               , "MANAGERNM=" + quote(edManagerNm.Text)      //성명
                    + " BIRTHDAY=" + quote(left(medResidentNo.Value,6))    //생년월일
                    + " PERSONKEY=" + quote(ls_PersonKey)    //개인식별번호
	               , "afterSearch"  //callBack
	               , false
	               ,false
				   ,true
	               ); 
}

function afterSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);     
        return;
    }
/*
    if (ds_EduPassList.GetCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }
*/
    ln_TotalGridCount = ds_EduPassList.GetCount();

    gfn_SearchMessage(gdEduList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}


