#include "LIB::cust.js"; // 공통업무 관련

function Form_OnLoadCompleted(obj)
{
	gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
	
    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

	gfn_SearchAllJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회
	cbxJibu_OnChanged("", cbxJibu.Value);
	gfn_SearchCustomerEduCourseFullName(ds_ioCourse, "0", "선택");	// 대표고객직능 조회
	radCourse.Value = "";
    
    edYear.Text = substr(getDate(),0,4);
    
	
	gfn_SearchCommonCode(ds_oAdditionInfo, "BIZCD", "Y");
	cbxBusinessType1.Index = "0"; 
	cbxBusinessType2.Index = ds_oAdditionInfo.rowcount - 1;
	
	radCourse_OnChanged();
	radMemberYmdGubun_OnChanged();
	radReport_OnChanged();
}

function lfn_SetFormAuth(bStatus) {

}

function lfn_Search(obj) {
	
	tit_clearActionInfo();
	
	if(radReport.Value == "1"||radReport.Value == "4"){ 		tit_addSearchActionInfo("cust:searchCust1160CourseEduReport1", false);
		tit_addSearchActionInfo("cust:searchCust1160CourseEduReport2", false);
		tit_addSearchActionInfo("cust:searchCust1160CourseEduReport3", false);
		tit_addSearchActionInfo("cust:searchCust1160CourseEduReport4", false);
		tit_addSearchActionInfo("cust:searchCust1160CourseEduReport5", false);
	}
	else if(radReport.Value == "2"||radReport.Value == "3"){ 
		tit_addSearchActionInfo("cust:searchCust1160CourseEduPassReport1", false);
		tit_addSearchActionInfo("cust:searchCust1160CourseEduPassReport2", false);
		tit_addSearchActionInfo("cust:searchCust1160CourseEduPassReport3", false);
		tit_addSearchActionInfo("cust:searchCust1160CourseEduPassReport4", false);
		tit_addSearchActionInfo("cust:searchCust1160CourseEduPassReport5", false);
	}	
	ds_SearchJogeon.DeleteAll();		ds_SearchJogeon.AddRow();
	ds_CourseStatistics1.Clear();
	ds_CourseStatistics2.Clear();
	ds_CourseStatistics3.Clear();
	ds_CourseStatistics4.Clear();
	ds_CourseStatistics5.Clear();

	ds_SearchJogeon.setColumn(0, "REPORT_GUBUN", radReport.Value);	
	ds_SearchJogeon.setColumn(0, "YEAR", edYear.Value);	
	ds_SearchJogeon.setColumn(0, "JIBU", cbxJibu.Value);	
	ds_SearchJogeon.setColumn(0, "REGION_GUBUN", radRegion.Value);	
	ds_SearchJogeon.setColumn(0, "REGION1", cbxRegion1.Value);			
	ds_SearchJogeon.setColumn(0, "REGION2", cbxRegion2.Value);	
	ds_SearchJogeon.setColumn(0, "MEMBER_GUBUN", radMemberGubun.Value);	
	ds_SearchJogeon.setColumn(0, "COURSE_GUBUN", radCourse.Value);	
	ds_SearchJogeon.setColumn(0, "COURSE", cbxCourse.Value);	
	
	var Course = "";
	
	if(cbxCourse1.Value != "")
		Course += "'" + cbxCourse1.Value + "'" + ",";
	if(cbxCourse2.Value != "")
		Course += "'" + cbxCourse2.Value + "'" + ",";	
    if(cbxCourse3.Value != "")
		Course += "'" + cbxCourse3.Value + "'" + ",";	
	if(cbxCourse4.Value != "")
		Course += "'" + cbxCourse4.Value + "'" + ",";	
	if(cbxCourse5.Value != "")
		Course += "'" + cbxCourse5.Value + "'" + ",";	
	if(cbxCourse6.Value != "")
		Course += "'" + cbxCourse6.Value + "'" + ",";	
	 	
	ds_SearchJogeon.setColumn(0, "COURSE1_6IN", left(Course, length(Course)-1));	
	ds_SearchJogeon.setColumn(0, "BIZ_GUBUN", radBusinessType.Value);	
	ds_SearchJogeon.setColumn(0, "BIZ1", cbxBusinessType1.Value);	
	ds_SearchJogeon.setColumn(0, "BIZ2", cbxBusinessType2.Value);	
	
	ds_SearchJogeon.setColumn(0, "MEMBERYMD_GUBUN", radMemberYmdGubun.Value);	
	ds_SearchJogeon.setColumn(0, "MEMBER_YMD_FR", calMemberYmdFr.Value);	
	ds_SearchJogeon.setColumn(0, "MEMBER_YMD_TO", calMemberYmdTo.Value);	
	ds_SearchJogeon.setColumn(0, "EDUYMD_GUBUN", radEduYmdGubun.Value);	
	ds_SearchJogeon.setColumn(0, "EDU_YMD_FR", calEduYmdFr.Value);	
	ds_SearchJogeon.setColumn(0, "EDU_YMD_TO", calEduYmdTo.Value);	
	ds_SearchJogeon.setColumn(0, "EDU_GUBUN", radEduGubun.Value);	
	
	if (edYear.Text >= 2010) {
		ds_SearchJogeon.setColumn(0, "EDU_ORDERGUBUN", radEduOrderGubun.Value); // 교육횟차 구분
		ds_SearchJogeon.setColumn(0, "EDU_ORDER", edEduOrder.Text);  			// 교육횟차
	}
              
	tit_callService(  
	                 ""
	               , ""   
	               , "ds_iCourseStatistics=ds_SearchJogeon"//inDs 
	               , "ds_CourseStatistics1=ds_oCourseStatistics1 ds_CourseStatistics2=ds_oCourseStatistics2 ds_CourseStatistics3=ds_oCourseStatistics3 ds_CourseStatistics4=ds_oCourseStatistics4 ds_CourseStatistics5=ds_oCourseStatistics5" //outDs
	               , ""
	               , "afterSearch"  //callBack
	               , false
	               ,""
				   ,true
	               ); 
	
	
}

