	}
	var RowCount1 = ds_CourseStatistics1.rowcount;
	var RowCount2 = ds_CourseStatistics2.rowcount;
	var RowCount3 = ds_CourseStatistics3.rowcount;
	var RowCount4 = ds_CourseStatistics4.rowcount;
	var RowCount5 = ds_CourseStatistics5.rowcount;

	gfn_CallReportPopup("frmCust1160R01CourseEduReport.rex"
					  , "ds_CourseStatistics1||ds_CourseStatistics1||ds_CourseStatistics1||ds_CourseStatistics2||ds_CourseStatistics3||ds_CourseStatistics4||ds_CourseStatistics5"
