function lfn_Cancel(obj)
{
	radReport.Value = "1";
	radEduGubun.Value = "0";
	radMemberYmdGubun.Value = "";
	
	edYear.Text = substr(getDate(),0,4);
	
	radReport_OnChanged();
	radCourse_OnChanged();
	radMemberYmdGubun_OnChanged();
	radRegion_OnChanged();
	radBusinessType_OnChanged();
	radReport_OnChanged();
}

//직능
function radCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 1) {          //선택
        cbxCourse.SetFocus();
        cbxCourse.Enable = true;
        cbxCourse1.Enable = false;
        cbxCourse2.Enable = false;
        cbxCourse3.Enable = false;
        cbxCourse4.Enable = false;
        cbxCourse5.Enable = false;
        cbxCourse6.Enable = false;        
        cbxCourse.index=0;
        cbxCourse1.index=0;
        cbxCourse2.index=0;
        cbxCourse3.index=0;
        cbxCourse4.index=0;
        cbxCourse5.index=0;
        cbxCourse6.index=0;
	} else if(strCode == 2){     //다중선택
        cbxCourse1.SetFocus();
        cbxCourse.Enable = false;
        cbxCourse1.Enable = true;
        cbxCourse2.Enable = true;
        cbxCourse3.Enable = true;
        cbxCourse4.Enable = true;
        cbxCourse5.Enable = true;
        cbxCourse6.Enable = true;        
        cbxCourse.index=0;
        cbxCourse1.index=0;
        cbxCourse2.index=0;
        cbxCourse3.index=0;
        cbxCourse4.index=0;
        cbxCourse5.index=0;
        cbxCourse6.index=0;
	} else {                    //전체
        cbxCourse.Enable = false;
        cbxCourse1.Enable = false;
        cbxCourse2.Enable = false;
        cbxCourse3.Enable = false;
        cbxCourse4.Enable = false;
        cbxCourse5.Enable = false;
        cbxCourse6.Enable = false;
        cbxCourse.index=0;
        cbxCourse1.index=0;
        cbxCourse2.index=0;
        cbxCourse3.index=0;
        cbxCourse4.index=0;
        cbxCourse5.index=0;
        cbxCourse6.index=0;
	}
} 

function radMemberYmdGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{ 
	if(radMemberYmdGubun.Value == ""){ 
		calMemberYmdFr.Enable = false;
		calMemberYmdTo.Enable = false;
		calMemberYmdFr.Value = "";
		calMemberYmdTo.Value = "";
	}
	else if(radMemberYmdGubun.Value == "1"){ 
		calMemberYmdFr.Enable = true;
		calMemberYmdTo.Enable = true;
		calMemberYmdFr.Value = substr(getDate(),0,4)+"0101";
		calMemberYmdTo.Value = substr(getDate(),0,8);
	}
	
}

function radEduYmdGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{ 
	if(radEduYmdGubun.Value == ""){ 
		calEduYmdFr.Enable = false;
		calEduYmdTo.Enable = false;
		calEduYmdFr.Value = "";
		calEduYmdTo.Value = "";
	}
	else if(radEduYmdGubun.Value == "1"){ 
		calEduYmdFr.Enable = true;
		calEduYmdTo.Enable = true;
		calEduYmdFr.Value = substr(getDate(),0,4)+"0101";
		calEduYmdTo.Value = substr(getDate(),0,8);
	}
	
}

function cbxJibu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	gfn_SearchCodeRegion(strCode, ds_oRegion, "0"); //지역 코드 조회
	cbxRegion1.Index = 0;
	cbxRegion2.Index = ds_oRegion.rowcount-1;
}

//용도
function radBusinessType_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	cbxBusinessType1.Index = "0"; 
	cbxBusinessType2.Index = ds_oAdditionInfo.rowcount - 1;
	
	if (strCode == "1") { // 선택 
        cbxBusinessType1.Enable = true;
        cbxBusinessType2.Enable = true;                       
        cbxBusinessType1.SetFocus();
	} else {
        cbxBusinessType1.Enable = false;
        cbxBusinessType2.Enable = false;                       	
	}
}

function radRegion_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	cbxRegion1.Index = "0"; 
	cbxRegion2.Index = ds_oRegion.rowcount - 1;
	if (strCode == "1") {          //선택
        cbxRegion1.SetFocus();
        cbxRegion1.Enable = true;
        cbxRegion2.Enable = true;
    }else {
		cbxRegion1.Enable = false;
        cbxRegion2.Enable = false;
    }    
}

function radReport_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(radReport.Value == "1"){
		edYear.Text = "";
		edYear.Visible = false;
		Static2.Visible = false;
		radEduGubun.Value = ""; 
		radEduGubun.Enable = false;
	}else{
		edYear.Text = substr(getDate(),0,4);
		edYear.Visible = true;
		Static2.Visible = true;
		radEduGubun.Value = "0";
		radEduGubun.Enable = true;
	}
	
	if(radReport.Value == "1" || radReport.Value == "4"){		
		// 교육회차
		radEduOrderGubun.Value = ""; 
		radEduOrderGubun.Enable = false;
		edEduOrder.Enable = false;
		
		radEduYmdGubun.Enable = false; 
		radEduYmdGubun_OnChanged();
	}else{
		// 교육회차
		radEduOrderGubun.Enable = true;
		edEduOrder.Enable = true;;
		
		radEduYmdGubun.Enable = true; 
		radEduYmdGubun_OnChanged();
	}
	
}

// 회차 변경시 체크 (단. 2010년 이전년도는 입력 불가 
function radEduOrderGubun_OnClick(obj)
{
	if (edYear.Text >= "2010") {
		if(radEduOrderGubun.Value == "1"){
			edEduOrder.Enable = true;
		} else {
			edEduOrder.Enable = false;
		}
	} else {
		alert("회차조회는 2010년 이후만 가능합니다.");
		edEduOrder.Enable = false;
	}
}
