//수납내역 리스트 레포트용 1.전체현황, 2.회비현황, 3.교육비현황, 4.상세현황  날짜 집계 포함
function lfn_Print(obj)
{	

	if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }

    if (radPrintGubun == "1") {
		trace(radGubun.text);
		lfn_serchMemberfee1_1();       // 해당 조건에 따른 일별 회비 /교육비 수납 자료 조회 (일자별 총 합계 )
		lfn_serchMemberfee2_1();       // 해당 조건에 따른 일별 회비 /교육비 수납 자료 조회 (일자별 분리)

		 //레포트 호출  
		 gfn_CallReportPopup("frmcust2090R01DailyFeeReport.rex"
					,"ds_oMemberfee2_1||ds_oMemberfee1_1||ds_oMemberfee1_1"
					, "JIBU=" + quote(cbxJibu.Text)                                                    
					  + "^STARTDATE=" + quote(calStartDate.Value)
					  +   "^ENDDATE=" + quote(calEndDate.Value)
					,""
					,true
		 );
     } else {
		btnCreateListTotaling_OnClick();
     }
}


/*
* 해당 조건에 따른 일별 총 집계 회비 /교육비 수납 자료 조회(조건일 합계)
*/
function lfn_serchMemberfee1_1()  
{
    ds_oMemberfee1_1.ClearData(); // 데이터셋 초기화            	 
    tit_clearActionInfo();
    tit_AddCSVSearchActionInfo("cust:searchCust2090Memberfee1_1", false); //회비/교육비 날짜전체집계(날짜 시작일 ~날짜 종료일)      
                                                                        //전자결제 연동 복사해서 씀 
    tit_callService(
        ""
        ,""
        ,""// inDs
		,"ds_oMemberfee1_1=ds_oMemberfee1_1"  // outDs
        ,"JIBU=" + quote(cbxJibu.value)
            + " rpStartDate=" + quote(calStartDate.Value) // 날짜 시작
            + " rpEndDate=" + quote(calEndDate.Value)	  // 날짜 끝 
            + " ORDER_GUBUN=" + quote(radOrder.Value)      //처리구분
            + " CFMEMBERGUBUN_CD=" + quote(radMemberGubun.Value)      // 전체, 회비, 교육비 여부
            + " CFGUBUNCD_NAME=" + quote(radGubun.text)      //수납구분 (결제방법 이름)             
            + " SUNAB_GUBUN=" + quote(radGubun.Value)      //수납구분 (결제방법) args
        ,"lfn_AfterSearchElecAppr"
        ,false
        ,""
        ,true        
    );	
}

/*
* 해당 조건에 따른 일별 회비 /교육비 수납 자료 조회
*/
function lfn_serchMemberfee2_1()  
