            + " rpStartDate=" + quote(calStartDate.Value) // 날짜 시작
            + " rpEndDate=" + quote(calEndDate.Value)	  // 날짜 끝 
            + " ORDER_GUBUN=" + quote(radOrder.Value)      //처리구분
            + " CFMEMBERGUBUN_CD=" + quote(radMemberGubun.Value)      // 전체, 회비, 교육비 여부
