/*
* 조회 쿼리 실행 후 실행(에러 처리)
*/
function lfn_AfterSearchElecAppr(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

};


