/*--------------------------------------------------------*
 *  엑셀  버튼 선택 시                                    *
 *--------------------------------------------------------*/
function lfn_localExcel(obj)
{  
	if (radPrintGubun.Value == "1") {
		lfn_serchMemberfee1_1();       // 해당 조건에 따른 일별 회비 /교육비 수납 자료 조회 (일자별 총 합계 )
		lfn_serchMemberfee2_1();       // 해당 조건에 따른 일별 회비 /교육비 수납 자료 조회 (일자별 분리)

		gdList2.ExportExcelEx("sheet1");
	} else {
		lfn_Serch_EducationCost(); // 교육비	
		gdList.ExportExcelEx("sheet1");
	}

}



