#include "LIB::cust.js"; // 공통업무 관련

function Form_OnLoadCompleted(obj)
{
	gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
	
    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

	edYear.Text = substr(getDate(),0,4);
	
	gfn_SearchAllJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회   
	gfn_SearchCustomerCourseFullName(ds_ioCourse, "0", "선택");	// 대표고객직능 조회
	cbxJibu_OnChanged("", cbxJibu.Value);
	
	//콤보박스-직능
    cbxCourse.index=0;
	cbxCourse1.index=0;
	cbxCourse2.index=0;
	cbxCourse3.index=0;
	cbxCourse4.index=0;
	cbxCourse5.index=0;
	cbxCourse6.index=0;    
	
	calStartDate.Value = left(getDate(),4)+"0101";
	calEndDate.Value = left(getDate(),8);
	
}

//대표직능
function radCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 0) {          //선택
        cbxCourse.SetFocus();
        cbxCourse.Enable = true;
        cbxCourse1.Enable = false;
        cbxCourse2.Enable = false;
        cbxCourse3.Enable = false;
        cbxCourse4.Enable = false;
        cbxCourse5.Enable = false;
        cbxCourse6.Enable = false;        
        cbxCourse.index = 0;
        cbxCourse_OnChanged(object("cbxCourse"),"");
        cbxCourse1.index = 0;
        cbxCourse2.index = 0;
        cbxCourse3.index = 0;
        cbxCourse4.index = 0;
        cbxCourse5.index = 0;
        cbxCourse6.index = 0;
        cbxCourseD.Enable = false;	
        cbxCourseD.index = 0;        
	} else if(strCode == 1){     //다중선택
        cbxCourse1.SetFocus();
        cbxCourse.Enable = false;
        cbxCourse1.Enable = true;
        cbxCourse2.Enable = true;
        cbxCourse3.Enable = true;
        cbxCourse4.Enable = true;
        cbxCourse5.Enable = true;
        cbxCourse6.Enable = true;        
        cbxCourse.index = 0;
        cbxCourse_OnChanged(object("cbxCourse"),"");
        cbxCourse1.index = 0;
        cbxCourse2.index = 0;
        cbxCourse3.index = 0;
        cbxCourse4.index = 0;
        cbxCourse5.index = 0;
        cbxCourse6.index = 0;
        radCourseD.Enable = false;
        radCourseD.Index="0";	
        cbxCourseD.Enable = false;
        cbxCourseD.index = 0;        
	} else {                    //전체
        cbxCourse.Enable = false;
        cbxCourse1.Enable = false;
        cbxCourse2.Enable = false;
        cbxCourse3.Enable = false;
        cbxCourse4.Enable = false;
        cbxCourse5.Enable = false;
        cbxCourse6.Enable = false;
        cbxCourse.index = 0;
        cbxCourse_OnChanged(object("cbxCourse"),"");
        cbxCourse1.index = 0;
        cbxCourse2.index = 0;
        cbxCourse3.index = 0;
        cbxCourse4.index = 0;
        cbxCourse5.index = 0;
        cbxCourse6.index = 0;       
        radCourseD.Enable = false;
        radCourseD.Index="0";
        cbxCourseD.Enable = false;
        cbxCourseD.index = 0;        

	}
}

//대표직능 --> 선택콤보
function cbxCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if(gfn_IsNull(strCode)){
        radCourseD.Enable = false;
        radCourseD.Index="0";
        cbxCourseD.Enable = false;
        cbxCourseD1.Enable = false;
        cbxCourseD2.Enable = false;
        cbxCourseD3.Enable = false;
        cbxCourseD4.Enable = false;
        cbxCourseD5.Enable = false;
        cbxCourseD6.Enable = false;   
        radCpersonGubun.Index = 0;
        radCpersonGubun.Enable = false;
        lfn_FilterPersonGubun(strCode);        
    }else{
        gfn_SearchCustomerDetailCourse(cbxCourse.Value,ds_ioCourseD, "0", "선택");// 세부고객직능 조회	
        radCourseD.Enable = true;
        radCourseD.Index="0";
        cbxCourseD.Enable = false;
        cbxCourseD1.Enable = false;
        cbxCourseD2.Enable = false;
        cbxCourseD3.Enable = false;
        cbxCourseD4.Enable = false;
        cbxCourseD5.Enable = false;
        cbxCourseD6.Enable = false; 
        cbxCourseD.index = 0;
        cbxCourseD1.index = 0;
        cbxCourseD2.index = 0;
        cbxCourseD3.index = 0;
        cbxCourseD4.index = 0;
        cbxCourseD5.index = 0;
        cbxCourseD6.index = 0;

        radCpersonGubun.Index = 1;
        radCpersonGubun.Enable = true;
        lfn_FilterPersonGubun(strCode);
    }
    radCpersonGubun_OnChanged(object("radCpersonGubun"),radCpersonGubun.Value);

}

//세부직능-->선택콤보
function cbxCourseD_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if(gfn_IsNull(strCode)){
        radCpersonGubun.Enable = false;
        radCpersonGubun.Index = 0;
        lfn_FilterPersonGubun(strCode);

    }else{
        radCpersonGubun.Index = 1;
        radCpersonGubun.Enable = true;
        lfn_FilterPersonGubun(strCode);
    }
    radCpersonGubun_OnChanged(object("radCpersonGubun"),radCpersonGubun.Value);
}


