        cbxCourseD3.index = 0;
        cbxCourseD4.index = 0;
        cbxCourseD5.index = 0;
        cbxCourseD6.index = 0;
	
	} else {                    //전체
        if(cbxCourse.Value=="30"||cbxCourse.Value=="50") {
            radCpersonGubun.Enable=true;
