function fn_Search(){

	var course="";
	var courseD="";
	
	// 대표직능
	if(cbxCourse1.Value != "")
        course += "'" + cbxCourse1.Value + "'" + ",";
    if(cbxCourse2.Value != "")
        course += "'" + cbxCourse2.Value + "'" + ","; 
    if(cbxCourse3.Value != "")
        course += "'" + cbxCourse3.Value + "'" + ","; 
    if(cbxCourse4.Value != "")
        course += "'" + cbxCourse4.Value + "'" + ","; 
    if(cbxCourse5.Value != "")
        course += "'" + cbxCourse5.Value + "'" + ","; 
    if(cbxCourse6.Value != "")
        course += "'" + cbxCourse6.Value +"'" + ","; 
    course = left(course,length(course)-1); 
    
    // 세부직능
    if(cbxCourseD1.Value != "")
        courseD += "'" + cbxCourseD1.Value + "'" + ",";
    if(cbxCourseD2.Value != "")
        courseD += "'" + cbxCourseD2.Value + "'" + ","; 
    if(cbxCourseD3.Value != "")
        courseD += "'" + cbxCourseD3.Value + "'" + ","; 
    if(cbxCourseD4.Value != "")
        courseD += "'" + cbxCourseD4.Value + "'" + ","; 
    if(cbxCourseD5.Value != "")
        courseD += "'" + cbxCourseD5.Value + "'" + ","; 
    if(cbxCourseD6.Value != "")
        courseD += "'" + cbxCourseD6.Value +"'" + ","; 
    courseD = left(courseD,length(courseD)-1); 

    ds_SunapList.Clear();
	tit_clearActionInfo();		tit_addSearchActionInfo("cust:searchCust2100RegionSunapReport", false);
	
	//ds_SearchJogeon.DeleteAll();		//ds_SearchJogeon.AddRow();
    /*
	ds_SearchJogeon.setColumn(0, "JIBU", cbxJibu.Value);	
	ds_SearchJogeon.setColumn(0, "REGION_GUBUN", radRegion.Value);	
	ds_SearchJogeon.setColumn(0, "REGION1", cbxRegion1.Value);	
	ds_SearchJogeon.setColumn(0, "REGION2", cbxRegion2.Value);	
	ds_SearchJogeon.setColumn(0, "SETLM_GUBUN", radSetlmGubun.Value);	
	*/
	tit_callService(  
	                 ""
	               , ""  
	               , ""  //"ds_iSunapList=ds_SearchJogeon"//inDs 
	               , "ds_SunapList=ds_oSunapList" //outDs
	              ," REGION1=" + quote(cbxRegion1.Value) //   --지역 1 
                 + " REGION2=" + quote(cbxRegion2.Value)//   --지역 2
                 + " REGION_GUBUN=" + quote(radRegion.Value)//   --지역구분
                 + " YEAR=" + quote(edYear.Text)    //기준년도
                 + " JIBU=" + quote(cbxJibu.Value)//   --지부
                 + " SELECT_COURSE=" +quote(radCourse.value) //직능선택구분====
			  	 + " COURSE_GUBUN=" +quote(cbxCourse.value)  //대표직능선택시 (1개 선택시)
				 + " COURSE_GUBUN_IN=" +quote(course)        //여러개선택시
				 + " SELECT_COURSE_D=" +quote(radCourseD.value) //직능선택구분====
				 + " COURSE_GUBUN_D=" +quote(cbxCourseD.value)  //세부직능선택시 (1개 선택시)
				 + " COURSE_GUBUN_IN_D=" +quote(courseD)        //여러개선택시
                 + " SETLM_GUBUN=" + quote(radSetlmGubun.Value)//   --회비구분
                 + " SUNAPDATESTART=" + quote(left(calStartDate.Value,8))//   --수납일자시작
                 + " SUNAPDATEEND=" + quote(left(calEndDate.Value,8))//   --수납일자종료
                   // args	             
	               , "afterSearch"  //callBack
	               , false
	               ,false
				   ,true
	               ); 
}

