function fn_Search(){
	tit_clearActionInfo();
		tit_addSearchActionInfo("cust:searchCust2110BusinessSunapReport", false);
	
	ds_SearchJogeon.DeleteAll();		ds_SearchJogeon.AddRow();
	ds_SunapList.Clear();
	ds_SearchJogeon.setColumn(0, "YEAR", edYear.Text);	
	ds_SearchJogeon.setColumn(0, "JIBU", cbxJibu.Value);	
	ds_SearchJogeon.setColumn(0, "BIZ_GUBUN", radBusinessType.Value);	
	//ds_SearchJogeon.setColumn(0, "BIZ1", cbxBusinessType1.Value);	
	//ds_SearchJogeon.setColumn(0, "BIZ2", cbxBusinessType2.Value);	
	ds_SearchJogeon.setColumn(0, "SETLM_GUBUN", radSetlmGubun.Value);	
	
	tit_callService(  
	                 ""
	               , ""  
	               , "ds_iSunapList=ds_SearchJogeon"//inDs 
	               , "ds_SunapList=ds_oSunapList" //outDs
	               , "BIZ1=" + quote(cbxBusinessType1.Value) // 
                   +" BIZ2=" + quote(cbxBusinessType1.Value)//  
	               , "afterSearch"  //callBack
	               , false
	               ,false
				   ,true
	               ); 
}

