Window.Script:
#include "LIB::cust.js"; // 공통업무 관련

function Form_OnLoadCompleted(obj)
{
	gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
	
    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

	// 년도자료 생성
	for (var i = 0; i < 5; i++) {
		ds_ioYear.AddRow();
		ds_ioYear.SetColumn(i, "CD", toString(toNumber(substr(getdate(),0,4))+1 - i));
		ds_ioYear.SetColumn(i, "DATA", toString(toNumber(substr(getdate(),0,4))+1 - i)+"년");
	}
	cbxYear.Index = 1;
	cbxYear.SetFocus();
	
	lfn_Search();	

}

function lfn_SetFormAuth(bStatus) {

}

function lfn_Search(){

	ds_oEduSchedule.ClearData();
		
	tit_clearActionInfo(this);

	tit_addSearchActionInfo("cust:searchCust4060CyberEduOrderSearch", false);
	
	tit_callService(  
	                 ""
	               , ""  
	               , ""//inDs 
	               , "ds_oEduSchedule=ds_oEduSchedule" //outDs
	               , "ESYEAR=" + quote(cbxYear.Value)
	               , "afterSearch"  //callBack
	               , false
	               ,false
				   ,true
	               ); 
}

// 조회 후 callbackfunction afterSearch(errCode, errMsg){
	//trace(ds_CustomerList.SaveXML());
	if(errCode != 0 ){
		alert("오류가 발생했습니다.");
		return;
	}
	else{
		if(ds_oEduSchedule.rowcount > 0){
				
		} else {
			gfn_Alert("사이버교육 회차정보가 없습니다.");
		}
	}}


function lfn_Save(obj)
{
	if(!gfn_Confirm("자료를 생성하시겠습니까?")){
		return;
	}


	if(cbxYear.Value == ""){
		alert("년도를 선택하여 주십시오.");
		return;
	}
	
	lfn_Search();
	
	if(ds_oEduSchedule.rowcount > 0){
			alert("해당년도는 이미 자료가 있습니다.");
			return;
	}

	tit_clearActionInfo();
	
	tit_addSingleActionInfo("cust:executeCust4060CyberEduOrderSave", false);
	
	ds_SendList.Clear();		
	tit_callService(  
	                 ""
	               , ""  
	               , ""//inDs 
	               , "" //outDs
	               , "ESYEAR=" + quote(cbxYear.Value)
	               , "lfn_AfterSave"  //callBack
	               , ""
	               ,true
				   ,true
	               ); 
               
	               
}
	
	
// INSERT후 callback function lfn_AfterSave(errCode, errMsg){
	//trace(ds_CustomerList.SaveXML());
	if(errCode != 0 ){
		alert("오류가 발생했습니다.");
		return;
	}
	lfn_Search();
	alert("사이버교육 회차가 생성되었습니다.");}


function cbxYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_Search();
}
