// 신규추가 상태 세팅
function lfn_AdditionStatus() {	

	medCNo.Value = "";						//고객번호
	medCNo.Enable = false;					//고객번호
    //cbxCourse.Enable = true;					//대표직능

	/* 대상물 정보 */


	cbxRegion.SetFocus();						//지역 콤보 포커스
}

/*
 * 기능 : 초기화 (모든 데이터셋)
 */
function lfn_ClearData() {
	//Dataset Clear
	ds_ioCustomerInfo.ClearData();		// 고객정보
	ds_ioBuildingInfo.ClearData(); 		// 대상물 정보(MAIN 2)
	ds_ioTankInfo.ClearData(); 		    // 이동탱크 부가정보

	lbCancelDate.Value = "";			// 선임자 해임처리시스템일시
}

/*
 * 기능 : Main 데이타셋 입력가능상태로 만들기
 */
function lfn_AddDataMain() {
	ds_ioCustomerInfo.InsertRow(0);			// 고객정보
	ds_ioBuildingInfo.InsertRow(0); 		// 대상물 정보(MAIN 2)
	ds_ioTankInfo.InsertRow(0); 		    // 이동탱크 부가정보
}

/*
 * 기능 : CheckBox Check 여부
 */
function lfn_SetCheckStatus(bStatus) {

    chkRegion.Value = bStatus;
    chkCourse.Value = bStatus;
    chkDetailCourse.Value = bStatus;

	/* 대상물 정보 */
    chkBuilding.Value = bStatus;	
    chkBNM.Value = bStatus;
    chkAddr.Value = bStatus;
    chkFireCenter.Value = bStatus;
    chkTel.Value = bStatus;

    // 이동탱크 부가정보
    chkTank.Value = bStatus;	
    chkCarNo.Value = bStatus;	
    chkCarYear.Value = bStatus;	
    chkPerMissionNo.Value = bStatus;
    chkRemark.Value = bStatus;
    chkParkPlace.Value = bStatus;	
    chkInstlrAddr.Value = bStatus;	

}

/*
 * 기능 : CheckBox Enable 여부
 */
function lfn_SetCheckEnableStatus(bStatus) {

    chkRegion.Enable = bStatus;
    chkCourse.Enable = bStatus;
    chkDetailCourse.Enable = bStatus;

	/* 대상물 정보 */
    chkBuilding.Enable = bStatus;	
    chkBNM.Enable = bStatus;
    chkAddr.Enable = bStatus;
    chkFireCenter.Enable = bStatus;
    chkTel.Enable = bStatus;

    // 이동탱크 부가정보
    chkTank.Enable = bStatus;	
    chkCarNo.Enable = bStatus;	
    chkCarYear.Enable = bStatus;	
    chkPerMissionNo.Enable = bStatus;
    chkRemark.Enable = bStatus;
    chkParkPlace.Enable = bStatus;	
    chkInstlrAddr.Enable = bStatus;	

}

// 소방서 콤보 Changed 이벤트
function cbxStation_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_FilterFireCenter(strCode);	// 소방센터 필터링
}


// 소방서 필터링
function lfn_FilterFireStation(sFilterGubun) {	
	ds_oFireCd.UnFilter();

	var ls_FilterExpr = "";
    if (!gfn_IsNull(sFilterGubun)) {
        ls_FilterExpr += "JIBUCD=='" + trim(sFilterGubun) + "'";
    }

    ds_oFireCd.Filter(ls_FilterExpr);

	if( ds_oFireCd.FindRow("DATA", cbxStation.Text) == -1) {
		cbxStation.Text = "";
    }
}

// 소방센터 필터링
function lfn_FilterFireCenter(sFilterGubun) {	
	ds_oFireCenterCd.UnFilter();

	var ls_FilterExpr = "";
    if (!gfn_IsNull(sFilterGubun)) {
        ls_FilterExpr += "UPCD=='" + trim(sFilterGubun) + "'";
    } else {
		ls_FilterExpr += "UPCD=='" + trim("~!@#$%") + "'";
    }

    ds_oFireCenterCd.Filter(ls_FilterExpr);

	if( ds_oFireCenterCd.FindRow("DATA", cbxCenter.Text) == -1) {
		cbxCenter.Text = "";
    }
}


// 청정보 조회
function lfn_FPISSearchFPISDanTankInfo(sArg) 
{    
    ds_ioFPISDanTankInfo.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
    tit_addSearchActionInfo("cust:searchCust6301FPISDanTankInfo", false);
    // 서버 호출
    tit_callService(
        ""
        , ""
        , "" // inDs
        , "ds_ioFPISDanTankInfo=ds_ioFPISDanTankInfo" // outDs
        , sArg
        , "lfn_AfterFPISSearchDanTankInfo"
        ,false
        ,false
        ,true
    );

}


/*
 * 기능 : 청조회 후 처리해야 할 내용
 * parameter : ErrorCode - 에러코드
 *             ErrorMsg - 에러메시지
 * Return : 없음
 */

function lfn_AfterFPISSearchDanTankInfo(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

	if(ds_ioFPISDanTankInfo.rowcount = 0) {
		gfn_Alert(MSGI00010);	//검색된 레코드가 없습니다.
		lfn_Cancel();				//초기화
		if(length(ls_curComponent) > 0 ) {
			object(ls_curComponent).SetFocus();
		}
    }
}

// 안전원 대상물 검색
function btnBuildingSearch_OnClick(obj)
{
	//팝업
	var sArg  = "sBuildingNm=" + quote(trim(ds_oJubsuInfo.GetColumn(0, "FCBULDNAME")));
		sArg += " sGtmgno=" + quote(ds_oJubsuInfo.GetColumn(0, "CGTMGNO"));
	/*
     * arrNo[0] : OLD:검색된 경우, NEW:신규추가
     * arrNo[1] : 지부
     * arrNo[2] : 고객번호
     */
    var arrNo = Dialog("cust::frmcust0032PSearchbuilding.xml", sArg, 600, 400, "", -1, -1);
    if (length(arrNo) > 0) {
		if(arrNo[0] == "OLD") {	// 대상물이 검색된 경우
            sStatus = "U";      // 대상물 검색상태
            var sArg = "CGTMGNO=" + quote(arrNo[1]);	// 고객번호
            sArg += " CNO="	+ quote(arrNo[2]); 	// 지부코드

			lfn_SearchAllInfo(sArg);
		} else if(arrNo[0] == "NEW") {	// 신규추가일경우
            sStatus = "I";      // 대상물 신규상태
            cbxCourse.Value = ds_oJubsuInfo.GetColumn(0, "CGCOURSECD");
            cbxDetailCourse.Value = ds_oJubsuInfo.GetColumn(0, "CCOURSECD");

            lfn_SetEnableStatus(false);

            lfn_ClearData();					//초기화 (모든 데이터셋)	
            lfn_AddDataMain();					//Main 데이타셋 입력가능상태로 만들기
			lfn_AdditionStatus();
		}
		lfn_SetEnableStatus(false);
    }
}

// 안전원 한방(대상물+이동탱크 조회)조회
function lfn_SearchAllInfo(sArg) 
{    
    ds_oAllObject.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
    tit_addSearchActionInfo("cust:searchCust6301TankInfo", false);
    // 서버 호출
    tit_callService(
        ""
        , ""
        , "" // inDs
        , "ds_oAllObject=ds_oAllObject" // outDs
        , sArg
        , "lfn_AfterSearchAllInfo"
        ,false
        ,false
        ,true
    );
}

/*
 * 기능 : 안전원 한방조회 후 처리해야 할 내용
 * parameter : ErrorCode - 에러코드
 *             ErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchAllInfo(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

	if(ds_oAllObject.rowcount > 0) {
		lfn_SeparateDataset();	

		// 대상물 구/새주소 구분
		cbxAddrGubun_OnChanged(this,cbxAddrGubun.Value);
		// 설치자주소 구/새주소구분 변경시
		cbxHopeParkAddrGubun_OnChanged(this,cbxHopeParkAddrGubun.Value);
		// 설치자주소 구/새주소구분 변경시
		cbxHopeInstlrAddrGubun_OnChanged(this,cbxHopeInstlrAddrGubun.Value);

	} else {
		gfn_Alert(MSGI00010);	//검색된 레코드가 없습니다.
		lfn_Cancel();				//초기화
		if(length(ls_curComponent) > 0 ) {
			object(ls_curComponent).SetFocus();
		}
    }
}

// 고객(+고객부가정보)+대상물+이동탱크정보 조회값 데이타셋 분리(TLogin 고려)
function lfn_SeparateDataset() {
	ds_ioCustomerInfo.ClearData();			// 고객정보
    ds_ioBuildingInfo.ClearData();
    ds_ioTankInfo.ClearData();

    ds_ioCustomerInfo.InsertRow(0);
    ds_ioBuildingInfo.InsertRow(0);
    ds_ioTankInfo.InsertRow(0);

    if(ds_oAllObject.rowcount == 0) { //데이타 미존재    
        return;
    }

    for(var i=0; i<ds_oAllObject.colcount; i++) {
        for(var j=0; j<ds_ioCustomerInfo.colcount; j++) {
            if (ds_oAllObject.GetColID(i) == ds_ioCustomerInfo.GetColID(j)) {
                ds_ioCustomerInfo.SetColumn(0, ds_ioCustomerInfo.GetColID(j), ds_oAllObject.GetColumn(0, ds_oAllObject.GetColID(i)));
            }
        }    
        for(var j=0; j<ds_ioBuildingInfo.colcount; j++) {
            if (ds_oAllObject.GetColID(i) == ds_ioBuildingInfo.GetColID(j)) {
                ds_ioBuildingInfo.SetColumn(0, ds_ioBuildingInfo.GetColID(j), ds_oAllObject.GetColumn(0, ds_oAllObject.GetColID(i)));
            }
        }
        for(var j=0; j<ds_ioTankInfo.colcount; j++) {
            if (ds_oAllObject.GetColID(i) == ds_ioTankInfo.GetColID(j)) {
                ds_ioTankInfo.SetColumn(0, ds_ioTankInfo.GetColID(j), ds_oAllObject.GetColumn(0, ds_oAllObject.GetColID(i)));
            }
        }
    }

    // 상단 조회박스
    cbxJibu.Value = ds_ioCustomerInfo.GetColumn(0, "CGTMGNO");
    cbxJibu_OnChanged(object("cbxJibu"), cbxJibu.Value);
    medCNo.Value = ds_ioCustomerInfo.GetColumn(0, "CNO");
    cbxRegion.Value = ds_ioCustomerInfo.GetColumn(0, "CREGCD");

    cbxCourse.Value = ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CGCOURSECD");				//대표직능
    cbxCourse_OnChanged(object("cbxCourse"), cbxCourse.Value);											//대표직능 변경 이벤트 강제 발생
    cbxDetailCourse.Value = ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CCOURSECD");			//세부직능
    lfn_FilterDetailCourse(ds_ioCustomerInfo.GetColumn(0, "CGCOURSECD"));								//세부고객직능 필터링

    ds_ioBuildingInfo.ApplyChange();
}


// 적용전 안전원정보 dataset에 복사
function lfn_oldInfoCopy() {
	// 기초정보
	sOldRegion = cbxRegion.CodeColumn; 				// 지역
	sOldCourse = cbxCourse.CodeColumn; 				// 직능
	sOldDetailCourse = cbxDetailCourse.CodeColumn;	// 세부직능

	ds_ioOldCustomerInfo.Copy("ds_ioCustomerInfo");		 // 고객정보 DS복사
	ds_ioOldBuildingInfo.Copy("ds_ioBuildingInfo");		 // 대상물 DS복사
	ds_ioOldTankInfo.Copy("ds_ioTankInfo");		 		 // 이동탱크부가정보 DS복사
}


// 변경내용 적용
function btnApply_OnClick(obj)
{
	// 상단 기초정보
	cbxJibu.Value = ds_ioFPISDanTankInfo.GetColumn(0, "MRGTMGNO");   // 해당지부 세팅
	cbxJibu_OnChanged(object("cbxJibu"), cbxJibu.Value);			 // 지역콤보
	cbxRegion.Value = ds_ioFPISDanTankInfo.GetColumn(0, "MRREGCD");   // 해당지역 세팅

	// 세부직능
	cbxDetailCourse.Value = "81";  // 위험물운송자

    // 대상물 정보
    if(!gfn_IsNull(edFPISBuildingNm.Text)) ds_ioBuildingInfo.SetColumn(0, "BNM", edFPISBuildingNm.Text);

    if(!gfn_IsNull(edFPISAddr1.Text)) {

		// 지번주소/도로명주소 조회 팝업
		var arrAddrInfo = gfn_PostRoadListPopup(cbxJibu.Value		// 지부코드
												, "0"		// 구새주소 구분[0:구주소, 1:새주소]
												, medFPISZipCode.Value			// 구:지번 우편번호
												, edFPISAddr1.Text		// 구:지번주소1
												, edFPISAddr2.Text		// 구:지번주소2
												, ""		// 구:산여부
												, ""	// 구:주번지
												, ""		// 구:부번지
												, ds_ioBuildingInfo.GetColumn(0, "OROADREMAIN")		// 구:아파트,건물명
												, ds_ioBuildingInfo.GetColumn(0, "OROADDETAIL")	// 구:상세주소
												, ""	// 새1:도로명 우편번호
												, ""		// 새1:도로명주소11
												, ""		// 새1:도로명주소12
												, ""	// 새1:건물관리번호1
												, ds_ioBuildingInfo.GetColumn(0, "OROADDETAIL")	// 새1:상세주소1
												, ds_ioBuildingInfo.GetColumn(0, "OROADREMAIN")		// 새1:부가주소1
												, ""	// 새2:도로명 우편번호
												, ""		// 새2:도로명주소21
												, ""		// 새2:도로명주소22
												, ""	// 새2:건물관리번호2
												, ""	// 새2:상세주소2
												, ""	// 새2:부가주소2
												, ""	// 본부
												, "DataConn"	// 프로그램 연동 구분
												); 

		if (length(arrAddrInfo) > 0) {
			if (arrAddrInfo[0] == 0) {
				/* 지번주소 조회 Tab */
				// 구주소
				medZipCode.Value = arrAddrInfo[1];
				edAddr1.Text = arrAddrInfo[2];
				edAddr2.Text = arrAddrInfo[3];

				// 새주소
				if (!gfn_IsNull(arrAddrInfo[10])) {
					medRoadZipCode.Value = arrAddrInfo[10];
				}

				edRoadAddr1.Text = arrAddrInfo[11];
				edRoadAddr2.Text = arrAddrInfo[12];

				ds_ioBuildingInfo.SetColumn(0, "BRPNUCD", arrAddrInfo[9]);		// PNU 코드
				ds_ioBuildingInfo.SetColumn(0, "BRROADBUILDMGNO", arrAddrInfo[13]);	// 건물관리번호
				ds_ioBuildingInfo.SetColumn(0, "BRROADDETAIL", arrAddrInfo[14]);		// 상세주소
				ds_ioBuildingInfo.SetColumn(0, "BRROADREMAIN", arrAddrInfo[15]);		// 부가주소
				ds_ioBuildingInfo.SetColumn(0, "BRSANGUBUN", arrAddrInfo[4]);
				ds_ioBuildingInfo.SetColumn(0, "BRMAINBUNJI", arrAddrInfo[5]);
				ds_ioBuildingInfo.SetColumn(0, "BRSUBBUNJI", arrAddrInfo[6]);
				ds_ioBuildingInfo.SetColumn(0, "BRBUILDNM", arrAddrInfo[7]);
				ds_ioBuildingInfo.SetColumn(0, "BRADDRETC", arrAddrInfo[8]);

				if (!gfn_IsNull(arrAddrInfo[11])) {	// 도로명주소1
					ds_ioBuildingInfo.SetColumn(0, "BRCONVPGM", "1");	// BRCONVPGM
					ds_ioBuildingInfo.SetColumn(0, "BRCONVSTATUS", "01");	// FBRCONVSTATUS
				} else {
					ds_ioBuildingInfo.SetColumn(0, "BRCONVPGM", "0");	// BRCONVPGM
					ds_ioBuildingInfo.SetColumn(0, "BRCONVSTATUS", "00");	// BRCONVSTATUS
				}

				// 주소 조회 후 청구지 반영
				if (arrAddrInfo[11] == "") {
					edAddr2_OnKeyDown(obj);			
				} else {
					cbxAddrGubun.Value = 1;	// 도로명주소
					cbxAddrGubun_OnChanged(obj, cbxAddrGubun.Value);
					edAddr2_OnKeyDown(obj);
				}

			} else {
				/* 도로명주소 조회 Tab */
				medRoadZipCode.Value = arrAddrInfo[10];	// 우편번호
				edRoadAddr1.Text = arrAddrInfo[11];		// 도로명주소1
				edRoadAddr2.Text = arrAddrInfo[12];		// 도로명주소2

				ds_ioBuildingInfo.SetColumn(0, "BRSANGUBUN", arrAddrInfo[4]);		// 산여부
				ds_ioBuildingInfo.SetColumn(0, "BRMAINBUNJI", arrAddrInfo[5]);		// 주번지
				ds_ioBuildingInfo.SetColumn(0, "BRSUBBUNJI", arrAddrInfo[6]);		// 부번지
				ds_ioBuildingInfo.SetColumn(0, "BRPNUCD", arrAddrInfo[9]);			// PNU 코드
				ds_ioBuildingInfo.SetColumn(0, "BRROADBUILDMGNO", arrAddrInfo[13]);	// 건물관리번호
				ds_ioBuildingInfo.SetColumn(0, "BRROADDETAIL", arrAddrInfo[14]);		// 상세주소
				ds_ioBuildingInfo.SetColumn(0, "BRROADREMAIN", arrAddrInfo[15]);		// 부가주소

				if (!gfn_IsNull(arrAddrInfo[11])) {	// 도로명주소1
					ds_ioBuildingInfo.SetColumn(0, "BRCONVPGM", "1");	// BRCONVPGM
					ds_ioBuildingInfo.SetColumn(0, "BRCONVSTATUS", "01");	// FBRCONVSTATUS
				} else {
					ds_ioBuildingInfo.SetColumn(0, "BRCONVPGM", "0");	// BRCONVPGM
					ds_ioBuildingInfo.SetColumn(0, "BRCONVSTATUS", "00");	// BRCONVSTATUS
				}

				// 주소 조회 후 청구지 반영
				cbxAddrGubun.Value = 1;	// 도로명주소
				cbxAddrGubun_OnChanged(obj, cbxAddrGubun.Value);
				edAddr2_OnKeyDown(obj);
			}
		}
	}	
    lfn_CbxPostGubun("0");									// 청구지 주소 반영

	if(gfn_IsNull(edAddr1.Text) && edRoadAddr1.Visible == true) ds_ioBuildingInfo.SetColumn(0, "BADDR1", edFPISAddr1.Text);
    if(gfn_IsNull(edAddr2.Text) && edRoadAddr1.Visible == true) ds_ioBuildingInfo.SetColumn(0, "BADDR2", edFPISAddr2.Text);
    if(!gfn_IsNull(medFPISZipCode.Value) && medZipCode.Visible == true) ds_ioBuildingInfo.SetColumn(0, "BZIPCD", medFPISZipCode.Value);
    if(!gfn_IsNull(EdFPISFireCenter.Text)) ds_ioBuildingInfo.SetColumn(0, "BFIREBONBUCD", ds_ioFPISDanTankInfo.GetColumn(0, "BONBU_ID"));
    if(!gfn_IsNull(EdFPISFireCenter.Text)) ds_ioBuildingInfo.SetColumn(0, "BFIREUPCD", ds_ioFPISDanTankInfo.GetColumn(0, "CMPFRSTT_ID"));
    if(!gfn_IsNull(EdFPISFireCenter.Text)) ds_ioBuildingInfo.SetColumn(0, "BFIRECD", ds_ioFPISDanTankInfo.GetColumn(0, "CMPN119SC_ID"));
    if(!gfn_IsNull(edFPISTel.Text)) ds_ioBuildingInfo.SetColumn(0, "BTEL", edFPISTel.Text);

    // 이동탱크 부가정보
    if(!gfn_IsNull(edFPISCarNo.Text)) ds_ioTankInfo.SetColumn(0, "TCARNO", edFPISCarNo.Text);
    if(!gfn_IsNull(edFPISCarYear.Text)) ds_ioTankInfo.SetColumn(0, "TCARYEAR", edFPISCarYear.Text);
    if(!gfn_IsNull(edFPISPrmisn_No.Text)) ds_ioTankInfo.SetColumn(0, "TPERMISSIONNO", edFPISPrmisn_No.Text);
    if(!gfn_IsNull(edITL_NO.Text)) ds_ioTankInfo.SetColumn(0, "TREMARK", "설치(변경)허가번호 : " + edITL_NO.Text);
    radPostGubun.Value = "0";  // 청구지 구분
    if(!gfn_IsNull(medFPISZipCode.Text)) ds_ioTankInfo.SetColumn(0, "TPARKPLACEZIPCD", medFPISZipCode.Value);
    if(!gfn_IsNull(edFPISAddr1.Text)) ds_ioTankInfo.SetColumn(0, "TPARKPLACEADDR1", edFPISAddr1.Text);
    if(!gfn_IsNull(edFPISAddr2.Text)) ds_ioTankInfo.SetColumn(0, "TPARKPLACEADDR2", edFPISAddr2.Text);
    if(!gfn_IsNull(medFPISINSTLRZipCode.Text)) ds_ioTankInfo.SetColumn(0, "TINSTLRZIPCD", medFPISINSTLRZipCode.Value);
    if(!gfn_IsNull(edFPISINSTLRAddr1.Text)) ds_ioTankInfo.SetColumn(0, "TINSTLRADDR1", edFPISINSTLRAddr1.Text);
    if(!gfn_IsNull(edFPISINSTLRAddr2.Text)) ds_ioTankInfo.SetColumn(0, "TINSTLRADDR2", edFPISINSTLRAddr2.Text);

    lfn_SetEnableStatus(true); // 입력가능 활성화
    lfn_SetCheckEnableStatus(true); // 변경내용 적용 후 체크박스 활성화 상태(활성화) 
    lfn_SetCheckStatus(true);  // 변경내용 적용 후 체크박스 상태(체크)

    btnApply.Enable = False;
}


/*
 * 기능 : 변경내용 적용 전/후 활성화 상태를 지정한다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetEnableStatus(bStatus) {
    /* 조회 정보 */
    cbxJibu.Enable = bStatus;
    cbxRegion.Enable = bStatus;
    //cbxCourse.Enable = bStatus;
    cbxDetailCourse.Enable = bStatus;

	/* 대상물 정보 */
    edBNM.Enable = bStatus;
    edAddr1.Enable = bStatus;
    edAddr2.Enable = bStatus;
    edRoadAddr1.Enable = bStatus;
    edRoadAddr2.Enable = bStatus;
    btnSearchZipCode1.Enable = bStatus;    
    cbxBonbu.Enable = bStatus;
    cbxStation.Enable = bStatus;
    cbxCenter.Enable = bStatus;
    edTel.Enable = bStatus;

    /* 이동탱크정보 */
    edCarNo.Enable = bStatus;
    edCarYear.Enable = bStatus;
    edPerMissionNo.Enable = bStatus;
    edRemark.Enable = bStatus;
    radPostGubun.Enable = bStatus;
    edParkAddr1.Enable = bStatus;
    edParkAddr2.Enable = bStatus;
    medParkZipCode.Enable = bStatus;
    edParkRoadAddr1.Enable = bStatus;
    edParkRoadAddr2.Enable = bStatus;
    medParkRoadZipCode.Enable = bStatus;
    btnSearchParkZipCode.Enable = bStatus;
    edInstlrAddr1.Enable = bStatus;
    edInstlrAddr2.Enable = bStatus;
    medInstlrZipCode.Enable = bStatus;
    edInstlrRoadAddr1.Enable = bStatus;
    edInstlrRoadAddr2.Enable = bStatus;
    medInstlrRoadZipCode.Enable = bStatus;
    btnSearchInstlrZipCode.Enable = bStatus;

    btnSave.Enable = bStatus;
}

// 대표직능 변경
function cbxCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{	
	lfn_AddressHidden(true);			//대표주소 입력 히든처리
	lfn_FilterDetailCourse(strCode);		// 세부고객직능 필터링
}

// 대표주소 입력 히든처리 (위험물운송자)
function lfn_AddressHidden(bStatus) {
	// 히든처리
	// 대상물 주소
    chkAddr.Visible  		 = bStatus;
    edAddr1.Visible          = bStatus;
    medZipCode.Visible       = bStatus;
    btnSearchZipCode1.Visible   = bStatus;
    edAddr2.Visible          = bStatus;


	// 데이터 초기화
	if( gfn_IsNull(ds_ioBuildingInfo.GetColumn(0, "BMGNO")) ) {
		if(bStatus == false) {
			// 대상물 주소
            edAddr1.Text       = "";
            medZipCode.Value   = "";
            edAddr2.Text       = "";
		}
	}
}

// 대상물주소
function btnSearchZipCode1_OnClick(obj)
{
	var sAddrGubun = 0;

	if (cbxAddrGubun.Index == 0) {	// 구주소
		sAddrGubun = 0;
	} else {
		sAddrGubun = 1;
	}

	// 지번주소/도로명주소 조회 팝업
	var arrAddrInfo = gfn_PostRoadListPopup(cbxJibu.Value		// 지부코드
											, sAddrGubun		// 구새주소 구분[0:구주소, 1:새주소]
											, medZipCode.Value			// 구:지번 우편번호
											, edAddr1.Text		// 구:지번주소1
											, edAddr2.Text		// 구:지번주소2
											, ds_ioBuildingInfo.GetColumn(0, "BRSANGUBUN")		// 구:산여부
											, ds_ioBuildingInfo.GetColumn(0, "BRMAINBUNJI")	// 구:주번지
											, ds_ioBuildingInfo.GetColumn(0, "BRSUBBUNJI")		// 구:부번지
											, ds_ioBuildingInfo.GetColumn(0, "BRBUILDNM")		// 구:아파트,건물명
											, ds_ioBuildingInfo.GetColumn(0, "BRADDRETC")	// 구:상세주소
											, medRoadZipCode.Value	// 새1:도로명 우편번호
											, edRoadAddr1.Text		// 새1:도로명주소11
											, edRoadAddr2.Text		// 새1:도로명주소12
											, ds_ioBuildingInfo.GetColumn(0, "BRROADBUILDMGNO")	// 새1:건물관리번호1
											, ds_ioBuildingInfo.GetColumn(0, "BRROADDETAIL")	// 새1:상세주소1
											, ds_ioBuildingInfo.GetColumn(0, "BRROADREMAIN")		// 새1:부가주소1
											, medRoadZipCode.Value	// 새2:도로명 우편번호
											, edRoadAddr1.Text		// 새2:도로명주소21
											, edRoadAddr2.Text		// 새2:도로명주소22
											, ds_ioBuildingInfo.GetColumn(0, "BRROADBUILDMGNO")	// 새2:건물관리번호2
											, ds_ioBuildingInfo.GetColumn(0, "BRROADDETAIL")	// 새2:상세주소2
											, ds_ioBuildingInfo.GetColumn(0, "BRROADREMAIN")	// 새2:부가주소2
											); 

    if (length(arrAddrInfo) > 0) {

		if (arrAddrInfo[0] == 0) {

			/* 지번주소 조회 Tab */
			// 구주소
			medZipCode.Value = arrAddrInfo[1];

			edAddr1.Text = arrAddrInfo[2];

			edAddr2.Text = arrAddrInfo[3];

			// 새주소

			if (!gfn_IsNull(arrAddrInfo[10])) {

				medRoadZipCode.Value = arrAddrInfo[10];

			}

			edRoadAddr1.Text = arrAddrInfo[11];

			edRoadAddr2.Text = arrAddrInfo[12];

			ds_ioBuildingInfo.SetColumn(0, "BRPNUCD", arrAddrInfo[9]);		// PNU 코드

			ds_ioBuildingInfo.SetColumn(0, "BRROADBUILDMGNO", arrAddrInfo[13]);	// 건물관리번호

			ds_ioBuildingInfo.SetColumn(0, "BRROADDETAIL", arrAddrInfo[14]);		// 상세주소

			ds_ioBuildingInfo.SetColumn(0, "BRROADREMAIN", arrAddrInfo[15]);		// 부가주소

			ds_ioBuildingInfo.SetColumn(0, "BRSANGUBUN", arrAddrInfo[4]);

			ds_ioBuildingInfo.SetColumn(0, "BRMAINBUNJI", arrAddrInfo[5]);

			ds_ioBuildingInfo.SetColumn(0, "BRSUBBUNJI", arrAddrInfo[6]);

			ds_ioBuildingInfo.SetColumn(0, "BRBUILDNM", arrAddrInfo[7]);

			ds_ioBuildingInfo.SetColumn(0, "BRADDRETC", arrAddrInfo[8]);

			if (!gfn_IsNull(arrAddrInfo[11])) {	// 도로명주소1

				ds_ioBuildingInfo.SetColumn(0, "BRCONVPGM", "1");	// BRCONVPGM

				ds_ioBuildingInfo.SetColumn(0, "BRCONVSTATUS", "01");	// FBRCONVSTATUS

			} else {

				ds_ioBuildingInfo.SetColumn(0, "BRCONVPGM", "0");	// BRCONVPGM

				ds_ioBuildingInfo.SetColumn(0, "BRCONVSTATUS", "00");	// BRCONVSTATUS

			}

			// 주소 조회 후 청구지 반영
			if (arrAddrInfo[11] == "") {

				edAddr2_OnKeyDown(obj);			

			} else {

				cbxAddrGubun.Value = 1;	// 도로명주소

				cbxAddrGubun_OnChanged(obj, cbxAddrGubun.Value);

				edAddr2_OnKeyDown(obj);
			}


		} else {

			/* 도로명주소 조회 Tab */
			medRoadZipCode.Value = arrAddrInfo[10];	// 우편번호

			edRoadAddr1.Text = arrAddrInfo[11];		// 도로명주소1

			edRoadAddr2.Text = arrAddrInfo[12];		// 도로명주소2

			ds_ioBuildingInfo.SetColumn(0, "BRSANGUBUN", arrAddrInfo[4]);		// 산여부

			ds_ioBuildingInfo.SetColumn(0, "BRMAINBUNJI", arrAddrInfo[5]);		// 주번지

			ds_ioBuildingInfo.SetColumn(0, "BRSUBBUNJI", arrAddrInfo[6]);		// 부번지

			ds_ioBuildingInfo.SetColumn(0, "BRPNUCD", arrAddrInfo[9]);			// PNU 코드

			ds_ioBuildingInfo.SetColumn(0, "BRROADBUILDMGNO", arrAddrInfo[13]);	// 건물관리번호

			ds_ioBuildingInfo.SetColumn(0, "BRROADDETAIL", arrAddrInfo[14]);		// 상세주소

			ds_ioBuildingInfo.SetColumn(0, "BRROADREMAIN", arrAddrInfo[15]);		// 부가주소

			if (!gfn_IsNull(arrAddrInfo[11])) {	// 도로명주소1

				ds_ioBuildingInfo.SetColumn(0, "BRCONVPGM", "1");	// BRCONVPGM

				ds_ioBuildingInfo.SetColumn(0, "BRCONVSTATUS", "01");	// FBRCONVSTATUS

			} else {

				ds_ioBuildingInfo.SetColumn(0, "BRCONVPGM", "0");	// BRCONVPGM

				ds_ioBuildingInfo.SetColumn(0, "BRCONVSTATUS", "00");	// BRCONVSTATUS

			}

			// 주소 조회 후 청구지 반영
			cbxAddrGubun.Value = 1;	// 도로명주소
			cbxAddrGubun_OnChanged(obj, cbxAddrGubun.Value);
			edAddr2_OnKeyDown(obj);
			lfn_CbxPostGubun("0");

		}

    }
}
// 대상물주소
function btnSearchZipCode1_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if(nChar == 13) {	// Enter key
		btnSearchZipCode1_OnClick();
	} else {
		gfn_ComponentOnKillFocus(obj);
	}
}
// 대상물 주소1
function edNewAddr1_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
        btnSearchZipCode1.SetFocus();
        btnSearchZipCode1_OnClick();
/*        var arrZipCode = gfn_ZipCodeListPopup(edNewAddr1.Text); // 우편번호 조회 팝업
        if (length(arrZipCode) > 0) {
            medZipCode.Value = arrZipCode[0];
            edAddr1.Text = arrZipCode[1];
            edAddr2.SetFocus();
        } else {
            edAddr1.SetFocus();
        }
*/
	} else {
		gfn_EnterNextFocus(obj);
	}
}


// 우편번호(상치장소)
function medParkZipCode_OnFocus(obj)
{
    btnSearchParkZipCode.SetFocus();
}

// 우편번호(상치장소)
function btnSearchParkZipCode_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if(nChar == 13) {	// Enter key
		btnSearchParkZipCode_OnClick();
	} else {
		gfn_ComponentOnKillFocus(obj);
	}
}

// 우편번호(상치장소)
function btnSearchParkZipCode_OnClick(obj)
{

	var sAddrGubun = 0;

	if (cbxHopeParkAddrGubun.Index == 0) {	// 구주소
		sAddrGubun = 0;
	} else {
		sAddrGubun = 1;
	}

	// 지번주소/도로명주소 조회 팝업
	var arrAddrInfo = gfn_PostRoadListPopup(cbxJibu.Value		// 지부코드
											, sAddrGubun		// 구새주소 구분[0:구주소, 1:새주소]
											, medParkZipCode.Value			// 구:지번 우편번호
											, edParkAddr1.Text		// 구:지번주소1
											, edParkAddr2.Text		// 구:지번주소2
											, ds_ioTankInfo.GetColumn(0, "TRPARKSANGUBUN")		// 구:산여부
											, ds_ioTankInfo.GetColumn(0, "TRPARKMAINBUNJI")	// 구:주번지
											, ds_ioTankInfo.GetColumn(0, "TRPARKSUBBUNJI")		// 구:부번지
											, ds_ioTankInfo.GetColumn(0, "TRPARKBUILDNM")		// 구:아파트,건물명
											, ds_ioTankInfo.GetColumn(0, "TRPARKADDRETC")	// 구:상세주소
											, medParkRoadZipCode.Value	// 새1:도로명 우편번호
											, edParkRoadAddr1.Text		// 새1:도로명주소11
											, edParkRoadAddr2.Text		// 새1:도로명주소12
											, ds_ioTankInfo.GetColumn(0, "TRPARKROADBUILDMGNO")	// 새1:건물관리번호1
											, ds_ioTankInfo.GetColumn(0, "TRPARKROADDETAIL")	// 새1:상세주소1
											, ds_ioTankInfo.GetColumn(0, "TRPARKROADREMAIN")		// 새1:부가주소1
											, medParkRoadZipCode.Value	// 새2:도로명 우편번호
											, edParkRoadAddr1.Text		// 새2:도로명주소21
											, edParkRoadAddr2.Text		// 새2:도로명주소22
											, ds_ioTankInfo.GetColumn(0, "TRPARKROADBUILDMGNO")	// 새2:건물관리번호2
											, ds_ioTankInfo.GetColumn(0, "TRPARKROADDETAIL")	// 새2:상세주소2
											, ds_ioTankInfo.GetColumn(0, "TRPARKROADREMAIN")	// 새2:부가주소2
											); 

    if (length(arrAddrInfo) > 0) {

		if (arrAddrInfo[0] == 0) {

			/* 지번주소 조회 Tab */
			// 구주소
			medParkZipCode.Value = arrAddrInfo[1];

			edParkAddr1.Text = arrAddrInfo[2];

			edParkAddr2.Text = arrAddrInfo[3];

			// 새주소

			if (!gfn_IsNull(arrAddrInfo[10])) {

				medParkRoadZipCode.Value = arrAddrInfo[10];

			}

			edParkRoadAddr1.Text = arrAddrInfo[11];

			edParkRoadAddr2.Text = arrAddrInfo[12];

			ds_ioTankInfo.SetColumn(0, "TRPARKPNUCD", arrAddrInfo[9]);		// PNU 코드

			ds_ioTankInfo.SetColumn(0, "TRPARKROADBUILDMGNO", arrAddrInfo[13]);	// 건물관리번호

			ds_ioTankInfo.SetColumn(0, "TRPARKROADDETAIL", arrAddrInfo[14]);		// 상세주소

			ds_ioTankInfo.SetColumn(0, "TRPARKROADREMAIN", arrAddrInfo[15]);		// 부가주소

			ds_ioTankInfo.SetColumn(0, "TRPARKSANGUBUN", arrAddrInfo[4]);

			ds_ioTankInfo.SetColumn(0, "TRPARKMAINBUNJI", arrAddrInfo[5]);

			ds_ioTankInfo.SetColumn(0, "TRPARKSUBBUNJI", arrAddrInfo[6]);

			ds_ioTankInfo.SetColumn(0, "TRPARKBUILDNM", arrAddrInfo[7]);

			ds_ioTankInfo.SetColumn(0, "TRPARKADDRETC", arrAddrInfo[8]);

			if (!gfn_IsNull(arrAddrInfo[11])) {	// 도로명주소1

				ds_ioTankInfo.SetColumn(0, "TRCONVPGM", "1");	// BRCONVPGM

				ds_ioTankInfo.SetColumn(0, "TRCONVPARKSTATUS", "01");	// FBRCONVSTATUS

			} else {

				ds_ioTankInfo.SetColumn(0, "TRCONVPGM", "0");	// BRCONVPGM

				ds_ioTankInfo.SetColumn(0, "TRCONVPARKSTATUS", "00");	// BRCONVSTATUS

			}


		} else {

			/* 도로명주소 조회 Tab */
			medParkRoadZipCode.Value = arrAddrInfo[10];	// 우편번호

			edParkRoadAddr1.Text = arrAddrInfo[11];		// 도로명주소1

			edParkRoadAddr2.Text = arrAddrInfo[12];		// 도로명주소2

			ds_ioTankInfo.SetColumn(0, "TRPARKSANGUBUN", arrAddrInfo[4]);		// 산여부

			ds_ioTankInfo.SetColumn(0, "TRPARKMAINBUNJI", arrAddrInfo[5]);		// 주번지

			ds_ioTankInfo.SetColumn(0, "TRPARKSUBBUNJI", arrAddrInfo[6]);		// 부번지

			ds_ioTankInfo.SetColumn(0, "TRPARKPNUCD", arrAddrInfo[9]);			// PNU 코드

			ds_ioTankInfo.SetColumn(0, "TRPARKROADBUILDMGNO", arrAddrInfo[13]);	// 건물관리번호

			ds_ioTankInfo.SetColumn(0, "TRPARKROADDETAIL", arrAddrInfo[14]);		// 상세주소

			ds_ioTankInfo.SetColumn(0, "TRPARKROADREMAIN", arrAddrInfo[15]);		// 부가주소

			if (!gfn_IsNull(arrAddrInfo[11])) {	// 도로명주소1

				ds_ioTankInfo.SetColumn(0, "TRCONVPGM", "1");	// BRCONVPGM

				ds_ioTankInfo.SetColumn(0, "TRCONVPARKSTATUS", "01");	// FBRCONVSTATUS

			} else {

				ds_ioTankInfo.SetColumn(0, "TRCONVPGM", "0");	// BRCONVPGM

				ds_ioTankInfo.SetColumn(0, "TRCONVPARKSTATUS", "00");	// BRCONVSTATUS

			}

		}

    }

}

