// 고객변경이력생성
function lfn_CustomerModifyHistoryGenerator(sCmgno) {

	if(gfn_IsNull(sCmgno)) {
		gfn_Alert("고객정보 변경이력 처리에 실패하였습니다.");
		//return;
	}

	ds_iCustomerModifyHistory.ClearData();

	//대상물정보
	if( ds_ioBuildingInfo.GetRowType(0) == "insert" ) {	//대상물정보 신규

	} else if( ds_ioBuildingInfo.GetRowType(0) == "update" ) {	//대상물정보 수정

		for(var i=0; i<ds_ioBuildingInfo.GetColCount(); i++) {

			if( ds_ioBuildingInfo.GetOrgColumn(0, ds_ioBuildingInfo.GetColID(i))
				<> ds_ioBuildingInfo.GetColumn(0, ds_ioBuildingInfo.GetColID(i)) ) {	//컬럼값 변경

				var nRow = ds_iCustomerModifyHistory.AddRow();

				ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "U");
				ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "대상물정보");
				ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
				ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioBuildingInfo.GetColID(i)) , "COLKORNM"));

				var lsComboYn = "N";
				var lsInnerDsNm = "";
				if( ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioBuildingInfo.GetColID(i)) , "COMBOGUBUN" ) == "Y" ) {	//Combo Component Check
					lsComboYn = "Y";
					lsInnerDsNm = ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioBuildingInfo.GetColID(i)) , "INNERDSNM" );	//Inner Dataset Name
				}

				if(lsComboYn == "Y") {	//Combo Component 인 경우
					//BEFOREDATA
					if(!gfn_IsNull((ds_ioBuildingInfo.GetOrgColumn(0, ds_ioBuildingInfo.GetColID(i))))) {
						var lsOrgCd = ds_ioBuildingInfo.GetOrgColumn(0, ds_ioBuildingInfo.GetColID(i));
						if(ds_ioBuildingInfo.GetColID(i) == "BFIREUPCD" || ds_ioBuildingInfo.GetColID(i) == "BFIRECD") {	//소방관서 (필터링된 데이터셋까지 처리)
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", Object(lsInnerDsNm).GetColumnNF(Object(lsInnerDsNm).FindRowNF("CD", lsOrgCd), "DATA"));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsOrgCd), "DATA"));
						}
					} else {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
					}
					//AFTERDATA
					if(!gfn_IsNull((ds_ioBuildingInfo.GetColumn(0, ds_ioBuildingInfo.GetColID(i))))) {
						var lsNewCd = ds_ioBuildingInfo.GetColumn(0, ds_ioBuildingInfo.GetColID(i));
						if(ds_ioBuildingInfo.GetColID(i) == "BFIREUPCD" || ds_ioBuildingInfo.GetColID(i) == "BFIRECD") {	//소방관서 (필터링된 데이터셋까지 처리)
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", Object(lsInnerDsNm).GetColumnNF(Object(lsInnerDsNm).FindRowNF("CD", lsNewCd), "DATA"));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsNewCd), "DATA"));
						}
					} else {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
					}
				} else {	//Combo Component 가 아닌경우
					//BEFOREDATA
					if(!gfn_IsNull((ds_ioBuildingInfo.GetOrgColumn(0, ds_ioBuildingInfo.GetColID(i))))) {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", ds_ioBuildingInfo.GetOrgColumn(0, ds_ioBuildingInfo.GetColID(i)));
					} else {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
					}
					//AFTERDATA
					if(!gfn_IsNull((ds_ioBuildingInfo.GetColumn(0, ds_ioBuildingInfo.GetColID(i))))) {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", ds_ioBuildingInfo.GetColumn(0, ds_ioBuildingInfo.GetColID(i)));
					} else {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
					}
				}
			}
		}
	}

	//고객정보
	if( ds_ioCustomerInfo.GetRowType(0) == "insert" ) {	//고객정보 신규

	} else if( ds_ioCustomerInfo.GetRowType(0) == "update" ) {	//고객정보 수정

		for(var i=0; i<ds_ioCustomerInfo.GetColCount(); i++) {

			if( ds_ioCustomerInfo.GetColID(i) == "CREGCD" ) {	//지역변경
				if( ds_ioCustomerInfo.GetOrgColumn(0, "CREGCD") <> cbxRegion.Value ) {	
					var nRow = ds_iCustomerModifyHistory.AddRow();

					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "U");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "고객정보");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", "CREGCD") , "COLKORNM"));
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", ds_oRegion.GetColumn( ds_oRegion.FindRow("CD", ds_ioCustomerInfo.GetOrgColumn(0, "CREGCD")), "DATA" ) );
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", cbxRegion.Text);
				}
			} else if( ds_ioCustomerInfo.GetColID(i) == "CCOURSECD" ) {	//직능변경
				if( ds_ioCustomerInfo.GetOrgColumn(0, "CCOURSECD") <> cbxDetailCourse.Value ) {	
					var nRow = ds_iCustomerModifyHistory.AddRow();

					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "U");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "고객정보");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", "CCOURSECD") , "COLKORNM"));
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", ds_oDetailCourse.GetColumn( ds_oDetailCourse.FindRow("CD", ds_ioCustomerInfo.GetOrgColumn(0, "CCOURSECD")), "DATA" ) );
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", cbxDetailCourse.Text);
				}

			} else if ( ds_ioCustomerInfo.GetColID(i) == "CGCOURSECD" 
					|| ds_ioCustomerInfo.GetColID(i) == "AGNM" ) {	//제외 대상

				//대표직능 : CUSTOMER TABLE에 물리적으로 미존재
				//대행업체명 : CUSTOMER TABLE에 물리적으로 미존재

			} else {	//지역, 직능을 제외한 변경
				if( ds_ioCustomerInfo.GetOrgColumn(0, ds_ioCustomerInfo.GetColID(i))
					<> ds_ioCustomerInfo.GetColumn(0, ds_ioCustomerInfo.GetColID(i)) ) {	//컬럼값 변경

					var nRow = ds_iCustomerModifyHistory.AddRow();

					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "U");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "고객정보");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioCustomerInfo.GetColID(i)) , "COLKORNM"));

					var lsComboYn = "N";
					var lsInnerDsNm = "";
					if( ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioCustomerInfo.GetColID(i)) , "COMBOGUBUN" ) == "Y" ) {	//Combo Component Check
						lsComboYn = "Y";
						lsInnerDsNm = ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioCustomerInfo.GetColID(i)) , "INNERDSNM" );	//Inner Dataset Name
					}

					if(lsComboYn == "Y") {	//Combo Component 인 경우
						//BEFOREDATA
						if(!gfn_IsNull((ds_ioCustomerInfo.GetOrgColumn(0, ds_ioCustomerInfo.GetColID(i))))) {
							var lsOrgCd = ds_ioCustomerInfo.GetOrgColumn(0, ds_ioCustomerInfo.GetColID(i));
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsOrgCd), "DATA"));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
						}
						//AFTERDATA
						if(!gfn_IsNull((ds_ioCustomerInfo.GetColumn(0, ds_ioCustomerInfo.GetColID(i))))) {
							var lsNewCd = ds_ioCustomerInfo.GetColumn(0, ds_ioCustomerInfo.GetColID(i));
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsNewCd), "DATA"));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
						}
					} else {	//Combo Component 가 아닌경우
						//BEFOREDATA
						if(!gfn_IsNull((ds_ioCustomerInfo.GetOrgColumn(0, ds_ioCustomerInfo.GetColID(i))))) {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", ds_ioCustomerInfo.GetOrgColumn(0, ds_ioCustomerInfo.GetColID(i)));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
						}
						//AFTERDATA
						if(!gfn_IsNull((ds_ioCustomerInfo.GetColumn(0, ds_ioCustomerInfo.GetColID(i))))) {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", ds_ioCustomerInfo.GetColumn(0, ds_ioCustomerInfo.GetColID(i)));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
						}
					}
				}
			}
		}
	}

	//이동탱크부가정보
	for( var j=0; j<ds_ioTankInfo.GetRowCount(); j++) {
		if( ds_ioTankInfo.GetRowType(j) == "insert" ) {	//이동탱크부가정보 신규

			var nRow = ds_iCustomerModifyHistory.AddRow();
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "I");
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "이동탱크부가정보");
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", "신규등록");
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", ds_ioTankInfo.GetColumn(j, "TCARNO"));

		} else if( ds_ioTankInfo.GetRowType(j) == "update" ) {	//이동탱크부가정보 수정

			for(var i=0; i<ds_ioTankInfo.GetColCount(); i++) {
				if( ds_ioTankInfo.GetOrgColumn(j, ds_ioTankInfo.GetColID(i))
					<> ds_ioTankInfo.GetColumn(j, ds_ioTankInfo.GetColID(i)) ) {	//컬럼값 변경

					var nRow = ds_iCustomerModifyHistory.AddRow();

					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "U");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "이동탱크부가정보");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioTankInfo.GetColID(i)) , "COLKORNM"));

					var lsComboYn = "N";
					var lsInnerDsNm = "";
					if( ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioTankInfo.GetColID(i)) , "COMBOGUBUN" ) == "Y" ) {	//Combo Component Check
						lsComboYn = "Y";
						lsInnerDsNm = ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioTankInfo.GetColID(i)) , "INNERDSNM" );	//Inner Dataset Name
					}

					if(lsComboYn == "Y") {	//Combo Component 인 경우
						//BEFOREDATA
						if(!gfn_IsNull((ds_ioTankInfo.GetOrgColumn(j, ds_ioTankInfo.GetColID(i))))) {
							var lsOrgCd = ds_ioTankInfo.GetOrgColumn(j, ds_ioTankInfo.GetColID(i));
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsOrgCd), "DATA"));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
						}
						//AFTERDATA
						if(!gfn_IsNull((ds_ioTankInfo.GetColumn(j, ds_ioTankInfo.GetColID(i))))) {
							var lsNewCd = ds_ioTankInfo.GetColumn(j, ds_ioTankInfo.GetColID(i));
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsNewCd), "DATA"));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
						}
					} else {	//Combo Component 가 아닌경우
						//BEFOREDATA
						if(!gfn_IsNull((ds_ioTankInfo.GetOrgColumn(j, ds_ioTankInfo.GetColID(i))))) {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", ds_ioTankInfo.GetOrgColumn(j, ds_ioTankInfo.GetColID(i)));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
						}
						//AFTERDATA
						if(!gfn_IsNull((ds_ioTankInfo.GetColumn(j, ds_ioTankInfo.GetColID(i))))) {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", ds_ioTankInfo.GetColumn(j, ds_ioTankInfo.GetColID(i)));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
						}
					}
				}
			}
		}
	}
}


// 대상물 구/새주소구분 변경시
function cbxAddrGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 0) {	// 지번주소
		medZipCode.Visible = true;
		edAddr1.Visible = true;
		edAddr2.Visible = true;

		medRoadZipCode.Visible = false;
		edRoadAddr1.Visible = false;
		edRoadAddr2.Visible = false;

	} else {			// 도로명주소
		medRoadZipCode.Visible = true;
		edRoadAddr1.Visible = true;
		edRoadAddr2.Visible = true;

		medZipCode.Visible = false;
		edAddr1.Visible = false;
		edAddr2.Visible = false;

	}	
}

// 상치장소 구/새주소구분 변경시
function cbxHopeParkAddrGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 0) {	// 지번주소

		medParkZipCode.Visible = true;
		edParkAddr1.Visible = true;
		edParkAddr2.Visible = true;

		medParkRoadZipCode.Visible = false;
		edParkRoadAddr1.Visible = false;
		edParkRoadAddr2.Visible = false;

	} else {			// 도로명주소

		medParkRoadZipCode.Visible = true;
		edParkRoadAddr1.Visible = true;
		edParkRoadAddr2.Visible = true;

		medParkZipCode.Visible = false;
		edParkAddr1.Visible = false;
		edParkAddr2.Visible = false;
	}	
}

// 설치자주소 구/새주소구분 변경시
function cbxHopeInstlrAddrGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 0) {	// 지번주소

		medInstlrZipCode.Visible = true;
		edInstlrAddr1.Visible = true;
		edInstlrAddr2.Visible = true;

		medInstlrRoadZipCode.Visible = false;
		edInstlrRoadAddr1.Visible = false;
		edInstlrRoadAddr2.Visible = false;

	} else {			// 도로명주소

		medInstlrRoadZipCode.Visible = true;
		edInstlrRoadAddr1.Visible = true;
		edInstlrRoadAddr2.Visible = true;

		medInstlrZipCode.Visible = false;
		edInstlrAddr1.Visible = false;
		edInstlrAddr2.Visible = false;
	}	
}

// 지번주소를 도로명주소로 변환
function lfn_RoadConversion(sADDR1, sADDR2, sZIPCD) {

	var lsOutDsListNm = "";
	var lsArgListNm = "";

	ds_iRoadConv.ClearData();   // 조회 입력 데이터셋
	ds_oRoadConv.ClearData(); 	// 도로명정보 출력 데이터셋

    tit_clearActionInfo();

	tit_addSearchActionInfo("sys:searchRoadConversion", false);     // 자격증 정보

	lsOutDsListNm += " ds_oRoadConv=ds_oRoadConv";
	lsArgListNm += "ADDR1=" + quote(sADDR1); // 지번주소1
	lsArgListNm += " ADDR2=" + quote(sADDR2); // 지번구주소2
	lsArgListNm += " ZIPCD=" + quote(sZIPCD); // 우편번호


    // 서버 호출
    tit_callService(
        ""
        , ""
        , "" // inDs
        , lsOutDsListNm // outDs
        , lsArgListNm	// args
        , "lfn_AfterRoadConversion"
        ,false
        ,false
        ,true
    );
}

function lfn_AfterRoadConversion(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    if (ds_oRoadConv.GetCount() < 0) {
        ds_oRoadConv.ClearData();
    }
} 

function edAddr2_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {

		cbxBonbu.SetFocus();

		lfn_CbxPostGubun("0");

	}
}

function edRoadAddr2_OnChanged(obj,strText)
{
	if (nChar == 13) {

		cbxBonbu.SetFocus();

		if(cbxPostGubun2.Value == 0) { 	//대상물

			lfn_CbxPostGubun(cbxPostGubun2.Value);

		}

	}	
}


// 청구지 주소 선택
function lfn_CbxPostGubun(strCode) {
	switch(strCode)
	{
		case 0 :	//대상물

			if(cbxAddrGubun.Value == 0) {	//구주소

				cbxHopeParkAddrGubun.Value = 0;	// 청구지 구/새주소 구분				
				cbxHopeParkAddrGubun_OnChanged(this, cbxHopeParkAddrGubun.Value);

				edParkAddr1.Text = edAddr1.Text; //주소1
				edParkAddr2.Text = edAddr2.Text;	//주소2
				medParkZipCode.Value = medZipCode.Value;	//우편번호

				edParkAddr1.Enable = false;
				edParkAddr2.Enable = false;
				btnSearchParkZipCode.Enable = false;

				if (btnApply.Enable == false) {
					if(gfn_IsNull(edAddr1.Text) || gfn_IsNull(edAddr2.Text) || gfn_IsNull(medZipCode.Value)) {	//원본 주소가 Null일때
						gfn_Alert("대상물 주소에 빈값이 존재합니다.\n먼저 대상물의 주소를 입력하세요.");
						edOldAddr1.SetFocus();	//대상물의 주소로 이동.
						return;
					}
				}

			} else if(cbxAddrGubun.Value == 1) {	//새주소

				cbxHopeParkAddrGubun.Value = 1;	// 청구지 구/새주소 구분				
				cbxHopeParkAddrGubun_OnChanged(this, cbxHopeParkAddrGubun.Value);


				edParkRoadAddr1.Text = edRoadAddr1.Text; //주소1
				edParkRoadAddr2.Text = edRoadAddr2.Text;	//주소2
				medParkRoadZipCode.Value = medRoadZipCode.Value;	//우편번호

				edParkRoadAddr1.Enable = false;
				edParkRoadAddr2.Enable = false;
				btnSearchParkZipCode.Enable = false;

				// 새주소 CUSTOMERROAD 테이블 적용

				// 회비
				ds_ioTankInfo.SetColumn(0, "TRPARKPNUCD", ds_ioBuildingInfo.GetColumn(0, "BRPNUCD"));

				ds_ioTankInfo.SetColumn(0, "TRPARKROADBUILDMGNO", ds_ioBuildingInfo.GetColumn(0, "BRROADBUILDMGNO"));

				ds_ioTankInfo.SetColumn(0, "TRPARKROADDETAIL", ds_ioBuildingInfo.GetColumn(0, "BRROADDETAIL"));

				ds_ioTankInfo.SetColumn(0, "TRPARKROADREMAIN", ds_ioBuildingInfo.GetColumn(0, "BRROADREMAIN"));

				ds_ioTankInfo.SetColumn(0, "TRPARKSANGUBUN", ds_ioBuildingInfo.GetColumn(0, "BRSANGUBUN"));

				ds_ioTankInfo.SetColumn(0, "TRPARKMAINBUNJI", ds_ioBuildingInfo.GetColumn(0, "BRMAINBUNJI"));

				ds_ioTankInfo.SetColumn(0, "TRPARKSUBBUNJI", ds_ioBuildingInfo.GetColumn(0, "BRSUBBUNJI"));

				ds_ioTankInfo.SetColumn(0, "TRPARKBUILDNM", ds_ioBuildingInfo.GetColumn(0, "BRBUILDNM"));

				ds_ioTankInfo.SetColumn(0, "TRPARKADDRETC", ds_ioBuildingInfo.GetColumn(0, "BRADDRETC"));

				ds_ioTankInfo.SetColumn(0, "TRCONVPGM", ds_ioBuildingInfo.GetColumn(0, "BRCONVPGM"));

				ds_ioTankInfo.SetColumn(0, "TRCONVPARKSTATUS", ds_ioBuildingInfo.GetColumn(0, "BRCONVSTATUS"));

				if (btnApply.Enable == false) {
					if(gfn_IsNull(edRoadAddr1.Text) || gfn_IsNull(medRoadZipCode.Value)) {	//원본 주소가 Null일때
						gfn_Alert("대상물 도로명주소에 빈값이 존재합니다.\n먼저 대상물의 도로명주소를 입력하세요.");
						edRoadAddr1.SetFocus();	//대상물의 주소로 이동.
						return;
					}
				}			
			}
		break;

		case 2 :	//직접
			if(cbxAddrGubun.Value == 0) {	//구주소
				cbxHopeParkAddrGubun.Value = 0;	// 청구지 구/새주소 구분				
				cbxHopeParkAddrGubun_OnChanged(this, cbxHopeParkAddrGubun.Value);

				edParkAddr1.Text = "";	//주소1
				edParkAddr2.Text = "";	//주소2
				medParkZipCode.Value = "";	//우편번호

				edParkAddr1.Enable = false;
				edParkAddr2.Enable = false;
				btnSearchParkZipCode.Enable = true;

				edParkAddr1.SetFocus();
			} else if(cbxAddrGubun.Value == 1) {	//새주소
				cbxHopeParkAddrGubun.Value = 1;	// 청구지 구/새주소 구분				
				cbxHopeParkAddrGubun_OnChanged(this, cbxHopeParkAddrGubun.Value);

				edParkRoadAddr1.Text = ""; //주소1
				edParkRoadAddr2.Text = "";	//주소2
				medParkRoadZipCode.Value = "";	//우편번호

				edParkRoadAddr1.Enable = false;
				edParkRoadAddr2.Enable = false;
				btnSearchZipCode2.Enable = true;

				edParkRoadAddr1.SetFocus();
			}
		break;
	}
}
