#include "LIB::profes.js"; // 공통업무 관련

var sFirst = "";  // 권한에 따른 그리드 크기 변경하기 

function Form_OnLoadCompleted(obj)
{
	gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리	
    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.
    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true, 244); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false, 320); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    G_AUTH              = gds_oUserInfo.GetColumn(0, "ADMINGUBUN");  ////// 권한

    cbxEduGubun2.Enable = false;
    cbxEduGubun2.Value = "B";        
    edCourseCode.Enable = false;
    edEduCourseNM.Enable = false;
    cbxMunjeGubun.Enable = false;
    edEduTime.Enable = false;
    calEduDate.Enable = false;
    edRemark.Enable = false;
    cbxEduGubun.Index = 0; 


    if (G_AUTH == "SM" || G_AUTH == "PM" ) {    //////// 권한
        btnInput.Enable      = true;
        btnSave.Enable = true;
      //  btnSave_2.Enable = true;
        btnDelete.Enable = true;

    } else {
        btnInput.Enable = false;
        btnSave.Enable = false;
      //  btnSave_2.Enable = false;
        btnDelete.Enable = false;       
    }
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do
    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        grdEduCourse.Height = nCy - grdEduCourse.Top - 4;
        grdEduCourse.Width = nCx - grdEduCourse.Left - 4;
    }
}

/*
 *     직능 목록 조회 
 */
function Cbo_Cours_Init()
{

    tit_clearActionInfo(this);
    tit_addSearchActionInfo("profes:searchProfes0003Course", false);   

    tit_callService( 
          ""
        , ""  
        , ""//inDs 
        , "ds_Cours=ds_oCourse" //outDs
        , "TCSEARCHGUBUN= " + quote(cbxEduGubun2.Value) // 교육구분(조회구분)
        , "afterSearch_Cours"  //callBack
        , false
        ,""
        , true
        );    
    }
}

function afterSearch_Cours(errCode, errMsg){
    if(errCode != 0 ){
        alert("오류가 발생했습니다.");
    } else{
        ds_Cours.insertRow(0);
        ds_Cours.SetColumn(0,"TCCOURSENM","전체");        
    }
}


/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus, sFirst) {
    btnInput.Enable = bStatus; // 추가 버튼
    btnDelete.Enable = bStatus; // 삭제 버튼
    btnSave.Enable = bStatus; // 수정&저장 버튼
    grdEduCourse.Height = sFirst;

    lbEduGubun2.Visible = bStatus;
    lbCourseCode.Visible = bStatus;
    lbEduCourseNM.Visible = bStatus;
    lbMunjeGubun.Visible = bStatus;
    lbEduTime.Visible = bStatus;
    lbEduDate.Visible = bStatus;
    lbRemark.Visible = bStatus;
    cbxEduGubun2.Visible = bStatus;
    edCourseCode.Visible = bStatus;
    edEduCourseNM.Visible = bStatus;    
    cbxMunjeGubun.Visible = bStatus;
    edEduTime.Visible = bStatus;
    calEduDate.Visible = bStatus; 
    edRemark.Visible = bStatus;   
}

/*
 * 기능 : 조회기능
 * parameter : 
 * return : 없음
 */
function lfn_Search(obj)
{

    // 조회이력을 저장한다.  
    ls_HistoryArg = "교육구분=" + quote(cbxEduGubun.Text)
                //+ ",교육과정=" + quote(cbxEduCours.Text)
                ;
    gfn_InsertSearchPrintHistory(
        "P"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );

	ds_EduCourse.ClearData(); // 데이타셋 초기화
    //medTotalCnt.Value = "";
	tit_clearActionInfo(this);	
	tit_addCSVSearchActionInfo("profes:searchProfes0003EduCourse", false);

	tit_callService(  
	                 ""
	               , ""  
	               , ""//inDs 
	               , "ds_EduCourse=ds_oProfesList" //outDs
	               , "TCSEARCHGUBUN= " + quote(cbxEduGubun.Value) // 교육구분(조회구분)
	               , "afterSearch_Grid"  //callBack
	               , false	               
	               ); 

    if (ms_FormAuth == "R") {
        edEduCourseNM.Visible = false;  // 교육과정 텍스트 읽기 권한

    } else {
        edEduCourseNM.Visible = true;  // 교육과정 텍스트
        cbxEduCourse.Visible = false;  // 교육과정 콤보
    }
}

// 조회 후 callback
function afterSearch_Grid(errCode, errMsg){

	if(errCode != 0 ){
		alert("오류가 발생했습니다.");
	}
	else{
		if(ds_EduCourse.rowcount > 0){
		}
		else{
           // medTotalCnt.Value = "";
            //입력부분초기화
            gfn_FormInit(this);
            cbxMunjeGubun.Enable = true;
		}
	}
    //총건수 구하기
  //  medTotalCnt.Value = ds_EduCourse.GetRowCount();    

    if (ms_FormAuth == "R") {
        for(i = 0; i < ds_EduCourse.RowCount(); i++) {
                ds_EduCourse.SetColumn(i, "PCREMARK", ""); 
            }  
   	}
}

// 조회 후 callback
function afterSearch(errCode, errMsg){

	if(errCode != 0 ){
		alert("오류가 발생했습니다.");
	}
	else{
		if(ds_EduCourse.rowcount > 0){
		}
		else{
           // medTotalCnt.Value = "";
            //입력부분초기화
            gfn_FormInit(this);
            cbxMunjeGubun.Enable = true;
		}
	}
    //총건수 구하기
   // medTotalCnt.Value = ds_EduCourse.GetRowCount();

}


/*
 * 기능 : 추가
 */
function lfn_Input(obj) {

	if (!gfn_Confirm(MSGI00070)) { // 신규 추가 하시겠습니까?\n입력완료 후 저장 버튼을 누르세요.
        return false;
	}
	mb_IsNewData = true; // 저장버튼이 클릭 설정

    lfn_Cancel(); // 값 초기화 

    edEduCourseNM.Visible = false;  // 교육과정 텍스트
    cbxEduCourse.Visible = true;  // 교육과정 콤보
    cbxEduCourse.Left = 445;

    edCourseCode.Enable = true;
    edEduCourseNM.Enable = true;
    cbxMunjeGubun.Enable = false;
    edCourseCode.Enable = false;
    edEduTime.Enable = true;
    calEduDate.Enable = true;
    edRemark.Enable = true;

    if(cbxEduGubun2.Value == "B") {    
        cbxMunjeGubun.Enable = false;
    } else if(cbxEduGubun2.Value == "T") {
        cbxEduGubun2.Enable = true;
    }

    Cbo_Cours_Init();  // 교육과정을 가져온다.
    edCourseCode.Text = ds_Cours.GetColumn(1, "TCCOURSECD");
    cbxMunjeGubun.Value = ds_Cours.GetColumn(1, "TCMUNJEGUBUN");
    calEduDate.Enable = true;
}

/*
 * 기능 : 삭제
 */
function lfn_Delete(obj) {
    if (ds_EduCourse.currow < 0) {
        gfn_Alert(MSGI00030); // 삭제할 레코드가 없습니다.
        return false;
    }

	if (!gfn_Confirm("교육과정 삭제시 교육과목이 있을 경우 함께 삭제 됩니다. 삭제 하시겠습니까?")) { // 정말로 삭제 하시겠습니까?
        return false;
	}

    gfn_FormInit(this); // 입력란 초기화
    tit_clearActionInfo();
    tit_AddSingleActionInfo("profes:deleteProfes0003EduCourse", "", 0, "", "N");
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,"" // inDs		
        ,""	// outDs
        , "TCCOURSECD=" + ds_EduCourse.GetColumn(ds_EduCourse.currow, "PCTCCOURSECD")
          +" TCSEARCHGUBUN=" + ds_EduCourse.GetColumn(ds_EduCourse.currow, "PCTCSEARCHGUBUN")
          +" PCMGNO=" + ds_EduCourse.GetColumn(ds_EduCourse.currow, "PCMGNO")         
          +" TCMUNJEGUBUN=" + ds_EduCourse.GetColumn(ds_EduCourse.currow, "PCTCMUNJEGUBUN")
        ,"afterDelete"
        ,false
        ,false
        ,true
    );                       

//    ds_EduCourse.DeleteRow(ds_EduCourse.currow); // 선택된 ROW를 삭제한다.    
    gfn_Alert(MSGI00020);
    lfn_Search(); //재조회 타기
    }   
}

/*
 * 기능 : 삭제 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function afterDelete(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {
	if (!gfn_FormValidate(this)) { // 입력 Validation Check
		return false;
	}

	if (mb_IsNewData || ds_EduCourse.GetCount() == 0) { // 신규 저장		

        // 면제 구분 해당 없을 경우 X값을 DB는 N으로 값들어가도록 하게함
        var munje_gubun = ""; // 면제 구분         
        if (cbxMunjeGubun.Value == "X") {
            munje_gubun = "N";
        } else {
            munje_gubun = cbxMunjeGubun.Value;
        }

	////////////// 과정코드 중복 여부 체크    
        tit_clearActionInfo();
        tit_addSearchActionInfo("profes:searchProfes0003EduCourseCheck", false);// 

        // 서버 호출 
        tit_callService( 
            ""
            ,""
            ,""	// inDs
            ,"ds_EduCourseCDCheck=ds_oProfesCheck"	// outDs

            , "TCCOURSECD=" + quote(edCourseCode.Text)      //직능코드
                //+ " TCCOURSENM=" + quote(edEduCourseNM.Text)     //직능명
                + " TCSEARCHGUBUN=" + quote(cbxEduGubun2.Value)  //교육구분(B,T)
                + " TCMUNJEGUBUN=" + quote(munje_gubun)  //면제구분	// args 
            ,"lfn_AfterSaveCourseCodeCheck"
            ,false
            ,""
            ,true
        );

        if (ds_EduCourseCDCheck.count > 0) {
            alert("동일한 교육과정코드가 이미 사용 중 입니다. 확인바랍니다.");
            return false;
        }            
	    //////////////

        // 키 중복 체크    
        if (!gfn_Confirm(MSGW00020)) { // 정말로 추가 저장 하시겠습니까?
			return false;
		}
		// 입력된 값을 그리드로 복사한다.
		ds_EduCourse.InsertRow(0);
		ds_EduCourse.SetColumn(0, "PCTCCOURSECD", edCourseCode.Text);      //직능코드
		ds_EduCourse.SetColumn(0, "PCTCCOURSENM", edEduCourseNM.Text);     //직능명
		ds_EduCourse.SetColumn(0, "PCTCSEARCHGUBUN", cbxEduGubun2.Value);  //교육구분(B,T)
		ds_EduCourse.SetColumn(0, "PCTCMUNJEGUBUN", cbxMunjeGubun.Value);  //면제구분
		ds_EduCourse.SetColumn(0, "PCTIME", edEduTime.Text);         //교육시간
		ds_EduCourse.SetColumn(0, "PCFDATE", calEduDate.Value);        //적용일자
		ds_EduCourse.SetColumn(0, "PCREMARK", edRemark.Text);      //비고

        /////////////        

        tit_clearActionInfo();
        tit_addSearchActionInfo("profes:insertProfes0003EduCourse", false);// 

        // 서버 호출 
        tit_callService( 
            ""
            ,""
            ,""	// inDs
            ,""	// outDs

            , "TCCOURSECD=" + quote(edCourseCode.Text)      //직능코드
                + " TCCOURSENM=" + quote(cbxEduCourse.text)     //직능명
                + " TCSEARCHGUBUN=" + quote(cbxEduGubun2.Value)  //교육구분(B,T)
                + " TCMUNJEGUBUN=" + quote(munje_gubun)  //면제구분
                + " TCTERMTIME= " + quote(edEduTime.Text)         //교육시간
                + " PCFDATE=" + quote(calEduDate.Value)           //적용일자
                + " TCREQUIREDDOCS=" + quote(edRemark.Text)      //비고	// args 
            ,"lfn_AfterSaveCourseCode"
            ,false
        );
        /////////////
    } else {

        var firstvalue =  ds_EduCourse.GetColumn(ds_EduCourse.currow, "PCTCCOURSECD")      //직능코드
                        + ds_EduCourse.GetColumn(ds_EduCourse.currow, "PCTCCOURSENM")     //직능명
                        + ds_EduCourse.GetColumn(ds_EduCourse.currow, "PCTCSEARCHGUBUN") //교육구분(B,T)
                        + ds_EduCourse.GetColumn(ds_EduCourse.currow, "PCTCMUNJEGUBUN")  //면제구분
                        + ds_EduCourse.GetColumn(ds_EduCourse.currow, "PCTIME")         //교육시간
                        + ds_EduCourse.GetColumn(ds_EduCourse.currow, "PCFDATE")        //적용일자
                        + ds_EduCourse.GetColumn(ds_EduCourse.currow, "PCREMARK");      //비고

        var second =   edCourseCode.Text+edEduCourseNM.Text+cbxEduGubun2.Value+cbxMunjeGubun.Value
                     + edEduTime.Text + calEduDate.Value + edRemark.Text;

        if(firstvalue == second) {     //비고
            alert("변경할 사항이 없습니다.");
            return false;
        }

        if (!gfn_Confirm(MSGW00040)) { // 정말로 수정 저장 하시겠습니까?
			return false;
		}	

		// 입력된 값을 그리드로 복사한다.
		ds_EduCourse.SetColumn(ds_EduCourse.currow, "PCTCCOURSECD", edCourseCode.Text);      //직능코드
		ds_EduCourse.SetColumn(ds_EduCourse.currow, "PCTCCOURSENM", edEduCourseNM.Text);     //직능명
		ds_EduCourse.SetColumn(ds_EduCourse.currow, "PCTCSEARCHGUBUN", cbxEduGubun2.Value);  //교육구분(B,T)
		ds_EduCourse.SetColumn(ds_EduCourse.currow, "PCTCMUNJEGUBUN", cbxMunjeGubun.Value);  //면제구분
		ds_EduCourse.SetColumn(ds_EduCourse.currow, "PCTIME", edEduTime.Text);         //교육시간
		ds_EduCourse.SetColumn(ds_EduCourse.currow, "PCFDATE", calEduDate.Value);        //적용일자
		ds_EduCourse.SetColumn(ds_EduCourse.currow, "PCREMARK", edRemark.Text);      //비고


        tit_addSingleActionInfo("profes:updateProfes0003EduCourse", "", 0, "");
        tit_callService(
            ""
            ,""
            ,"ds_iProfesList=ds_EduCourse:U"	// inDs
            ,""	// outDs
            ,"TCCOURSECD=" + quote(ds_EduCourse.GetColumn(ds_EduCourse.currow, "PCTCCOURSECD")) //직능코드*******
                + " TCSEARCHGUBUN=" + quote(ds_EduCourse.GetColumn(ds_EduCourse.currow, "PCTCSEARCHGUBUN"))  //교육구분(B,T)****
                + " TCMUNJEGUBUN=" + quote(ds_EduCourse.GetColumn(ds_EduCourse.currow, "PCTCMUNJEGUBUN"))  //면제구분****
                + " TCTERMTIME= " + quote(edEduTime.Text)         //교육시간
                + " PCFDATE=" + quote(calEduDate.Value)           //적용일자
                + " TCREQUIREDDOCS=" + quote(edRemark.Text)      //비고 // args
            ,"lfn_AfterSaveCourseCode"
            ,false
            ,""
            ,true
        );

    }

}    

/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveCourseCode(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    gfn_FormInit(this); // 입력란 초기화
    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    lfn_Search(); // 직능코드 조회
}


function lfn_AfterSaveCourseCodeCheck(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

}
//그리드 행 선택시 입력란에 보이도록
function ds_EduCourse_OnRowPosChanged(obj,nOldRow,nRow) {
    // 그리드의 값을 입력란에 복사한다. 추가상태인 경우 복사 후 저장을 하기 위해 제외한다.
    if (obj.GetRowType(nRow) <> "insert" && nRow <> -1) { 

        if (ms_FormAuth == "R") {
            edEduCourseNM.Visible = false;  // 교육과정 텍스트 읽기 권한
        } else {
            edEduCourseNM.Visible = true;  // 교육과정 텍스트
        }
        cbxEduCourse.Visible = false;  // 교육과정 콤보  

        cbxEduGubun2.Value = obj.GetColumn(nRow, "PCTCSEARCHGUBUN");
        edCourseCode.Value = obj.GetColumn(nRow, "PCTCCOURSECD");
        edEduCourseNM.Value = obj.GetColumn(nRow, "PCTCCOURSENM");
        edEduTime.Value = obj.GetColumn(nRow, "PCTIME");
        calEduDate.Value = obj.GetColumn(nRow, "PCFDATE");
        edRemark.Value = obj.GetColumn(nRow, "PCREMARK");        
        cbxMunjeGubun.Value = obj.GetColumn(nRow, "PCTCMUNJEGUBUN");                             

        edEduTime.Enable = true;
        calEduDate.Enable = false;
        edRemark.Enable = true;

        gfn_MakeKeyMark(this); // 키값 수정 금지 설정        
	}

	mb_IsNewData = false;      // 저장버튼이 클릭 초기화
}

function gdCourseCode_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var lobj_Dataset = object(obj.BindDataset);
	ds_EduCourse_OnRowPosChanged(lobj_Dataset,nRow,nRow);
}

//보고서 출력
function lfn_Print(obj)
{
    if (ds_EduCourse.RowCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }

    // 조회이력을 저장한다.  
    ls_HistoryArg = "교육구분=" + quote(cbxEduGubun.Text)
               //  + ",교육과정=" + quote(cbxEduCours.Text)
                ;
    gfn_InsertSearchPrintHistory(
        "P"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );

	gfn_CallReportPopup("frmProfes0003R01EduMgnt.rex"
                      , "ds_EduCourse"
                      , "EDUGUBUN=" + cbxEduGubun.Text // + "^EDUCOURS=" + cbxEduCours.Text 
                      , true);
}


/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(grdEduCourse, 0); // 멀티 정렬을 실행한다.
}

function grdEduCourse_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
    if (nCell == 0) { // 선택
        if (obj.GetCellProp("head", nCell, "text") == 1) { // 전체가 이미 선택된 경우
            obj.SetCellProp("head", nCell, "text", 0);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "SEL", "0");
            }
        } else { // 전체가 이미 선택되지 않은 경우
            obj.SetCellProp("head", nCell, "text", 1);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "SEL", "1");
            }
        }
	} else { // 헤드 정렬
        lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex);
	}
}

function lfn_localExcel(obj)
{
  	if (ds_EduCourse.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return false;
    }
	lfn_Excel(obj);
}

function lfn_localCancel(obj)
{

	lfn_Cancel();
}

