 #include "LIB::profes.js"; // 공통업무 관련
 
function Form_OnLoadCompleted(obj)
{
     gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정

    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";
    calstartdate.Value = left(today(),4)+"0101";  // 당해년도 1/1일 고정
    calenddate.Value = today();
    
    gfn_SearchJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회
    cbxjibu.Index = 0;
}

function lfn_Search(obj)
{
	var searchInfo = "";
    var ds = "";
    tit_clearActionInfo();
    
	/////
	 // 내역조회전 CNT를 구해놓고 계산한다.
		searchInfo = "profes:searchProfes0001proMgmtList_ChartCnt";
		ds = "ds_ioListChartCnt=ds_ioListChartCnt";	
		ds_ioListChartCnt.ClearData(); // 데이타셋 초기화
		tit_addCsvSearchActionInfo(searchInfo, false);		
		// 서버 호출 
		tit_callService(
			 ""
			,""
			,""					// inDs
			,ds	// outDs
			,"STARTDATE=" + quote(calStartDate.Value)
			+" ENDDATE=" + quote(calEndDate.Value)
			+" JIBU="+ quote(cbxjibu.Value)
			,"afterSearch_ChartCnt"
			,false
		);	 
	////
    
    switch(TAB_Profes.TabIndex)
    {
		case 0:
			ds_ioListChart1.ClearData(); // 데이타셋 초기화
			searchInfo = "profes:searchProfes0001proMgmtList_Chart1";
			ds = "ds_ioListChart1=ds_ioListChart1";		
		break;
		
		case 1:
			ds_ioListChart2.ClearData(); // 데이타셋 초기화
			searchInfo = "profes:searchProfes0001proMgmtList_Chart2";
			ds = "ds_ioListChart2=ds_ioListChart2";		
		break;
		
		case 2:
			ds_ioListChart3.ClearData(); // 데이타셋 초기화
			searchInfo = "profes:searchProfes0001proMgmtList_Chart3";
			ds = "ds_ioListChart3=ds_ioListChart3";		
		break;
		
		case 3:
			ds_ioListChart4.ClearData(); // 데이타셋 초기화
			searchInfo = "profes:searchProfes0001proMgmtList_Chart4";
			ds = "ds_ioListChart4=ds_ioListChart4";		
		break;	
			
		case 4:
			searchInfo = "profes:searchProfes0001proMgmtList_Chart5";
			ds = "ds_ioListChart5=ds_ioListChart5";		
			ds_ioListChart5.ClearData(); // 데이타셋 초기화
		break;
		
		case 5:	
			ds_ioListChart6.ClearData(); // 데이타셋 초기화
			searchInfo = "profes:searchProfes0001proMgmtList_Chart6";
			ds = "ds_ioListChart6=ds_ioListChart6";		
		break;
    }
   
	tit_addCsvSearchActionInfo(searchInfo, false);	
	// 서버 호출 
	tit_callService(
		 ""
		,""
		,""					// inDs
		,ds	// outDs
		,"STARTDATE=" + quote(calStartDate.Value)
		+" ENDDATE=" + quote(calEndDate.Value)
		+" JIBU=" + quote(cbxjibu.Value)
		,"afterSearch_Chart"
		,false
	);	
}

function afterSearch_ChartCnt(nErrorCode, strErrorMsg)
{
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
		gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
   
    edK.Value = ds_ioListChartCnt.GetColumn(0, "CNT2");
    edS.Value = ds_ioListChartCnt.GetColumn(1, "CNT2");
}

// 조회 후 callbackfunction afterSearch_Chart(nErrorCode, strErrorMsg)
{
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
		gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
    switch(TAB_Profes.TabIndex)
    {
		case 0:
			Chart_Start1();
		break;
		
		case 1:
			Chart_Start2();
		break;
		
		case 2:
			Chart_Start3();
		break;
		
		case 3:
			Chart_Start4();
		break;	
			
		case 4:
			Chart_Start5();
		break;
		
		case 5:	
			Chart_Start6();
		break;
    }
}	

function Chart_Start1()
{
	var Row = ds_ioListChart1.rowcount;
	TAB_Profes.tab2.XChart1.ClearData("ClearDataFlag_AllData");
	
    //-----------소숫점 자리수 표현------------------------------------------------------- 
    TAB_Profes.tab2.XChart1.Axis[0].LabelsFormat.Decimals = 1;	  //소수점 자리수 표현 
    TAB_Profes.tab2.XChart1.AxisX.Font.size="5";
    TAB_Profes.tab2.XChart1.Font.size="6";
    //TAB_Training.tab2.XChart1.AxisY.LabelsFormat.Format = "AxisFormat_Currency"; 
    TAB_Profes.tab2.XChart1.AxisY.LabelsFormat.Format = "AxisFormat_Number";
    TAB_Profes.tab3.XChart1.Volume = 100;
    //------------------------------------------------------------------------------------

	TAB_Profes.tab2.XChart1.OpenData("COD_Values", 0, 2);
	TAB_Profes.tab2.XChart1.BackColor = "white";
	TAB_Profes.tab2.XChart1.BorderColor = "white";
	//축에 이름을 넣는다.
	TAB_Profes.tab2.XChart1.NValues = Row;
	For(var j=0;j<Row; j++)	{
		TAB_Profes.tab2.XChart1.Legend(j) = ds_ioListChart1.GetColumn(j,"EMSCNM");   
	}
	
	For(var j=0;j<Row; j++)	{
		TAB_Profes.tab2.XChart1.Value(0, j) = round(ds_ioListChart1.GetColumn(j,"VAL_TT"),1);  //결과
		TAB_Profes.tab2.XChart1.Value(1, j) = TAB_Profes.tab2.XChart1.Hidden;
	}
	
    TAB_Profes.tab2.XChart1.AxisY.step=10;
	TAB_Profes.tab2.XChart1.Point.item(0, 0).color= "yellow";
	TAB_Profes.tab2.XChart1.Point.item(0, 1).color= "lightcyan";
	TAB_Profes.tab2.XChart1.Point.item(0, 2).color= "lightcyan";
	TAB_Profes.tab2.XChart1.Point.item(0, 3).color= "lightcyan";
	TAB_Profes.tab2.XChart1.Point.item(0, 4).color= "lightcyan";
	TAB_Profes.tab2.XChart1.Point.item(0, 5).color= "lightcyan";
	TAB_Profes.tab2.XChart1.Point.item(0, 6).color= "lightcyan";
	TAB_Profes.tab2.XChart1.Point.item(0, 7).color= "lightcyan";
	TAB_Profes.tab2.XChart1.Point.item(0, 8).color= "lightcyan";
	TAB_Profes.tab2.XChart1.Point.item(0, 9).color= "lightcyan";	
}

