function fn_avg(ds)
{
	var nSum = 0;
	var rowcount = 0;
	for(i=0; i<ds.GetRowCount(); i++)
	{
		nSum = toNumber(ds.Sum("VAL_TT"));
		rowcount = ds.GetRowCount();
	}
		return (round(nSum/rowcount,1));
}

function TAB_Profes_OnChanged(obj,nOldIndex,nNewindex)
{
	if(nNewindex > 1)
	{	
		cbxjibu.Index = 0;
		cbxjibu.Visible = false;
		Static2.Visible = false;
	}else {
		cbxjibu.Index = 0;
		cbxjibu.Visible = true;
		Static2.Visible = true;
	}
}
