//조회
function lfn_Search(obj)
{
    if (calEduDateStart.Value > calEduDateEnd.Value) {
        gfn_Alert("시작일자가 종료일자보다 늦습니다.");
        calEduDateStart.Value = left(GetDate(), 8);
        calDateStart.SetFocus();
        return false;
    }

    // 조회이력을 저장한다.  
    ls_HistoryArg = "직급=" + quote(cbxClass.Text)
                + ",교수구분=" + quote(cbxProGubun.Text)
                + ",교육시작일=" + quote(left(calEduDateStart.Value, 8))
                + ",교육종료일=" + quote(left(calEduDateEnd.Value, 8))
                ;
    gfn_InsertSearchPrintHistory(
        "P"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );    

    tit_clearActionInfo(this); 
    //TAB_Training.TabIndex = 1; //조회버튼 선택시  나오게 한다.    tit_addSearchActionInfo("profes:searchProfes0011StatJibu", false);
    ds_SearchJogeon.DeleteAll(); 
    ds_SearchJogeon.AddRow();
    ds_SearchJogeon.setColumn(0, "EDUDATEST", calEduDateStart.Value); 
    ds_SearchJogeon.setColumn(0, "EDUDATEED", calEduDateEnd.Value);   
    ds_SearchJogeon.setColumn(0, "EDUCLASS", cbxClass.Value); 
    ds_SearchJogeon.setColumn(0, "PROGUBUN", cbxProGubun.Value); 

    tit_callService(  
                  ""
                , ""  
                , "ds_iProfesList=ds_SearchJogeon"//inDs 
                , "ds_ProfesList=ds_oProfesList" //outDs
                , ""
                , "afterSearch"  //callBack
                , false
                ); 
}

