function lfn_Print(obj)
{
    if (ds_ProfesList.RowCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }

    // 조회이력을 저장한다.  
    ls_HistoryArg = "직급=" + quote(cbxClass.Text)
                + ",교수구분=" + quote(cbxProGubun.Text)
                + ",교육시작일=" + quote(left(calEduDateStart.Value, 8))
                + ",교육종료일=" + quote(left(calEduDateEnd.Value, 8))
                ;
    gfn_InsertSearchPrintHistory(
        "P"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );

    gfn_CallReportPopup("frmProfes0011R01ProfesSearch.rex"
                      , "ds_ProfesList"
                      , "STARTDATE=" + calEduDateStart.Value + "^ENDDATE=" + calEduDateEnd.Value
                       + "^CLASS=" + cbxClass.Text
                       + "^PROGUBUN=" + cbxProGubun.Text
                      , true);
}


