#include "LIB::COMMON.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close(false);
    }

    // 공통 코드 조회
    gfn_SearchJibu(cbxJibu, "Y"); // 지부 코드 조회
    cbxJibu.Enable = false;

    //초기값 셋팅
	edTimeTypeNm.Text = sTypeNm;	//시간표 유형이름
	cbxEduGubun.Value = sGubun;     //교육구분

	if(sFlag == "NEW") {
		lbAddLabel.Visible = true;
		edTimeTypeNm.Enable = false;
		cbxEduGubun.Enable = false;
		//btnAddLunch.Visible = false;
		//btnAdd.Visible = false;
		//btnDel.Visible = false;
		//gdTimeSchedule.SetCellProp("body", 4, "Edit", "none");
		//gdTimeSchedule.SetCellProp("body", 3, "Edit", "none");
		ds_ioTimeSchedule.Copy(parent.ds_iTimeScheduleDetailList);
	} else {
        edTimeType.Text = sType;	//시간표 유형
		lfn_Search();
	}
}

function Form_OnUnloadCompleted(obj) {
    //Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 113 : // 조회(F2)
            //lfn_Search();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_ioTimeSchedule.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("profes:searchProfes0021PTimeScheduleList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioTimeSchedule=ds_ioTimeSchedule"	// outDs
		,"TSJIBU=" + quote(sJibu) // 지부
		 + " TSTYPE=" + quote(sType)	// 시간표 유형
		,""
		,false
		,true
		,true
	);
}

/*
 * 기능 : 조회 (시간표유형 조회)
 */
function lfn_SearchTeachingTimeType() {

	tit_clearActionInfo();
	tit_addSearchActionInfo("profes:searchProfes0005TimeType", false);

	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oTimeType=ds_oTimeType"	// outDs
		,"TSGEMGNO=" + quote(cbxJibu.Value) // 지부
		,"lfn_AfterSearchTeachingTimeType"
		,false
		,true
		,true
	);
}

/*
 * 기능 : 시간표유형 조회 후 처리해야 할 내용
 * parameter : ErrorCode - 에러코드
 *             ErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchTeachingTimeType(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
}

