{

}

/*
 * 기능 : 저장
 */
function lfn_Save(obj)
{
	//Validation Check
	for(var i=0; i<ds_ioTimeSchedule.GetRowCount(); i++) {
		for(var j=0; j<ds_ioTimeSchedule.GetColCount(); j++) {
			if(ds_ioTimeSchedule.GetColID(j) != "TSTYPESEQ" && ds_ioTimeSchedule.GetColID(j) != "TSREMARK") {
				if(gfn_IsNull(ds_ioTimeSchedule.GetColumn(i, ds_ioTimeSchedule.GetColID(j)))) {
					if(ds_ioTimeSchedule.GetColID(j) == "TSGEMGNO") {
						gfn_Alert("지부코드 은(는) 필수입력사항 입니다. ");
						return;
					}
					else if(ds_ioTimeSchedule.GetColID(j) == "TSSEQ") {
						gfn_Alert("시간표순번 은(는) 필수입력사항 입니다. ");
						return;
					}
					else if(ds_ioTimeSchedule.GetColID(j) == "TSTIME") {
						gfn_Alert("강의교시 은(는) 필수입력사항 입니다. ");
						return;
					}
					else if(ds_ioTimeSchedule.GetColID(j) == "TSSTARTTIME") {
						gfn_Alert("강의시작시간 은(는) 필수입력사항 입니다. ");
						return;
					}
					else if(ds_ioTimeSchedule.GetColID(j) == "TSENDTIME") {
						gfn_Alert("강의종료시간 은(는) 필수입력사항 입니다. ");
						return;
					}
					else if(ds_ioTimeSchedule.GetColID(j) == "TSHOUR") {
						gfn_Alert("강의시간 은(는) 필수입력사항 입니다. ");
						return;
					}
				}
			}
		}
	}

	if(!gfn_Confirm(MSGW00060)) {
		return;
	}

	if(sFlag == "VIEW") {	//수정
		if(ds_ioTimeSchedule.GetUpdate() == false 
            && sTypeNm == edTimeTypeNm.Text
            && sGubun == cbxEduGubun.Value) {
			gfn_Alert(MSGI00040);
			return;
		}
		if(sTypeNm != edTimeTypeNm.Text) {
			for(var i=0; i<ds_ioTimeSchedule.GetRowCount(); i++) {
				ds_ioTimeSchedule.SetColumn(i, "TSTYPENM", trim(edTimeTypeNm.Text));
			}
		}
		if(sGubun != cbxEduGubun.Value) {
			for(var i=0; i<ds_ioTimeSchedule.GetRowCount(); i++) {
				ds_ioTimeSchedule.SetColumn(i, "TSGUBUN", trim(cbxEduGubun.Value));
			}
		}
	}

	tit_clearActionInfo();
	if(sFlag == "VIEW") {	//수정
		tit_addMultiActionInfo("profes:updateProfes0021TimeScheduleList", "", 0, "", "N");
	} else if (sFlag == "NEW") {	//신규
        tit_addMultiActionInfo("profes:insertProfes0021TimeScheduleList", "", 0, "", "N");
    }
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_ioTimeSchedule=ds_ioTimeSchedule"	// inDs
		,""	// outDs
		,"GUBUN=" + quote(sFlag)	// args 
		,"lfn_AfterSaveTimeScheduleList"
		,false
		,true
		,true
	);
}

/*
 * 기능 : 저장 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveTimeScheduleList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

	gfn_SaveMessage(); // 저장 메세지를 처리한다.
    Close(true);
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close(false);
}
