/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)

 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    btnSave.Enable = bStatus; // 수정&저장 버튼
    btnInput.Enable = bStatus; // 추가 버튼
}

/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchProfessorList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdProfessorList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 사용자 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(gdProfessorList, 0); // 멀티 정렬을 실행한다.
}

function cbxJibu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_SearchTeachingTimeType();
}

/*
 * 기능 : 강의교시 추가
 */
function btnAdd_OnClick(obj)
{
	var LunchFlag = false;

	for(var i=0; i<ds_ioTimeSchedule.GetRowCount(); i++) {
		if(ds_ioTimeSchedule.GetColumn(i, "TSTIME") == 0) {
			LunchFlag = true;
		}
	}

	var dsParentObj = parent.ds_oTimeSchedule;
	var rIdx = ds_ioTimeSchedule.AddRow();

	ds_ioTimeSchedule.SetColumn(rIdx, "TSGEMGNO", dsParentObj.GetColumn(dsParentObj.currow, "TSGEMGNO"));		//지부코드
	ds_ioTimeSchedule.SetColumn(rIdx, "TSTYPE", dsParentObj.GetColumn(dsParentObj.currow, "TSTYPE"));			//시간표유형
	ds_ioTimeSchedule.SetColumn(rIdx, "TSSEQ", rIdx+1);			//시간표순번
	ds_ioTimeSchedule.SetColumn(rIdx, "TSTYPESEQ", "");		//시간표유형순번
	if(LunchFlag == true) {
		ds_ioTimeSchedule.SetColumn(rIdx, "TSTIME", rIdx);			//강의교시
	} else {
		ds_ioTimeSchedule.SetColumn(rIdx, "TSTIME", rIdx+1);			//강의교시
	}
	ds_ioTimeSchedule.SetColumn(rIdx, "TSSTARTTIME", "");	//강의시작시간
	ds_ioTimeSchedule.SetColumn(rIdx, "TSENDTIME", "");		//강의종료시간
	ds_ioTimeSchedule.SetColumn(rIdx, "TSREMARK", "");		//시간표유형설명
	ds_ioTimeSchedule.SetColumn(rIdx, "TSHOUR", 1);		//강의시간
}

/*
 * 기능 : 강의교시 삭제
 */
function btnDel_OnClick(obj)
{
	if(ds_ioTimeSchedule.currow+1 == ds_ioTimeSchedule.GetRowCount()) {
		ds_ioTimeSchedule.DeleteRow(ds_ioTimeSchedule.currow);
	} else {
		gfn_Alert("마지막 일차 순서대로 행삭제가 가능합니다.");
	}
}

/*
 * 기능 : 점심시간 추가
 */
function btnAddLunch_OnClick(obj)
{
	var dsParentObj = parent.ds_oTimeSchedule;
	var rIdx = ds_ioTimeSchedule.AddRow();

	ds_ioTimeSchedule.SetColumn(rIdx, "TSGEMGNO", dsParentObj.GetColumn(dsParentObj.currow, "TSGEMGNO"));		//지부코드
	ds_ioTimeSchedule.SetColumn(rIdx, "TSTYPE", dsParentObj.GetColumn(dsParentObj.currow, "TSTYPE"));			//시간표유형
	ds_ioTimeSchedule.SetColumn(rIdx, "TSSEQ", rIdx+1);			//시간표순번
	ds_ioTimeSchedule.SetColumn(rIdx, "TSTYPESEQ", "");		//시간표유형순번
	ds_ioTimeSchedule.SetColumn(rIdx, "TSTIME", 0);			//강의교시
	ds_ioTimeSchedule.SetColumn(rIdx, "TSSTARTTIME", "");	//강의시작시간
	ds_ioTimeSchedule.SetColumn(rIdx, "TSENDTIME", "");		//강의종료시간
	ds_ioTimeSchedule.SetColumn(rIdx, "TSREMARK", "");		//시간표유형설명
	ds_ioTimeSchedule.SetColumn(rIdx, "TSHOUR", 1);		//강의시간
}
