/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.

    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }	
	// 주무부서에서 사용자 권한관리
	if (gds_oUserInfo.GetColumn(0,"ADMINGUBUN") != "SM")  // 시스템관리자가 아니면
		lfn_SearchDeptAdminAuth();


    // 공통 코드 조회
    //gfn_SearchUserAuth(ds_oSysGubun, "MSYSGUBUN", "Y", "없음");
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        tvMenu.Height = nCy - tvMenu.Top - 4;
        gdUserAuth.Height = nCy - gdUserAuth.Top - 4;
        gdUserAuth.Width = nCx - 324 - 4;
    }
}
// 주무부서에서 사용자 권한관리 위해 주무부서별 메뉴권한 조회
function lfn_SearchDeptAdminAuth(obj) {
var DEPTCD = "";

    ds_ioDeptAuth.ClearData(); // 데이타셋 초기화

	DEPTCD = gds_oUserInfo.GetColumn(0, "PDEPTCD");
	
	tit_clearActionInfo();
	tit_addSearchActionInfo("sys:searchDeptAdminAuth", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioDeptAuth=ds_oDeptAuth"	// outDs
		,"DEPTCD=" + quote(DEPTCD)	// args 
		,""
		,false
	);
}

/*
 * 기능 : 권한 조회
 */
function lfn_Search(obj) {
	if (!gfn_FormValidate(this)) { // 입력 Validation Check
		return false;
	}
    ds_ioUserAuth.ClearData(); // 데이타셋 초기화
    // 전체 선택 초기화
    gdUserAuth.SetCellProp("head", 6, "text", 0); // 사용유무
    gdUserAuth.SetCellProp("head", 7, "text", 0); // 읽기쓰기권한
    gdUserAuth.SetCellProp("head", 8, "text", 0); // 읽기권한

    tit_clearActionInfo();
	tit_addSearchActionInfo("sys:searchUserAuth", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioUserAuth=ds_oUserAuth"	// outDs
		,"AUTHSABUN=" + quote(edUserId.Text)
            + " AUTHDEPTCD=" + quote(ls_AuthDeptCd)
            + " MMENUID=" + quote(ds_oMenuTree.GetColumn(ds_oMenuTree.currow, "MMENUID"))	// args 
			+ " DAAGUBUN=" + quote(ds_ioDeptAuth.GetColumn(0, "DAAGUBUN"))

		,"lfn_AfterSearchUserAuth"
		,false
	);
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {
	if (!gfn_FormValidate(this)) { // 입력 Validation Check
		return false;
	}

    // 키 중복 체크

    if (!gfn_Confirm(MSGW00040)) { // 정말로 수정 저장 하시겠습니까?
        return false;
    }

    // 부모 메뉴들 추가
    var ln_Currow = -1;
    for (ln_Currow = tvMenu.GetParentRow(ds_oMenuTree.currow); ln_Currow <> -1; ln_Currow = tvMenu.GetParentRow(ln_Currow)) {
        ds_ioUserAuth.InsertRow(0);
        ds_ioUserAuth.SetColumn(0, "MMENUID", ds_oMenuTree.GetColumn(ln_Currow, "MMENUID"));
        ds_ioUserAuth.SetColumn(0, "MMENUNM", ds_oMenuTree.GetColumn(ln_Currow, "MMENUNM"));
        ds_ioUserAuth.SetColumn(0, "MORDERSEQ", ds_oMenuTree.GetColumn(ln_Currow, "MORDERSEQ"));
        ds_ioUserAuth.SetColumn(0, "PSABUN", edUserId.Text);
        ds_ioUserAuth.SetColumn(0, "PDEPTCD", ls_AuthDeptCd);
        ds_ioUserAuth.SetColumn(0, "USE_YN", "1");
        ds_ioUserAuth.SetColumn(0, "UAAUTH", "1");
    }

	tit_addMultiActionInfo("sys:updateUserAuth", "", 0, "", "N");
	//서버 호출 
	tit_callService(
		""
		,""
		,"ds_iUserAuth=ds_ioUserAuth:U"	// inDs
		,""	// outDs
		,""	// args 
		,"lfn_AfterSaveUserAuth"
		,false
	);
/*
    tit_addSaveActionInfo(
        "sys:updateUserAuth"	// insert
        , "sys:updateUserAuth"	// update
        , ""	// delete
        , ""	// flag column 명
        , ""	// key sql id
        , 0	// key 증가 값
        , "" 
        , 0 
        , "N"	// 실행타입
    );
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,"ds_iUserAuth=ds_ioUserAuth:U"	// inDs
        ,""	// outDs
        ,""	// args 
        ,"lfn_AfterSaveUserAuth"
        ,false
    );*/
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 메뉴 조회
 */
function lfn_SearchMenuTree(obj) {
    ds_oMenuTree.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("sys:searchMenu", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oMenuTree=ds_oMenu"	// outDs
		,"USE_YN='Y'"	// args 
            + " PDEPTCD=" + quote(gds_oUserInfo.GetColumn(0, "PDEPTCD"))
            + " ISDEPTAUTH=" + quote(gs_IsDeptAuth)
            
            + " DAAGUBUN=" + quote(ds_ioDeptAuth.GetColumn(0, "DAAGUBUN"))
            
            + " AUTHDEPTCD=" + quote(ls_AuthDeptCd)
		,"lfn_AfterSearchMenu"
		,false
	);
}

/*
 * 기능 : 메뉴 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchMenu(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
    lfn_Search(); // 권한 조회
}

/*
 * 기능 : 권한 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchUserAuth(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdUserAuth); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
    
    if (ds_ioUserAuth.GetCount() == 0) {
        chkDeleteUserAuthYn.Value = "Y";
        btnDeleteKemsAuth.Text = "권한삭제취소";
    } else {
        chkDeleteUserAuthYn.Value = "N";
        btnDeleteKemsAuth.Text = "권한삭제";
    }
}

/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveUserAuth(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    lfn_Search(); // 메뉴 조회
}

/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    btnSave.Enable = bStatus; // 수정&저장 버튼
    btnDeleteKemsAuth.Enable = bStatus;

    gdUserAuth.Editable = bStatus;
}

/* 사용자 이벤트 함수 */
function edUser_OnFocus(obj)
{
    btnSearchUser.SetFocus();
}

var ls_AuthDeptCd = "";
function btnSearchUser_OnClick(obj)
{
    var arrUser = gfn_UserListPopup();
    if (length(arrUser) > 0) {
        ls_AuthDeptCd = arrUser[0];
        edDeptName.Text = arrUser[1];
        edPositn.Text = arrUser[3];
        edUserId.Text = arrUser[4];
        edUserName.Text = arrUser[5];
        lfn_SearchMenuTree();
    }
}

function ds_oMenuTree_OnRowPosChanged(obj,nOldRow,nRow)
{
	lfn_Search();
}

function gdUserAuth_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
    var lobj_Dataset = object(obj.BindDataset);
	if (nCell == 6) { // 사용안함
        if (obj.GetCellProp("head", nCell, "text") == 1) { // 전체가 이미 선택된 경우
            obj.SetCellProp("head", nCell, "text", 0);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "USE_YN", "0");
            }
        } else { // 전체가 이미 선택되지 않은 경우
            obj.SetCellProp("head", nCell, "text", 1);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "USE_YN", "1");
            }
        }
	} else if (nCell == 7) { // 읽기쓰기권한
        if (obj.GetCellProp("head", nCell, "text") == 1) { // 전체가 이미 선택된 경우
            obj.SetCellProp("head", nCell, "text", 0);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "UAAUTH", "0");
            }
        } else { // 전체가 이미 선택되지 않은 경우
            obj.SetCellProp("head", nCell, "text", 1);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "UAAUTH", "1");
            }
        }
	} else if (nCell == 8) { // 읽기권한
        if (obj.GetCellProp("head", nCell, "text") == 1) { // 전체가 이미 선택된 경우
            obj.SetCellProp("head", nCell, "text", 0);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "UAAUTH", "1");
            }
        } else { // 전체가 이미 선택되지 않은 경우
            obj.SetCellProp("head", nCell, "text", 1);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "UAAUTH", "0");
            }
        }
	} else { // 헤드 정렬
        lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex);
	}
}

function btnDeleteKemsAuth_OnClick(obj)
{
	if (!gfn_FormValidate(this)) { // 입력 Validation Check
		return false;
	}

	if (chkDeleteUserAuthYn.Value == "N") { // 권한 삭제
        if (!gfn_Confirm("정말로 교육종합관리시스템 사용권한을 삭제하시겠습니까?")) {
            return false;
        }

        // 기존 사번의 권한 전체 삭제
        tit_addSingleActionInfo("sys:deleteUserKemsAuth", "", 0, "");
        // 서버 호출 
        tit_callService(
            ""
            ,""
            ,""	// inDs
            ,""	// outDs
            ,"AUTHSABUN=" + quote(edUserId.Text)
                + " AUTHDEPTCD=" + quote(ls_AuthDeptCd) // args
				+ " DAAGUBUN=" + quote(ds_ioDeptAuth.GetColumn(0, "DAAGUBUN"))	// args 
			
            ,"lfn_AfterSaveUserAuth"
            ,false
        );
	} else { // 권한 삭제 취소
        if (!gfn_Confirm("정말로 교육종합관리시스템 사용권한을 삭제를 취소 하시겠습니까?")) {
            return false;
        }

        // 기존 사번의 권한 전체 삭제
        tit_addSingleActionInfo("sys:cancelDeleteUserKemsAuth", "", 0, "");
        // 서버 호출 
        tit_callService(
            ""
            ,""
            ,""	// inDs
            ,""	// outDs
            ,"AUTHSABUN=" + quote(edUserId.Text)
                + " AUTHDEPTCD=" + quote(ls_AuthDeptCd) // args
                
				+ " DAAGUBUN=" + quote(ds_ioDeptAuth.GetColumn(0, "DAAGUBUN"))	// args                 
            ,"lfn_AfterSaveUserAuth"
            ,false
        );	
	}
}
