Window.Script:
/* * 프로그램명 : frmsys0027PUserAuthList.xml * 설명 : 개인사용자권한리스트 * 작성일 : 2010.07.13 * 작성자 : 정현호 */

#include "LIB::sys.js"; // 공통업무 관련
/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
    // 폼 환경설정    ls_SystemGubun = gs_SystemGubun;    ls_SystemMenuId = gs_SystemMenuId;    var ls_FormId = split(sFormUrl, "::");    ls_FormId = split(ls_FormId[1], ".");    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";	
	lbTitle.Value = "개인별 메뉴권한 현황 ("+sPNM+")";  // 사용자 이름
	
    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정        lfn_SetFormAuth(true); // 저장 권한    } else if (ms_FormAuth == "R") {        lfn_SetFormAuth(false); // 읽기 권한    } else {        gfn_Alert(MSGI00080);        close();    }
	// 조회
	lfn_Search();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리}

function Form_OnSize(obj,nCx,nCy,nState){    if (gs_IsResizingForm == "Y") {        tvMenu.Height = nCy - tvMenu.Top - 4;        gdUserAuth.Height = nCy - gdUserAuth.Top - 4;        gdUserAuth.Width = nCx - 324 - 4;    }}

/*
 * 기능 : 권한 조회
 */
function lfn_Search(obj) {	if (!gfn_FormValidate(this)) { // 입력 Validation Check		return false;	}    ds_ioUserAuthList.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
	tit_addSearchActionInfo("sys:search0027UserAuthList", false);
	// 서버 호출 	tit_callService(		""		,""		,""	// inDs		,"ds_ioUserAuthList=ds_ioUserAuthList"	// outDs		,"UAGTMGNO=" + quote(sUAGTMGNO)            + " UAPSABUN=" + quote(sUAPSABUN)			+ " DAAGUBUN=" + quote(sDAAGUBUN) 	// args 
		,"lfn_AfterSearchUserAuth"		,false	);
}

/* * 기능 : 권한 조회 후 처리해야 할 내용 * parameter : nErrorCode - 에러코드 *             strErrorMsg - 에러메시지 * Return : 없음 */
function lfn_AfterSearchUserAuth(nErrorCode, strErrorMsg) {    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.        gfn_ErrorMsg(nErrorCode, strErrorMsg);        return;    }    gfn_SearchMessage(gdUserAuth); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

function lfn_Print(obj)
{
lbTitle.Value	
}

/***   기능 : 출력   ***/
function lfn_Print(obj){

    if (ds_ioUserAuthList.GetCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }

    var rpt = "frmSys0027R02UserAuthList.rex";
    var params = "sTitle=" + lbTitle.Value;
    var params2 = "";
    var sTopMargin = "100";
    var sLeftMargin = "50";
		
   
	/*
	 * 기능 : 렉스퍼트 보고서를 팝업화면으로 호출한다.
	 * parameter : sReportName - 레포트명(예:레포트.rex)
	 *             sReportDataset - 데이터셋명(예:dsReport1||dsResport2||...)
	 *             sReportParams - 렉스퍼트용 파라메터(예: a에 1, b에 2, c에 3의 값을 넣는 경우, a=1^b=2^c=3...)
	 *             bLcsYn - 수첩여부
	 *             LeftMargin = 왼쪽
	 *             RightMargin = 오른쪽
	 *             TopMargin = 상단
	 *             BottomMargin = 하단
	 * return value : 정상출력 true, 출력 안했으면 false
	 */    
	 
    //gfn_CallReportPopup(rpt, "ds_oDATA", params);
    gfn_CallReportPopup(rpt, "ds_ioUserAuthList", params, '', '', sTopMargin, sLeftMargin);	
}
