/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    
    gfn_Form_OnLoadCompletedCheck(obj); //공통 폼 로딩 이벤트 처리(공통코드 조회되었는지 확인)
    
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
    
    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.


	ds_oSido.Copy("gds_oFireBonbuCode");	// 시도코드 로딩
	ds_oSido.InsertRow(0);
	ds_oSido.SetColumn(0, "CD", "");
	ds_oSido.SetColumn(0, "DATA", "선택");	
    ds_oSido.row = 0;
    
    // 2014.05.13 추가 : 주소검색시는 경기2본부 제외
	for(var i=0; i<ds_oSido.GetRowCount(); i++) {
		if (ds_oSido.GetColumn(i-1, "DATA") == "경기2") {
			ds_oSido.DeleteRow(i-1);
			break;
		}
	}
        
    //trace(ds_oSido.SaveXML());
    
    
    //alert(sBonbu);
    // 시도코드 로그인 시도로 세팅
	if (sJibu == "1141") {
		TabAddr.tabPost.cbxSido.Index = 9;	// 경기
		TabAddr.tabRoad.cbxSido2.Index = 9;	// 경기	
	} else if (sJibu == "1031" || sJibu == "1051" || sJibu == "1061") {	// 대구경북, 광주전남, 대전충남
		if (!gfn_IsNull(sBonbu)) {	// 소방본부코드 파라미터가 넘어왔으면 소방본부코드로 시도 세팅
			for(var i=0; i<ds_oSido.GetRowCount(); i++) {
				if (ds_oSido.GetColumn(i-1, "CD") == sBonbu) {
					break;
				}
				TabAddr.tabPost.cbxSido.Index = i;
				TabAddr.tabRoad.cbxSido2.Index = i;
			}
		} else {					// 소방본부코드가 없으면
			for(var i=0; i<ds_oSido.GetRowCount(); i++) {
				if (ds_oSido.GetColumn(i-1, "JIBUCD") == sJibu) {
					break;
				}
				TabAddr.tabPost.cbxSido.Index = i;
				TabAddr.tabRoad.cbxSido2.Index = i;
			}
		}
		
	} else {
		for(var i=0; i<ds_oSido.GetRowCount(); i++) {
			if (ds_oSido.GetColumn(i-1, "JIBUCD") == sJibu) {
				break;
			}
			TabAddr.tabPost.cbxSido.Index = i;
			TabAddr.tabRoad.cbxSido2.Index = i;
		}
	}
    
    if (sAddrGubun == "0") {	// 구주소 선택하여 폼 로딩
   		TabAddr.TabIndex = 1;    // 지번주소 default
		TabAddr.tabPost.cbxSido.SetFocus();
		ds_oPostCode.ClearData();
		ds_oRoadAddr.ClearData();
		ds_oPostCode.InsertRow(0);
		if (sPostAddr1 <> "") {
		
			ds_oPostCode.SetColumn(0, "PPOSTCD", sPostCD11);
			ds_oPostCode.SetColumn(0, "PFULLADDR", sPostAddr1);
			ds_oPostCode.SetColumn(0, "PADDR1", sPostAddr1);
			ds_oPostCode.SetColumn(0, "PSAN", sPostMountain);
			ds_oPostCode.SetColumn(0, "PBUILDNM", sPostBuildNM);
			TabAddr.tabPost.medPostCode11.Text = sPostCD11;
			TabAddr.tabPost.edPostApplyAddr1.Text = sPostAddr1;
			TabAddr.tabPost.edPostApplyAddr2.Text = sPostAddr2;
			TabAddr.tabPost.edPostMainBunji.Text = sPostMainBunji;
			TabAddr.tabPost.edPostSubBunji.Text = sPostSubBunji;
			TabAddr.tabPost.edPostDetailAddr.Text = sPostDetailAddr;
			TabAddr.tabPost.medPostCode12.Text = sPostCD12;
			TabAddr.tabPost.edRoadApplyAddr11.Text = sRoadAddr11;
			TabAddr.tabPost.edRoadApplyAddr12.Text = sRoadAddr12;
			sTabPost_BuildMgno = sRoadBuildMGNO1;		//건물관리번호
			TabAddr.tabPost.edRoadDetailAddr1.Text = sRoadDetailAddr1;
			TabAddr.tabPost.edRoadEtcAddr1.Text = sRoadEtcAddr1;

			ds_iPostAddrDivide.ClearData();
			ds_iPostAddrDivide.InsertRow(0);
			ds_iPostAddrDivide.SetColumn(0, "ADDR1", sPostAddr1);
			ds_iPostAddrDivide.SetColumn(0, "ADDR2", sPostAddr2);
			ds_iPostAddrDivide.SetColumn(0, "ZIPCD", sPostCD11);
			tit_clearActionInfo();
			tit_addSearchActionInfo("sys:searchPostAddrDivide", false);
			// 서버 호출 
			tit_callService(
				""
				,""
				,"ds_iPostAddrDivide=ds_iPostAddrDivide"	// inDs
				,"ds_oPostAddrDivide=ds_oPostAddrDivide"	// outDs
				,"" 	// args 
				,""
				,false
				,false
				,true
			);
			TabAddr.tabPost.edDong.Text = ds_oPostAddrDivide.GetColumn(0, "ODONG");
			TabAddr.tabPost.ChkPostMountain.Value = iif(ds_oPostAddrDivide.GetColumn(0, "OSAN")="산","1","0");
			TabAddr.tabPost.edPostMainBunji.Text = ds_oPostAddrDivide.GetColumn(0, "OMAINBUNJI");
			TabAddr.tabPost.edPostSubBunji.Text = ds_oPostAddrDivide.GetColumn(0, "OSUBBUNJI");		


			if (sRoadBuildMGNO1 <> "") {
				ds_iSearch.ClearData();
				ds_iSearch.InsertRow(0);
				ds_iSearch.SetColumn(0, "BUILDMGNO", sRoadBuildMGNO1);
				//ds_iSearch.SetColumn(0, "SIDO", TabAddr.tabPost.cbxSido.Value);
				tit_clearActionInfo();
				tit_addSearchActionInfo("sys:searchFromPostToRoadList", false);
				// 서버 호출 
				tit_callService(
					""
					,""
					,"ds_iSearch=ds_iSearch"	// inDs
					,"ds_oRoadAddr=ds_oRoadAddr"	// outDs
					,"" 	// args 
					,"lfn_AfterPostCodeSearch"
					,false
					,false
					,true
				);
			}	

		} else {
			lfn_Cancel(obj);
		}
	} else {					// 새주소 선택하여 폼 로딩
   		TabAddr.TabIndex = 0;    // 도로명주소 default	
		TabAddr.tabRoad.cbxSido2.SetFocus();
		ds_oRoadAddr.ClearData();
		ds_oRoadAddr.InsertRow(0);

		if (sRoadAddr21 <> "") {
			ds_oRoadAddr.SetColumn(0, "RMZIPCD", sPostCD2);	// ds_oRoadAddr 우편번호
			TabAddr.tabRoad.medPostCode2.Text = sPostCD2;
			TabAddr.tabRoad.edRoadApplyAddr21.Text = sRoadAddr21;
			TabAddr.tabRoad.edRoadApplyAddr22.Text = sRoadAddr22;
			sTabRoad_BuildMgno = sRoadBuildMGNO2;		//건물관리번호
			TabAddr.tabRoad.edRoadDetailAddr2.Text = sRoadDetailAddr2;
			TabAddr.tabRoad.edRoadEtcAddr2.Text = sRoadEtcAddr2;

			if (sRoadBuildMGNO2 <> "") {
				ds_iSearch.ClearData();
				ds_iSearch.InsertRow(0);
				ds_iSearch.SetColumn(0, "BUILDMGNO", sRoadBuildMGNO2);
				//ds_iSearch.SetColumn(0, "SIDO", TabAddr.tabRoad.cbxSido2.Value);
				tit_clearActionInfo();
				tit_addSearchActionInfo("sys:searchRoadList", false);
				// 서버 호출 
				tit_callService(
					""
					,""
					,"ds_iSearch=ds_iSearch"	// inDs
					,"ds_oRoadList=ds_oRoadList"	// outDs
					,"" 	// args 
					,"lfn_AfterRoadSearch"
					,false
					,false
					,true
				);
			}	
			
		} else {
			lfn_Cancel(obj);
		}
	}
     


}

function Form_OnUnloadCompleted(obj) {
    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 115 : // 저장(F4)
			btnSave_OnClick(obj);
            break;
        case 121 : // 초기화(F10)
			lfn_Cancel(obj);
            break;
        case 123 : // 화면출력(F12)
			PrintScreen(true);
            break;
    }
}


/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}


/* * 기능 : 초기화(F10) */
function lfn_Cancel(obj) {	gfn_FormInit(this.TabAddr.tabRoad);
	
	gfn_FormInit(this.TabAddr.tabPost);
	
	lfn_ClearData();
}

/* * 기능 : 초기화 (모든 데이터셋) */function lfn_ClearData() {

	ds_oPostCode.ClearData();	// 지번주소 조회 : 지번 우편번호정보 초기화
	
	ds_oRoadAddr.ClearData();	// 지번주소 조회 : 도로명 주소 정보 초기화
	
	ds_oRoadList.ClearData();	// 도로명주소 조회 : 도로명 주소 정보 초기화

}




/* 공통 필수 종료 */

/* 우편번호 함수 */

function lfn_PostCodeSearch(obj)
{
    ds_iSearch.ClearData();
    ds_iSearch.InsertRow(0);
	ds_iSearch.SetColumn(0, "SIDO", TabAddr.tabPost.cbxSido.Value);
	ds_iSearch.SetColumn(0, "DONG", TabAddr.tabPost.edDong.Text);
		
	if (TabAddr.tabPost.cbxSido.index == 0) {
        gfn_Alert("지역을 선택해주세요!");
        return false;
    } else if (TabAddr.tabPost.edDong.Text == "") {
        gfn_Alert("동명을 입력해주세요!");
        TabAddr.tabPost.edDong.SetFocus();
        return false;
	} else {
        tit_clearActionInfo();
        tit_addSearchActionInfo("sys:searchPostCode", false);
        // 서버 호출 
        tit_callService(
            ""
            ,""
            ,"ds_iSearch=ds_iSearch"	// inDs
            ,"ds_oPostCode=ds_oPostCode"	// outDs
            ,"" 	// args 
            ,"lfn_AfterPostCodeSearch"
            ,false
            ,false
            ,true
        );
	}
    
}


/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterPostCodeSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    if (ds_oPostCode.GetCount() > 0) {
        TabAddr.tabPost.gdPost.SetFocus();
    }

    gfn_SearchMessage(TabAddr.tabPost.gdPost); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 우편번호 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

/* 지번주소 검색*/
function btnPostCodeSearch_OnClick(obj)
{
	lfn_PostCodeSearch(obj);
}

function TabAddr_tabPost_edDong_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
