        TabAddr.tabPost.btnPostCodeSearch.SetFocus();
        lfn_PostCodeSearch(obj);
	}
}

/* 지번주소 적용 */
function TabAddr_tabPost_btnPostApply_OnClick(obj)
{

	/* 우편번호 */ 
	TabAddr.tabPost.medPostCode11.Text = ds_oPostCode.GetColumn(ds_oPostCode.currow, "PPOSTCD");

	/* 주소1 */
	TabAddr.tabPost.edPostApplyAddr1.Text = TabAddr.tabPost.edPostAddr1.Text;
	
	var vAddr2 = ""; // 주소2
	
	/* 산 */
	var vMountain = "";
	if (TabAddr.tabPost.ChkPostMountain.Value == "1") vMountain = "산 ";
	else vMountain = "";
	
	vAddr2 = vMountain;
	
	/* 주번지 */
	vPostMainBunji = "";
	if (TabAddr.tabPost.edPostMainBunji.Text == "") vPostMainBunji = "";
	else vPostMainBunji = TabAddr.tabPost.edPostMainBunji.Text;
	vAddr2 += vPostMainBunji;
	
	/* 부번지 */
	vPostSubBunji = "";
	if (TabAddr.tabPost.edPostSubBunji.Text == "" or TabAddr.tabPost.edPostSubBunji.Text == "0") vPostSubBunji = "";
	else vPostSubBunji = "-" + TabAddr.tabPost.edPostSubBunji.Text;
	vAddr2 += vPostSubBunji;
	
	/* 건물명,아파트*/
	vPostBuildNM = "";
	if (TabAddr.tabPost.edPostBuildNM.Text == "") vPostBuildNM = "";
	else vPostBuildNM = TabAddr.tabPost.edPostBuildNM.Text;
	
	if (vAddr2 == "") {
		vAddr2 += vPostBuildNM;
	} else {
		if (vPostBuildNM <> "") vAddr2 += " " + vPostBuildNM;
	}
	
	/* 상세주소*/
	vPostDetailAddr = "";
	if (TabAddr.tabPost.edPostDetailAddr.Text == "") vPostDetailAddr = "";
	else vPostDetailAddr = TabAddr.tabPost.edPostDetailAddr.Text;
		
	if (vAddr2 == "") {
		vAddr2 += vPostDetailAddr;
	} else {
		if (vPostDetailAddr <> "") vAddr2 += " " + vPostDetailAddr;
	}
	
	/* 주소2 */
	TabAddr.tabPost.edPostApplyAddr2.Text = vAddr2;
	
	// 지번주소 => 도로명주소 검색
	TabAddr_tabPost_btnRoadSearch_OnClick(obj);
}

/* 지번주소 적용취소 */
function TabAddr_tabPost_btnPostApplyCancel_OnClick(obj)
{

	TabAddr.tabPost.medPostCode11.Text = "";
	TabAddr.tabPost.edPostApplyAddr1.Text = "";
	TabAddr.tabPost.edPostApplyAddr2.Text = "";
	
}

/* 지번주소 적용주소 직접입력*/
function TabAddr_tabPost_chkPostInput_OnClick(obj,strValue)
{
	if (TabAddr.tabPost.chkPostInput.Value = "1") {
		TabAddr.tabPost.medPostCode11.Readonly = false;
		TabAddr.tabPost.edPostApplyAddr1.Readonly = false;
		TabAddr.tabPost.edPostApplyAddr2.Readonly = false;
	} else {
		TabAddr.tabPost.medPostCode11.Readonly = true;
		TabAddr.tabPost.edPostApplyAddr1.Readonly = true;
		TabAddr.tabPost.edPostApplyAddr2.Readonly = true;
	}
}

/* 지번주소 조회 : 부가주소에서 엔터키 입력시 지번주소 적용*/
function TabAddr_tabPost_edPostDetailAddr_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) { // Enter key        TabAddr.tabPost.btnPostApply.SetFocus();
        TabAddr_tabPost_btnPostApply_OnClick(obj);	}	
}

/* 지번주소 조회 : 도로명주소 적용 */
function TabAddr_tabPost_btnRoadApply_OnClick(obj)
{

	/* 우편번호 */ 
	TabAddr.tabPost.medPostCode12.Text = ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow, "RMZIPCD");
	
	/* 주소1 */
	TabAddr.tabPost.edRoadApplyAddr11.Text = ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMADDR1");
	
	var vRoadDetailAddr1 = "";
	if (TabAddr.tabPost.edRoadDetailAddr1.Text == "") vRoadDetailAddr1 = "";
	else vRoadDetailAddr1 = TabAddr.tabPost.edRoadDetailAddr1.Text;
	
	var vRoadEtcAddr1 = "";
/*	if (TabAddr.tabPost.edRoadEtcAddr1.Text == "") {
		vRoadEtcAddr1 = ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMADDR2");
	} else {
		vRoadEtcAddr1 = substr(ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMADDR2"), 0, length(ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMADDR2"))-1)
	                    + ", "+TabAddr.tabPost.edRoadEtcAddr1.Text + ")";
	}
*/	
	if (gfn_IsNull(ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMLAWRINM"))) {	// 리가 없으면
		if (TabAddr.tabPost.edRoadEtcAddr1.Text <> "") {	// 부가주소를 입력했으면
			vRoadEtcAddr1 = "(" + ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMLAWDONGNM")
			                    + " " + ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMMAINBUNJI")
			                    + iif(ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMSUBBUNJI")=="0","", 
			                          "-" + ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMSUBBUNJI"))
			                + ", " + TabAddr.tabPost.edRoadEtcAddr1.Text + ")";
		} else {	// 부가주소를 입력하지 않았으면
			vRoadEtcAddr1 = "(" + ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMLAWDONGNM") 
							    + " " + ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMMAINBUNJI")
							    + iif(ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMSUBBUNJI")=="0","", 
								      "-" + ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMSUBBUNJI")) + ")";
		}
	
	} else {	// 리가 있으면
		if (TabAddr.tabPost.edRoadEtcAddr1.Text <> "") {	// 부가주소를 입력했으면
			vRoadEtcAddr1 = "(" + ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMLAWRINM")
			                    + " " + ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMMAINBUNJI")
			                    + iif(ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMSUBBUNJI")=="0","", 
			                          "-" + ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMSUBBUNJI"))
			                 + ", " + TabAddr.tabPost.edRoadEtcAddr1.Text + ")";
		} else {	// 부가주소를 입력하지 않았으면	
			vRoadEtcAddr1 = "(" + ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMLAWRINM")
								+ " " + ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMMAINBUNJI")
			                    + iif(ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMSUBBUNJI")=="0","", 
			                          "-" + ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMSUBBUNJI")) + ")";
		}
	}
	

	/* 주소2 */
	if (vRoadDetailAddr1 == "") {
		TabAddr.tabPost.edRoadApplyAddr12.Text = vRoadDetailAddr1 + vRoadEtcAddr1;
	} else {
		TabAddr.tabPost.edRoadApplyAddr12.Text = vRoadDetailAddr1 + iif(gfn_IsNull(vRoadEtcAddr1),"", " " + vRoadEtcAddr1);
	}

}

