// 도로명주소 조회 : 도로명주소 조회
function lfn_RoadSearch(obj)
{
    ds_iSearch.ClearData();
    ds_iSearch.InsertRow(0);
	ds_iSearch.SetColumn(0, "SIDO", TabAddr.tabRoad.cbxSido2.Value);
	ds_iSearch.SetColumn(0, "ROADNM", TabAddr.tabRoad.edRoad.Text);
	ds_iSearch.SetColumn(0, "BUILDMAINNO", TabAddr.tabRoad.edRoadBuildMainNo.Text);
	ds_iSearch.SetColumn(0, "BUILDSUBNO", TabAddr.tabRoad.edRoadBuildSubNo.Text);
		
	if (TabAddr.tabRoad.cbxSido2.index == 0) {
        gfn_Alert("지역을 선택해주세요!");
        return false;
    } else if (TabAddr.tabRoad.edRoad.Text == "") {
        gfn_Alert("도로명을 입력해주세요!");
        TabAddr.tabRoad.edRoad.SetFocus();
        return false;
	} else {
        tit_clearActionInfo();
        tit_addCsvSearchActionInfo("sys:searchRoadList", false);
        // 서버 호출 
        tit_callService(
            ""
            ,""
            ,"ds_iSearch=ds_iSearch"	// inDs
            ,"ds_oRoadList=ds_oRoadList"	// outDs
            ,"" 	// args 
            ,"lfn_AfterRoadSearch"
            ,false
            ,false
            ,true
        );
	}	
}

/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterRoadSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    if (ds_oRoadList.GetCount() > 0) {
        TabAddr.tabRoad.gdRoad2.SetFocus();
        
		if ((ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMBUILDNM") = "주택") or
			(ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMBUILDNM") = "단독주택") or
			(ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMBUILDNM") = "창고") or
			(ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMBUILDNM") = "축사")) {
			TabAddr.tabRoad.edRoadDetailAddr2.Text = "";
		} else {
			TabAddr.tabRoad.edRoadEtcAddr2.Text = ds_oRoadList.GetColumn(ds_oRoadList.currow, "RMBUILDNM");
		}
    }

    gfn_SearchMessage(TabAddr.tabRoad.gdRoad2); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

function TabAddr_tabRoad_edRoad_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) { // Enter key
        lfn_RoadSearch(obj);
        
        if (TabAddr.tabRoad.edRoad.Text <> "") {
			
			TabAddr.tabRoad.edRoadBuildMainNo.SetFocus();
		
		}
	}
	
}

function TabAddr_tabRoad_edRoadBuildSubNo_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) { // Enter key
        TabAddr.tabRoad.btnRoadSearch2.SetFocus();

        lfn_RoadSearch(obj);
	}	
}

/* 도로명주소 조회 : 도로명주소 적용 */ 
function TabAddr_tabRoad_btnRoadApply_OnClick(obj)
{
	/* 우편번호 */ 
	TabAddr.tabRoad.medPostCode2.Text = ds_oRoadList.GetColumn(ds_oRoadList.currow, "RMZIPCD");

	/* 주소1 */
	TabAddr.tabRoad.edRoadApplyAddr21.Text = ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMADDR1");
	
	var vRoadDetailAddr2 = "";
	if (TabAddr.tabRoad.edRoadDetailAddr2.Text == "") vRoadDetailAddr2 = "";
	else vRoadDetailAddr2 = TabAddr.tabRoad.edRoadDetailAddr2.Text;
	
	var vRoadEtcAddr2 = "";
/*	if (TabAddr.tabRoad.edRoadEtcAddr2.Text == "") {
		vRoadEtcAddr2 = ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMADDR2");
	} else {
		vRoadEtcAddr2 = substr(ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMADDR2"), 0, length(ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMADDR2"))-1)
	                    + ", "+TabAddr.tabRoad.edRoadEtcAddr2.Text + ")";
	}
*/	
	if (gfn_IsNull(ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMLAWRINM"))) {	// 리가 없으면
		if (TabAddr.tabRoad.edRoadEtcAddr2.Text <> "") {	// 부가주소를 입력했으면
			//vRoadEtcAddr2 = "(" + ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMLAWDONGNM")
			//                + ", " + TabAddr.tabRoad.edRoadEtcAddr2.Text + ")";
			vRoadEtcAddr2 = "(" + ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMLAWDONGNM")
			                    + " " + ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMMAINBUNJI")
			                    + iif(ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMSUBBUNJI")=="0","", 
			                          "-" + ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMSUBBUNJI"))			                    
			                + ", " + TabAddr.tabRoad.edRoadEtcAddr2.Text + ")";
			                
		} else {	// 부가주소를 입력하지 않았으면
			//vRoadEtcAddr2 = "(" + ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMLAWDONGNM") + ")";
			vRoadEtcAddr2 = "(" + ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMLAWDONGNM")
							    + " " + ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMMAINBUNJI")
							    + iif(ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMSUBBUNJI")=="0","", 
								      "-" + ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMSUBBUNJI")) + ")";
		}
	
	} else {	// 리가 있으면
		if (TabAddr.tabRoad.edRoadEtcAddr2.Text <> "") {	// 부가주소를 입력했으면
			//vRoadEtcAddr2 = "(" + TabAddr.tabRoad.edRoadEtcAddr2.Text + ")";
			vRoadEtcAddr2 = "(" + ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMLAWRINM")
			                    + " " + ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMMAINBUNJI")
			                    + iif(ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMSUBBUNJI")=="0","", 
			                          "-" + ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMSUBBUNJI"))
			                 + ", " + TabAddr.tabRoad.edRoadEtcAddr2.Text + ")";
		} else {	// 부가주소를 입력하지 않았으면	
			//vRoadEtcAddr2 = "(" + ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMLAWRINM") + ")";
			vRoadEtcAddr2 = "(" + ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMLAWRINM")
								+ " " + ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMMAINBUNJI")
			                    + iif(ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMSUBBUNJI")=="0","", 
			                          "-" + ds_oRoadList.GetColumn(ds_oRoadList.currow,"RMSUBBUNJI")) + ")";

		}
	}
	
	/* 주소2 */
	if (vRoadDetailAddr2 == "") {
		TabAddr.tabRoad.edRoadApplyAddr22.Text = vRoadDetailAddr2 + vRoadEtcAddr2;
	} else {
		TabAddr.tabRoad.edRoadApplyAddr22.Text = vRoadDetailAddr2 + iif(gfn_IsNull(vRoadEtcAddr2),"", " " + vRoadEtcAddr2);
	}
}

/* 도로명주소 조회 : 도로명주소 적용취소 */
function TabAddr_tabRoad_btnRoadApplyCancel_OnClick(obj)
{
	TabAddr.tabRoad.medPostCode2.Text = "";
	TabAddr.tabRoad.edRoadApplyAddr21.Text = "";
	TabAddr.tabRoad.edRoadApplyAddr22.Text = "";	
}

/* 도로명주소 조회 : 도로명주소 적용주소 직접입력*/
function TabAddr_tabRoad_chkRoadInput_OnClick(obj,strValue)
{
	if (TabAddr.tabRoad.chkRoadInput.Value = "1") {
		TabAddr.tabRoad.medPostCode2.Readonly = false;
		TabAddr.tabRoad.edRoadApplyAddr21.Readonly = false;
		TabAddr.tabRoad.edRoadApplyAddr22.Readonly = false;
	} else {
		TabAddr.tabRoad.medPostCode2.Readonly = true;
		TabAddr.tabRoad.edRoadApplyAddr21.Readonly = true;
		TabAddr.tabRoad.edRoadApplyAddr22.Readonly = true;
	}
}

/* 도로명 주소 조회 : 부가주소에서 엔터키 입력시 적용버튼 클릭*/
