        TabAddr_tabRoad_btnRoadApply_OnClick(obj);	}	
}



/* 적용주소 반영 */
function btnSave_OnClick(obj)
{
	// 지번주소 조회 : 지번주소 and 도로명주소
	if (TabAddr.TabIndex == 1) {
	
