	} else {	// 도로명주소 조회 : 도로명주소
	
		if (TabAddr.tabRoad.edRoadApplyAddr21.Text == "") {

//			gfn_Alert("적용주소가 입력되지 않았습니다.\n\n적용버튼을 눌러 적용주소를 입력해 주세요!");

			if(gfn_Confirm("적용주소가 입력되지 않았습니다.\n\n그래도 반영하시겠습니까?")) {
			
