/* 지번주소 조회 : 도로명주소 검색 */
function TabAddr_tabPost_btnRoadSearch_OnClick(obj)
{
    
    ds_iSearch.ClearData();
    ds_iSearch.InsertRow(0);
	
	if (gfn_IsNull(ds_oPostCode.GetColumn(ds_oPostCode.currow, "PSIDO"))) {
		ds_iSearch.SetColumn(0, "SIDO", TabAddr.tabPost.cbxSido.Value);
	} else {
		ds_iSearch.SetColumn(0, "SIDO", ds_oPostCode.GetColumn(ds_oPostCode.currow, "PSIDO")); //TabAddr.tabPost.cbxSido.Value);
	}
	
	if (gfn_IsNull(ds_oPostCode.GetColumn(ds_oPostCode.currow, "PDONG"))) {
		ds_iSearch.SetColumn(0, "DONG", TabAddr.tabPost.edDong.Text);
	} else {
		ds_iSearch.SetColumn(0, "DONG", ds_oPostCode.GetColumn(ds_oPostCode.currow, "PDONG"));
	}
	ds_iSearch.SetColumn(0, "MNTNGUBUN", TabAddr.tabPost.ChkPostMountain.Value);
	ds_iSearch.SetColumn(0, "MAINBUNJI", TabAddr.tabPost.edPostMainBunji.Text);
	ds_iSearch.SetColumn(0, "SUBBUNJI", TabAddr.tabPost.edPostSubBunji.Text);
	ds_iSearch.SetColumn(0, "ZIPCD", TabAddr.tabPost.medPostCode11.Value);
	
	if (TabAddr.tabPost.edPostMainBunji.Text == "" && TabAddr.tabPost.edPostSubBunji.Text == ""
	   && TabAddr.tabPost.edPostBuildNM.Text <> "")
		ds_iSearch.SetColumn(0, "BUILDNM", TabAddr.tabPost.edPostBuildNM.Text);
	
	
	if (TabAddr.tabPost.cbxSido.index == 0 && gfn_IsNull(ds_oPostCode.GetColumn(ds_oPostCode.currow, "PSIDO"))) {
        gfn_Alert("지역을 선택해주세요!");
        return false;
    }
    if (gfn_IsNull(TabAddr.tabPost.edDong.Text) && gfn_IsNull(ds_oPostCode.GetColumn(ds_oPostCode.currow, "PDONG"))) {
        gfn_Alert("동명을 입력해주세요!");
        TabAddr.tabPost.edDong.SetFocus();
        return false;
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("sys:searchFromPostToRoadList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iSearch=ds_iSearch"	// inDs
		,"ds_oRoadAddr=ds_oRoadAddr"	// outDs
		,"" 	// args 
		,"lfn_AfterPostRoadSearch"
		,false
		,false
		,true
	);
}

/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterPostRoadSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    if (ds_oRoadAddr.GetCount() > 0) {
    
		if ((ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow ,"RMBUILDNM") = "주택") or
			(ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMBUILDNM") = "단독주택") or
			(ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMBUILDNM") = "창고") or
			(ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow,"RMBUILDNM") = "축사")) {
			TabAddr.tabPost.edRoadDetailAddr1.Text = "";
		} else {
			//TabAddr.tabPost.edRoadDetailAddr1.Text = ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow, "RMBUILDNM")
			//										 + iif(gfn_IsNull(ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow, "RMBUILDNM")),"",iif(gfn_IsNull(TabAddr.tabPost.edPostDetailAddr.Text),""," "))
			//										 + TabAddr.tabPost.edPostDetailAddr.Text;
			TabAddr.tabPost.edRoadDetailAddr1.Text = TabAddr.tabPost.edPostDetailAddr.Text;
			TabAddr.tabPost.edRoadEtcAddr1.Text = ds_oRoadAddr.GetColumn(ds_oRoadAddr.currow, "RMBUILDNM");
		}    
        TabAddr.tabPost.gdRoad1.SetFocus();
    }

    gfn_SearchMessage(TabAddr.tabPost.gdRoad1); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

