var ls_AuthDeptCd = "";
function btnSearchUser_OnClick(obj)
{
    var arrUser = gfn_UserListPopup();
    if (length(arrUser) > 0) {
        ls_AuthDeptCd = arrUser[0];
        edDeptName.Text = arrUser[1];
        edPositn.Text = arrUser[3];
        edUserId.Text = arrUser[4];
        edUserName.Text = arrUser[5];
    }
}


/*
 * 기능 : 부서변경시 해당 부서인원 조회
*/
function cbxDeptList_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_UserSearch(strCode);
}

function edObjNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
		lfn_Search();
	}
}

