function lfn_Excel(obj) {
    if (ds_ioKFSAmBizUser.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {

    var ls_SearchGubun = "";

    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }

    ds_ioKFSAmBizUser.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("sys:search0100KFSAmBizUser", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioKFSAmBizUser=ds_oKFSAmBizUser"	// outDs
		,"GTMGNO=" + quote(cbxJibu.Value) // args 
		,"lfn_AfterSearch"
		,false

	);
	//alert(ds_ioRegionManager.GetRowCount());
}
function lfn_AfterSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }	medTotalCnt.Value = ds_ioKFSAmBizUser.GetRowCount();
    gfn_SearchMessage(gdAppManager); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

