}



function lfn_Save(obj)
{
	if (ds_ioKFSAmBizUser.GetCount() == 0) {
	
		gfn_Alert("변경할 내역이 없습니다.");
		return;
	
	}
	
	if(!gfn_Confirm("변경내역을 저장하시겠습니까?")){
		return;
	}
	
	// dataset  
    for(var i=0; i<ds_ioKFSAmBizUser.GetRowCount(); i++) {

		if (!gfn_IsNull(ds_ioKFSAmBizUser.GetColumn(i, "KSABUN"))) {

			tit_clearActionInfo();

			iArgsNm = "SABUN=" + quote(ds_ioKFSAmBizUser.GetColumn(i, "KSABUN"))
			          + " GTMGNO=" + quote(ds_ioKFSAmBizUser.GetColumn(i, "GTMGNO"))
			          + " USEGUBUN=" + quote(ds_ioKFSAmBizUser.GetColumn(i, "KUSEGUBUN"))
			          + " HPNO=" + quote(ds_ioKFSAmBizUser.GetColumn(i, "KHPNO"));
			
			tit_addSingleActionInfo("sys:update0100KFSAmBizUser", "", 0, "");  // 저장
			
			// 서버 호출 
			tit_callService(
				""
				,""
				,""		// inDs
				,""      // outDs
				,iArgsNm	// args
				,"lfn_AfterUpdate"
				,""
				,""
				,true
			);			
		
		}
	} // for
	
	lfn_Search();
}


function lfn_AfterUpdate(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
	
}

function lfn_Cancel(obj)
{
	ds_ioKFSAmBizUser.Clear();
}

function cbxJibu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_Search();
}
