#include "LIB::training.js"; // 공통업무 관련


/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {


    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;

    var ls_FormId = split(sFormUrl, "::");
		ls_FormId = split(ls_FormId[1], ".");

    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";
    gfn_MakeRequiredInputMark(this); 	 // 필수 입력란을 설정한다.
    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.

    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } 
    else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } 
    else {
        gfn_Alert(MSGI00080);
        close();
    }
    // 공통 코드 조회
    gfn_SearchCommonCode(ds_oMunjeGubun, "MUNJEGUBUN", "Y", "전체");

	// 시험직능(E) / 수첩직능(L) / 강습직능(T)
    gfn_SearchTrainingCourse("T", ds_oCourseMunje, "Y", "전체"); // 직능 코드 조회
    gfn_SearchTrainingCourse("T", ds_oCourse, "Y", "전체"); // 직능 코드 조회
    gfn_SearchJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회  

	// 강습일정관리에서 더블클릭하여 조회된 자료이면
	if (!gfn_isNull(sJibu)) {
		cbxJibu.Value = sJibu;		
		edTrainingYear.Text = sYear;
		cbxCourse.Value = sCourse;
		edTrainingOrderStart.Text = sOrder;
		edTrainingOrderEnd.Text = sOrder;
		radRepayYn.Value = sSunapGubun;
		lfn_Search(obj);		
	} 
	else {
		edTrainingYear.Text = left(GetDate(), 4);	
		cbxCourse.Value = "22";		
		edTrainingOrderStart.Text = "001";	
		edTrainingOrderEnd.Text = "001";	
		edTrainingOrderStart.SetFocus();
		//cbxCourse_OnChanged(cbxCourse, cbxCourse.Value);
	}	
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do
    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState) {
    if (gs_IsResizingForm == "Y") {
        gdTrainingJubsu.Height = nCy - gdTrainingJubsu.Top - 4;
        gdTrainingJubsu.Width = nCx - gdTrainingJubsu.Left - 4;
    }
}

/*
 * 기능 : 조회
 */
var course = "";
function lfn_Search(obj) {

    if (!gfn_FormValidate(this)) { // 조회 Validation Check
        return false;
    }
    ds_oTrainingJubsu.ClearData(); // 데이타셋 초기화    
    // 조회이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edTrainingYear.Text)
                + ",회차시작=" + quote(edTrainingOrderStart.Text)
                + ",회차종료=" + quote(edTrainingOrderEnd.Text)                
                + ",교육종료일시작=" + quote(calTrainingStart.Text)
                + ",교육종료일종료=" + quote(calTrainingEnd.Text)
                + ",환불구분=" + quote(radRepayYn.Text)
                + ",수료여부=" + quote(radPassYn.Text)                
                + ",회차변경여부=" + quote(RadLastGubun.Text)                
                + ",선후불구분=" + quote(radAmountGubun.Text)                
                + ",온라인구분=" + quote(radOnlineGubun.Text);

    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );

    course = cbxCourse.Text;  // 레포트에 직능이름 보내는 파라메터값
    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchTrainingJubsuStat", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oTrainingJubsu=ds_oTrainingJubsu"	// outDs
		,"TOMGGTMGNO=" + quote(cbxJibu.Value)
            + " TOTCCOURSECD=" + quote(cbxCourse.Value)
            + " TOYEAR=" + quote(edTrainingYear.Text)
            + " TOTRAININGORDERSTART=" + quote(edTrainingOrderStart.Text)
            + " TOTRAININGORDEREND=" + quote(edTrainingOrderEnd.Text)            
            + " TOTRAININGDATESTART=" + quote(left(calTrainingStart.Value,8))
            + " TOTRAININGDATEEND=" + quote(left(calTrainingEnd.Value,8))
            + " TJLASTSUNAPGUBUN=" + quote(radRepayYn.Value)
            + " TJPASSYN=" + quote(radPassYn.Value)
            + " TSAMOUNTGUBUN=" + quote(radLicenseYn.Value)            
            + " TOHLASTGUBUN=" + quote(RadLastGubun.Value)            
            + " TJAMOUNTGUBUN=" + quote(radAmountGubun.Value)            
            + " TOONLINEGUBUN=" + quote(radOnlineGubun.Value)// args 
		,"lfn_AfterSearchTrainingJubsu"
		,false
	);
}


/*
 * 기능 : 접수자목록출력
 */
function lfn_Print(obj) {

    if (ds_oTrainingJubsu.RowCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }   
    if(!gfn_CheckChangedGrid(this))  //선택한것이 있는지 체크 메세지 보임
    { alert("출력할 인원을 선택해 주십시오.");      
        return false;
    }    
    ds_ioReport.copy(ds_oTrainingJubsu);  // 1) 원본 복사           
    ds_ioReport.Filter("SEL = '1'"); // 2) 복사 후 필터함     
    ds_ioReportFilter.copyf(ds_ioReport);  // 3) 필터한 값을 실제 사용 위해 다른 곳에 복사     
    ds_ioReport.ClearData(); // 4) 데이타셋 초기화  레포트 출력위해 필터된 2)번의 데이터셋을 초기화함    

    // 출력이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edTrainingYear.Text)
                + ",회차시작=" + quote(edTrainingOrderStart.Text)
                + ",회차종료=" + quote(edTrainingOrderEnd.Text)
                + ",환불구분=" + quote(radRepayYn.Text)
                + ",수료여부=" + quote(radPassYn.Text)               
                + ",회차변경여부=" + quote(RadLastGubun.Text)                
                + ",선후불구분=" + quote(radAmountGubun.Text);
    gfn_InsertSearchPrintHistory(
        "P"
        , this.ID
        , "접수자목록출력"
        , ls_HistoryArg
    );

    ds_ioReportFilter.Sort("TOHJUBSUNO:A");
    // to do Validation Check
    gfn_CallReportPopup("frmtraining0030R01TrainingJubsuList.rex"
                      , "ds_ioReportFilter"
                      , "TCCOURSENM="+course);
}


