/* 공통 필수 종료 */
/* 사용자 함수 */
/*
 * 기능 : 결강자 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchTrainingJubsu(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    gfn_SearchMessage(gdTrainingJubsu); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.    
    medJubsuCnt.Value = ds_oTrainingJubsu.RowCount();
    ds_oTrainingJubsu_Filter.Copy(ds_oTrainingJubsu);
    // 수납구분이 " 수납 " 인것만 한번 더 필터를 건다
    // 영수증 출력시 수납구분이 TJ수납구분이 아닌 영수증용 결제수납여부를 판단해야 하므로 
    // 상단의 조회조건과 다른 컬럼을 사용해서 아래 해당 컬럼이 0인것만 조회하도록 설정
    ds_oTrainingJubsu_Filter.Filter("SUNAPGUBUNCD='0'"); 
    medJubsuCnt2.Value = ds_oTrainingJubsu_Filter.RowCount();
}


/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveTrainingJubsu(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    lfn_Search(); // 결강자 조회
}


