function btnFortec_OnClick(obj)  {

    if (ds_oTrainingJubsu.RowCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }   
    if(!gfn_CheckChangedGrid(this))  {    //선택한것이 있는지 체크 메세지 보임
		alert("출력할 인원을 선택해 주십시오.");      
        return false;
    }    
    ds_ioReport.copy(ds_oTrainingJubsu);  // 1) 원본 복사           
    ds_ioReport.Filter("SEL = '1'"); 	  // 2) 복사 후 필터함     
    ds_ioReportFilter.copyf(ds_ioReport); // 3) 필터한 값을 실제 사용 위해 다른 곳에 복사     
    ds_ioReport.ClearData(); 			  // 4) 데이타셋 초기화  레포트 출력위해 필터된 2)번의 데이터셋을 초기화함    

    // 출력이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edTrainingYear.Text)
                + ",회차시작=" + quote(edTrainingOrderStart.Text)
                + ",회차종료=" + quote(edTrainingOrderEnd.Text)
                + ",환불구분=" + quote(radRepayYn.Text)
                + ",수료여부=" + quote(radPassYn.Text)                
                + ",회차변경여부=" + quote(RadLastGubun.Text)                  
                + ",선후불구분=" + quote(radAmountGubun.Text);

    gfn_InsertSearchPrintHistory(
        "P"
        , this.ID
        , "Fromtec 주소출력"
        , ls_HistoryArg
    );

    ds_ioReportFilter.Sort("TOHJUBSUNO:A");
    // to do Validation Check
    gfn_CallReportPopup("frmTraining0030R06FormtecPrint.rex"
                      , "ds_ioReportFilter"
                      , "TCCOURSENM="+course);
}

function btnPrintReRct_OnClick(obj)
{
	var sPosYear = "";
	var sPosMgno = "";

	if(confirm(" 출력될 영수증은 총 " + medJubsuCnt2.Text + " 건입니다. \n 승인여부 체크 후 출력됩니다 "))
	{
		for(i=0; i<ds_oTrainingJubsu_Filter.GetRowCount(); i++)
		{
			// 승인된 건인지 체크한다.
			gds_oPrintReceiptMast.ClearData(); // 데이타셋 초기화
			tit_clearActionInfo();
			tit_addSingleActionInfo("com:searchPosprintm_pmresult", false);

			sPosYear = ds_oTrainingJubsu_Filter.GetColumn(i, "TSPMYEAR");
			sPosMgno = ds_oTrainingJubsu_Filter.GetColumn(i, "TSPMMGNO");

			// 서버 호출
			tit_callService(
				""
				,""
				,""    // inDs   
				,"gds_oPrintReceiptMast=ds_oPrintReceiptMast" // outDs
				,"POSYEAR=" + quote(sPosYear) + " POSMGNO=" + quote(sPosMgno)
				,""
				,false
				,false
				,true
			);

			if (gds_oPrintReceiptMast.GetColumn(, "PMRESULT") <> "1") {
				alert("【 " + toNumber(i+1) + " 】행의 POS 결제 정보가 미결재된 상태입니다. \n 영수증을 출력할 수 없습니다.");
				return false;
			}	
		}

		//ds_ioReport.copy(ds_oTrainingJubsu_Filter); 	// 1) 원본 복사           
		//ds_ioReport.Filter("SEL == '1'"); 				// 2) 복사 후 필터함         
		ds_ioReportFilter.copyf(ds_oTrainingJubsu_Filter);  			// 3) 필터한 값을 실제 사용 위해 다른 곳에 복사     
		//ds_ioReport.ClearData(); 						// 4) 데이타셋 초기화  레포트 출력위해 필터된 2)번의 데이터셋을 초기화
		gfn_CallReportPopup("frmtraining0020R02ReceiptReIssuePDFALL.rex", "ds_ioReportFilter", "");	
	}
}
