var flag = "";
function lfn_Search(obj)
{    
    if (!gfn_FormValidate(this)) { // 조회 Validation Check
        return false;
    }
    
    if (obj.ID == "btnPrintJubsuList") {
        flag = 1;
        // 조회이력을 저장한다.  
        ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                    + ",직능=" + quote(cbxCourse.Text)
                    + ",년도=" + quote(edTrainingYear.Text)
                    + ",회차=" + quote(edTrainingOrder.Text)
                    + ",정렬방법=" + quote(radSort.Text);
        gfn_InsertSearchPrintHistory(
            "S"
            , this.ID
            , global.mnuLeft.cbxOpenForm.Text
            , ls_HistoryArg
        );
        
        // 접수자명단출력
        ds_oTrainingJubsurRegisterPrint.ClearData(); // 데이타셋 초기화
        tit_clearActionInfo();
        tit_addCsvSearchActionInfo("training:searchTrainingJubsurRegisterList", false);
        // 서버 호출 
        tit_callService(
            ""
            ,""
            ,""	// inDs
            ,"ds_oTrainingJubsurRegisterPrint=ds_oTrainingJubsurRegisterPrint"	// outDs
            ,"TOMGGTMGNO=" + quote(cbxJibu.Value)
                + " TOTCCOURSECD=" + quote(cbxCourse.Value)
                + " TOYEAR=" + quote(edTrainingYear.Text)
                + " TOTRAININGORDER=" + quote(edTrainingOrder.Text)
                + " SORT=" + quote(radSort.Value)	// args 
            ,"lfn_AfterSearchTrainingJubsurRegisterPrint"
            ,false
        ); 
    } else if (obj.ID == "btnPrintJubsu") {
        flag = 2;
        // 조회이력을 저장한다.  
        ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                    + ",직능=" + quote(cbxCourse.Text)
                    + ",년도=" + quote(edTrainingYear.Text)
                    + ",회차=" + quote(edTrainingOrder.Text)
                    + ",정렬방법=" + quote(radSort.Text);
        gfn_InsertSearchPrintHistory(
            "S"
            , this.ID
            , global.mnuLeft.cbxOpenForm.Text
            , ls_HistoryArg
        );
        
        // 접수부출력
        ds_oTrainingJubsurRegisterPrint.ClearData(); // 데이타셋 초기화
        tit_clearActionInfo();
        tit_addCsvSearchActionInfo("training:searchTrainingJubsurRegisterPrint", false);
        // 서버 호출 
        tit_callService(
            ""
            ,""
            ,""	// inDs
            ,"ds_oTrainingJubsurRegisterPrint=ds_oTrainingJubsurRegisterPrint"	// outDs
            ,"TOMGGTMGNO=" + quote(cbxJibu.Value)
                + " TOTCCOURSECD=" + quote(cbxCourse.Value)
                + " TOYEAR=" + quote(edTrainingYear.Text)
                + " TOTRAININGORDER=" + quote(edTrainingOrder.Text)
                + " SORT=" + quote(radSort.Value)	// args 
            ,"lfn_AfterSearchTrainingJubsurRegisterPrint"
            ,false
        ); 
    } else if (obj.ID == "btnSeatNoAssign") {
        if (!gfn_Confirm("좌석 지정자를 제외하고, 환불자와 회차변경자(出)의\n\n좌석을 채워줍니다.\n\n좌석재배치를 하시겠습니까?")) {
            return false;
        }
        flag = 3;
        
        ds_oTrainingJubsurRegisterAll.ClearData(); // 데이타셋 초기화
        ds_oTrainingJubsurRegisterTemp.ClearData(); // 데이타셋 초기화

        tit_clearActionInfo();
        tit_addCsvSearchActionInfo("training:searchTrainingJubsurRegisterAll", false);
        tit_addCsvSearchActionInfo("training:searchTrainingJubsurRegisterTemp", false);
        
        // 서버 호출 
        tit_callService(
            ""
            ,""
            ,""	// inDs
            ,"ds_oTrainingJubsurRegisterAll=ds_oTrainingJubsurRegisterPrint ds_oTrainingJubsurRegisterTemp=ds_oTrainingJubsurRegisterTemp"	// outDs		  
            ,"TOMGGTMGNO=" + quote(cbxJibu.Value)
                + " TOTCCOURSECD=" + quote(cbxCourse.Value)
                + " TOYEAR=" + quote(edTrainingYear.Text)
                + " TOTRAININGORDER=" + quote(edTrainingOrder.Text)
                + " SORT=" + quote(radSort.Value)	// args 
            ,"lfn_AfterSearchTrainingJubsurRegisterPrint"
            ,false
        );
    }
}

/*
 * 기능 : 좌석현황 보기
 * parameter : 없음
 * return : 없음
 */
function lfn_SearchTrainingOrderMgno() {
    if (gfn_IsNull(cbxJibu.Value)
            || gfn_IsNull(cbxCourse.Value)
            || gfn_IsNull(edTrainingYear.Text)
            || gfn_IsNull(edTrainingOrder.Text)) {
        return false;
    }

    ds_oTrainingOrderMgno.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
    tit_addCsvSearchActionInfo("training:searchTrainingOrderMgno", false);
    // 서버 호출
    tit_callService(
        ""
        ,""
        ,""    // inDs
        ,"ds_oTrainingOrderMgno=ds_oTrainingOrderMgno"    // outDs
        ,"TOMGJIBUCD=" + quote(cbxJibu.Value)
            + " TCCOURSECD=" + quote(cbxCourse.Value+"N")
            + " TOYEAR=" + quote(edTrainingYear.Text)
            + " TOORDER=" + quote(edTrainingOrder.Text)
        ,"lfn_AfterSearchTrainingOrderMgno"
        ,false
        ,false
        ,true
    );
}

