/* 공통 필수 종료 */
/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchTrainingJubsurRegisterPrint(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
    if (flag == 1) {        
        if (ds_oTrainingJubsurRegisterPrint.RowCount() < 1) {
            gfn_Alert("출력할 데이터가 없습니다.");
            return false;
        }
        lfn_PrintName();
    } else if (flag == 2) {    
        if (ds_oTrainingJubsurRegisterPrint.RowCount() < 1) {
            gfn_Alert("출력할 데이터가 없습니다.");
            return false;
        }
        lfn_Print();
    } else if (flag == 3) {       
        if (ds_oTrainingJubsurRegisterAll.RowCount() < 1) {
            gfn_Alert("재배치할 데이터가 없습니다.");
            return false;
        }
        var row = ds_oTrainingJubsurRegisterAll.searchrow("TOHSEATCHANGEYN = 'Y'");
        ds_oTrainingJubsurRegisterAll.deleteRow(row);         
        
        lfn_ReOrder();
    }
}

// 좌석현황보기
function lfn_AfterSearchTrainingOrderMgno(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    ds_oTrainingJubsurRegisterAll.Sort("SEATNO:A"); // 데이터셋 데이터를 좌석번호순으로 재배치
    ds_oTrainingJubsurRegisterTemp.Sort("TOHDECISIONSEATNO:A");    
    
    btnSeatList_OnClick();
}

