/* 사용자 이벤트 함수 */
function lfn_ReOrder(obj)
{       
    var temp = "";    
    for (i=0;i<ds_oTrainingJubsurRegisterTemp.GetCount();i++) {
        temp = ds_oTrainingJubsurRegisterAll.GetColumn(i, "SEATNO");        
        ds_oTrainingJubsurRegisterTemp.SetColumn(i, "TOHDECISIONSEATNO", temp);
    }
    
	tit_clearActionInfo();	
    tit_addSaveActionInfo(
		""	// insert
		, "training:updateTrainingJubsurRegisterPrint"	// update
		, ""	// delete
		, ""	// flag column 명
		, ""	// key sql id
		, 0	// key 증가 값
		, "" 
		, 0 
		, "N"	// 실행타입
	);
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iTrainingJubsurRegisterPrint=ds_oTrainingJubsurRegisterTemp:U"	// inDs
		,""	// outDs
		,""	// args 
		,"lfn_AfterUpdateTrainingJubsurRegisterPrint"
		,false
	);
}

/*
 * 기능 : 업데이트 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterUpdateTrainingJubsurRegisterPrint(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    gfn_Alert("좌석재배치를 성공하였습니다.");
}

