/* 포커스 아웃될때 자동 0 입력 */
function edTrainingOrder_OnKillFocus(obj) {
	gfn_ComponentOnKillFocus(obj);
    
    obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 3));    
}

// 좌석현황보기
function btnSeatList_OnClick(obj)
{
    if (gfn_IsNull(ds_oTrainingOrderMgno.GetColumn(0, "TOMGNO"))) {
        gfn_Alert(MSGI00100); // "강습을 먼저 선택해 주십시오.";
        edTrainingOrder.SetFocus();
        return false;
    }

    lfn_SearchUnUseSeatNoListPopup(ds_oTrainingOrderMgno.GetColumn(0, "TOMGNO")); // 좌석현황보기
}

/*
 * 기능 : 좌석현황보기
 * parameter : sOrderMgno - 회차관리번호
 * return value : 좌석목록 정보를 가진 배열 오브젝트
 */
function lfn_SearchUnUseSeatNoListPopup(sOrderMgno) {
    var sArg = "sOrderMgno=" + quote(sOrderMgno);
    var arrSeatNo = Dialog("training::frmtraining0115PTrainingSeatNoList.xml", sArg, 600, 400, "", -1, -1);

}

/*
 * 기능 : 좌석재배치(접수번호와 동일하게)
 */
function btnSeatNoAssignJubsu_OnClick(obj)
{
	if (!gfn_Confirm("접수번호와 좌석번호를 일치시키는 기능입니다.\n\n좌석지정을 한 경우에도 좌석이 재배치되오니 신중하게 재배치 하기시 바랍니다.\n\n좌석 재배치를 하시겠습니까?")) {
        return false;
    }        
    ds_oTrainingJubsurRegisterPersonList.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
    tit_addCsvSearchActionInfo("training:searchTrainingJubsurRegisterPersonList", false);    
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,""	// inDs
        ,"ds_oTrainingJubsurRegisterPersonList=ds_oTrainingJubsurRegisterList"	// outDs		  
        ,"TOMGGTMGNO=" + quote(cbxJibu.Value)
            + " TOTCCOURSECD=" + quote(cbxCourse.Value)
            + " TOYEAR=" + quote(edTrainingYear.Text)
            + " TOTRAININGORDER=" + quote(edTrainingOrder.Text)	// args 
        ,""
        ,false
        ,""
        ,true
    );
    
    for (i=0;i<ds_oTrainingJubsurRegisterPersonList.rowcount;i++) {
        ds_oTrainingJubsurRegisterPersonList.SetColumn(i, "TOHDECISIONSEATNO", ToNumber(ds_oTrainingJubsurRegisterPersonList.GetColumn(i, "TOHJUBSUNO")));
    }
    
    tit_clearActionInfo();	
    tit_addSaveActionInfo(
		""	// insert
		, "training:updateTrainingJubsurRegisterPrint"	// update
		, ""	// delete
		, ""	// flag column 명
		, ""	// key sql id
		, 0	// key 증가 값
		, "" 
		, 0 
		, "N"	// 실행타입
	);
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iTrainingJubsurRegisterPrint=ds_oTrainingJubsurRegisterPersonList:U"	// inDs
		,""	// outDs
		,""	// args 
		,"lfn_AfterUpdateTrainingJubsurRegisterPrint"
		,false
	);
}
